import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AdminBookingList = () => {
  const [bookings, setBookings] = useState([]);
  const [totalBookings, setTotalBookings] = useState(0);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Function to fetch bookings from API
  const fetchBookings = async () => {
    try {
      setLoading(true);
      setError(null);

      // Get token from localStorage (adjust if stored elsewhere)
      const token = localStorage.getItem('authToken');
      if (!token) {
        throw new Error('No auth token found, please login');
      }

      // Make request with Authorization header
      const response = await axios.get('/api/bookings', {
        headers: {
          Authorization: `Bearer ${token}`,
        },
        params: {
          // Optional: pagination or filters
          page: 1,
          limit: 10,
        },
      });

      // Assume API response structure { bookings: [...], total: number }
      setBookings(response.data.bookings);
      setTotalBookings(response.data.total);
      setLoading(false);
    } catch (err) {
      setLoading(false);
      setError(err.message || 'Failed to fetch bookings');

      // Log full error info for debugging
      console.error('Error fetching bookings:', err);

      // If unauthorized, you might want to redirect to login
      if (err.response && err.response.status === 401) {
        // Optional: Redirect or notify user to login
        console.warn('Unauthorized! Redirecting to login...');
        // e.g., window.location.href = '/login';
      }
    }
  };

  // Fetch bookings on component mount
  useEffect(() => {
    fetchBookings();
  }, []);

  return (
    <div>
      <h2>Admin Booking List</h2>

      {loading && <p>Loading bookings...</p>}

      {error && <p style={{ color: 'red' }}>Error: {error}</p>}

      {!loading && !error && (
        <>
          <p>Total bookings: {totalBookings}</p>
          <table border="1" cellPadding="8" cellSpacing="0">
            <thead>
              <tr>
                <th>Booking ID</th>
                <th>User</th>
                <th>Date</th>
                <th>Status</th>
                {/* Add more columns as needed */}
              </tr>
            </thead>
            <tbody>
              {bookings.length === 0 ? (
                <tr>
                  <td colSpan="4">No bookings found</td>
                </tr>
              ) : (
                bookings.map((booking) => (
                  <tr key={booking._id}>
                    <td>{booking._id}</td>
                    <td>{booking.userName || booking.userEmail}</td>
                    <td>{new Date(booking.date).toLocaleString()}</td>
                    <td>{booking.status}</td>
                  </tr>
                ))
              )}
            </tbody>
          </table>
        </>
      )}
    </div>
  );
};

export default AdminBookingList;
