import React, { useState, useEffect } from "react";
import axios from "axios";

const BookSlot = () => {
  const [bunks, setBunks] = useState([]);
  const [selectedBunk, setSelectedBunk] = useState("");
  const [slotTime, setSlotTime] = useState("");
  const [message, setMessage] = useState("");
  const [token, setToken] = useState("");
  const [userId, setUserId] = useState("");

  useEffect(() => {
    const fetchBunks = async () => {
      try {
        const response = await axios.get("/api/bunks");
        setBunks(response.data.bunks || []); // defensive
      } catch (error) {
        console.error("Failed to fetch bunks:", error);
        setMessage("Failed to load EV bunks.");
      }
    };

    fetchBunks();
    setToken(localStorage.getItem("token"));
    setUserId(localStorage.getItem("userId"));
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!token) {
      setMessage("You must be logged in to book a slot.");
      return;
    }

    try {
      const res = await axios.post(
        "/api/bookings/create",
        {
          bunkId: selectedBunk,
          slotTime,
          userId, // include this if required by backend
        },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );

      setMessage(res.data?.message || "Slot booked successfully!");
    } catch (err) {
      console.error("Booking failed:", err);
      if (err.response?.data?.message) {
        setMessage(`Booking failed: ${err.response.data.message}`);
      } else {
        setMessage("Booking failed. Try again.");
      }
    }
  };

  return (
    <div className="max-w-md mx-auto p-6 bg-white rounded-lg shadow">
      <h2 className="text-2xl font-semibold mb-4">Book Recharge Slot</h2>

      <form onSubmit={handleSubmit} className="space-y-4">
        <select
          value={selectedBunk}
          onChange={(e) => setSelectedBunk(e.target.value)}
          className="w-full p-2 border rounded"
          required
        >
          <option value="">Select EV Bunk</option>
          {bunks.length > 0 &&
            bunks.map((bunk) => (
              <option key={bunk._id} value={bunk._id}>
                {bunk.name} - {bunk.location}
              </option>
            ))}
        </select>

        <input
          type="datetime-local"
          value={slotTime}
          onChange={(e) => setSlotTime(e.target.value)}
          className="w-full p-2 border rounded"
          required
        />

        <button
          type="submit"
          className="w-full bg-green-600 text-white py-2 rounded hover:bg-green-700"
        >
          Book Slot
        </button>

        {message && (
          <p
            className={`text-center mt-2 ${
              message.toLowerCase().includes("success")
                ? "text-green-600"
                : "text-red-600"
            }`}
          >
            {message}
          </p>
        )}
      </form>
    </div>
  );
};

export default BookSlot;
