import React, { useState } from "react";
import Navbar from "../components/common/navbars/Navbar"; // Ensure this path is correct for your Navbar
import axios from "axios"; // Import axios for API calls
import { Mail, User, MessageSquare, Send, Loader2, AlertCircle, CheckCircle } from 'lucide-react'; // Lucide icons

const Contact = () => {
  const [formData, setFormData] = useState({ name: "", email: "", message: "" });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [successMessage, setSuccessMessage] = useState("");

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(""); // Clear previous errors
    setSuccessMessage(""); // Clear previous success messages

    try {
      // Replace 'http://localhost:5000/api/v1/contact' with your actual backend API endpoint for contact forms
      await axios.post('http://localhost:5000/api/v1/contact', formData); 
      setSuccessMessage("Thank you for contacting us! We'll get back to you soon.");
      setFormData({ name: "", email: "", message: "" }); // Clear form fields on success
    } catch (err) {
      console.error("Error submitting message:", err);
      setError(err.response?.data?.message || "Failed to send message. Please try again.");
    } finally {
      setLoading(false); // End loading regardless of success or failure
    }
  };

  return (
    <div>
      <Navbar />
      <section className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 dark:from-gray-900 dark:to-gray-800 text-gray-800 dark:text-gray-100 py-12 px-4 sm:px-6 md:px-8 flex items-center justify-center">
        <div className="max-w-3xl w-full mx-auto bg-white dark:bg-gray-800 p-8 rounded-2xl shadow-xl border border-gray-100 dark:border-gray-700">
          {/* Header Section */}
          <div className="mb-8 text-center">
            <div className="flex items-center justify-center space-x-3 mb-4">
              <MessageSquare className="w-12 h-12 text-blue-600 dark:text-blue-400" />
              <h1 className="text-4xl font-extrabold text-gray-900 dark:text-white">Contact Us</h1>
            </div>
            <p className="text-lg text-gray-600 dark:text-gray-300">
              Have questions, suggestions, or need support? We'd love to hear from you.
            </p>
          </div>

          {/* Status Messages */}
          {loading && (
            <div className="mb-6 p-4 bg-blue-50 dark:bg-blue-900 border border-blue-200 dark:border-blue-700 rounded-xl flex items-center justify-center space-x-3">
              <Loader2 className="w-5 h-5 text-blue-600 dark:text-blue-400 animate-spin" />
              <span className="text-blue-800 dark:text-blue-200 font-medium">Sending message...</span>
            </div>
          )}
          
          {error && (
            <div className="mb-6 p-4 bg-red-50 dark:bg-red-900 border border-red-200 dark:border-red-700 rounded-xl flex items-center space-x-3">
              <AlertCircle className="w-5 h-5 text-red-600 dark:text-red-400" />
              <span className="text-red-800 dark:text-red-200 font-medium">{error}</span>
            </div>
          )}
          
          {successMessage && (
            <div className="mb-6 p-4 bg-green-50 dark:bg-green-900 border border-green-200 dark:border-green-700 rounded-xl flex items-center space-x-3">
              <CheckCircle className="w-5 h-5 text-green-600 dark:text-green-400" />
              <span className="text-green-800 dark:text-green-200 font-medium">{successMessage}</span>
            </div>
          )}

          <form onSubmit={handleSubmit} className="grid gap-6">
            {/* Name Input */}
            <div>
              <label className="block text-sm font-semibold mb-2 text-gray-700 dark:text-gray-300" htmlFor="name">
                Your Name
              </label>
              <div className="relative">
                <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                  <User className="h-5 w-5 text-gray-400 dark:text-gray-500" />
                </div>
                <input
                  type="text"
                  id="name"
                  name="name"
                  value={formData.name}
                  onChange={handleChange}
                  required
                  className="w-full pl-10 pr-4 py-3 rounded-xl bg-white dark:bg-gray-700 border border-gray-300 dark:border-gray-600 text-gray-900 dark:text-white focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all duration-200 text-base"
                  placeholder="John Doe"
                />
              </div>
            </div>

            {/* Email Input */}
            <div>
              <label className="block text-sm font-semibold mb-2 text-gray-700 dark:text-gray-300" htmlFor="email">
                Your Email
              </label>
              <div className="relative">
                <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                  <Mail className="h-5 w-5 text-gray-400 dark:text-gray-500" />
                </div>
                <input
                  type="email"
                  id="email"
                  name="email"
                  value={formData.email}
                  onChange={handleChange}
                  required
                  className="w-full pl-10 pr-4 py-3 rounded-xl bg-white dark:bg-gray-700 border border-gray-300 dark:border-gray-600 text-gray-900 dark:text-white focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all duration-200 text-base"
                  placeholder="you@example.com"
                />
              </div>
            </div>

            {/* Message Textarea */}
            <div>
              <label className="block text-sm font-semibold mb-2 text-gray-700 dark:text-gray-300" htmlFor="message">
                Your Message
              </label>
              <div className="relative">
                <div className="absolute top-3 left-3 flex items-center pointer-events-none">
                  <MessageSquare className="h-5 w-5 text-gray-400 dark:text-gray-500" />
                </div>
                <textarea
                  id="message"
                  name="message"
                  value={formData.message}
                  onChange={handleChange}
                  rows="6"
                  required
                  className="w-full pl-10 pr-4 py-3 rounded-xl bg-white dark:bg-gray-700 border border-gray-300 dark:border-gray-600 text-gray-900 dark:text-white focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all duration-200 text-base resize-y"
                  placeholder="Tell us how we can help you..."
                ></textarea>
              </div>
            </div>

            {/* Submit Button */}
            <div className="pt-2">
              <button
                type="submit"
                disabled={loading}
                className="w-full bg-gradient-to-r from-blue-600 to-indigo-600 hover:from-blue-700 hover:to-indigo-700 disabled:from-gray-400 disabled:to-gray-500 text-white font-semibold py-3 px-6 rounded-xl transition-all duration-300 flex items-center justify-center space-x-2 shadow-lg hover:shadow-xl disabled:cursor-not-allowed transform hover:scale-[1.01] active:scale-[0.99]"
              >
                {loading ? (
                  <>
                    <Loader2 className="w-5 h-5 animate-spin" />
                    <span>Sending...</span>
                  </>
                ) : (
                  <>
                    <Send className="w-5 h-5" />
                    <span>Send Message</span>
                  </>
                )}
              </button>
            </div>
          </form>

          {/* Contact Information */}
          <div className="mt-10 pt-6 border-t border-gray-200 dark:border-gray-700 text-sm text-gray-600 dark:text-gray-400 text-center">
            <p className="mb-2">We typically respond within 24-48 hours.</p>
            <p>Email: <a href="mailto:support@vrechargehub.com" className="text-blue-600 dark:text-blue-400 hover:underline font-medium">support@vrechargehub.com</a></p>
            <p>Phone: <a href="tel:+919876543210" className="text-blue-600 dark:text-blue-400 hover:underline font-medium">+91-9876543210</a></p>
            <p>Location: Mudbidri, Karnataka, India</p> {/* Updated based on current location */}
          </div>
        </div>
      </section>
    </div>
  );
};

export default Contact;
