import React, { useState, useEffect, useRef, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import { MapPin, Zap, Clock, Phone, Map, List, Edit3, Navigation } from 'lucide-react';
import AdminNavbar from "../common/navbars/AdminNavbar";

const EvBunkMapView = () => {
  const [bunkLocations, setBunkLocations] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedBunk, setSelectedBunk] = useState(null);
  const [viewMode, setViewMode] = useState('list'); // 'list' or 'map'
  const [map, setMap] = useState(null);
  const [userLocation, setUserLocation] = useState(null);
  const [locationPermission, setLocationPermission] = useState('unknown');
  const [mapsLoaded, setMapsLoaded] = useState(false);
  
  const mapRef = useRef(null);
  const infoWindowRef = useRef(null);
  const markersRef = useRef([]);
  const userMarkerRef = useRef(null);
  const directionsRendererRef = useRef(null);
  const googleMapsScriptRef = useRef(null);
  const navigate = useNavigate();

  const GOOGLE_MAPS_API_KEY = 'AIzaSyDozw7FDv161gMDT9lE-U0cSGZuWjYhyvw';

  // Calculate distance between two coordinates using Haversine formula
  const calculateDistance = (lat1, lon1, lat2, lon2) => {
    const R = 6371; // Radius of the Earth in kilometers
    const dLat = (lat2 - lat1) * Math.PI / 180;
    const dLon = (lon2 - lon1) * Math.PI / 180;
    const a = 
      Math.sin(dLat/2) * Math.sin(dLat/2) +
      Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) * 
      Math.sin(dLon/2) * Math.sin(dLon/2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1-a));
    const distance = R * c;
    return distance;
  };

  // Add distance calculation to bunks
  const addDistanceToBunks = (bunks, userLat, userLng) => {
    return bunks.map(bunk => ({
      ...bunk,
      distance: calculateDistance(userLat, userLng, bunk.latitude, bunk.longitude)
    })).sort((a, b) => a.distance - b.distance);
  };

  // Get user's current location
  const getCurrentLocation = useCallback(() => {
    return new Promise((resolve, reject) => {
      if (!navigator.geolocation) {
        reject(new Error('Geolocation is not supported by this browser.'));
        return;
      }

      navigator.geolocation.getCurrentPosition(
        (position) => {
          const location = {
            lat: position.coords.latitude,
            lng: position.coords.longitude
          };
          setUserLocation(location);
          setLocationPermission('granted');
          resolve(location);
        },
        (error) => {
          console.error('Error getting location:', error);
          setLocationPermission('denied');
          reject(error);
        },
        {
          enableHighAccuracy: true,
          timeout: 10000,
          maximumAge: 300000
        }
      );
    });
  }, []);

  // Load Google Maps script
  useEffect(() => {
    const loadGoogleMaps = () => {
      return new Promise((resolve, reject) => {
        if (window.google && window.google.maps) {
          setMapsLoaded(true);
          resolve();
          return;
        }

        if (googleMapsScriptRef.current) {
          googleMapsScriptRef.current.addEventListener('load', () => {
            setMapsLoaded(true);
            resolve();
          });
          googleMapsScriptRef.current.addEventListener('error', reject);
          return;
        }

        const existingScript = document.querySelector(`script[src*="maps.googleapis.com"]`);
        if (existingScript) {
          if (window.google && window.google.maps) {
            setMapsLoaded(true);
            resolve();
          } else {
            existingScript.addEventListener('load', () => {
              setMapsLoaded(true);
              resolve();
            });
            existingScript.addEventListener('error', reject);
          }
          return;
        }

        const script = document.createElement('script');
        script.src = `https://maps.googleapis.com/maps/api/js?key=${GOOGLE_MAPS_API_KEY}&libraries=places`;
        script.async = true;
        script.defer = true;
        
        script.onload = () => {
          setMapsLoaded(true);
          resolve();
        };
        script.onerror = reject;
        
        googleMapsScriptRef.current = script;
        document.head.appendChild(script);
      });
    };

    loadGoogleMaps().catch(error => {
      console.error('Failed to load Google Maps:', error);
      setError('Failed to load Google Maps');
    });

    return () => {
      googleMapsScriptRef.current = null;
    };
  }, [GOOGLE_MAPS_API_KEY]);

  // Clear existing markers
  const clearMarkers = useCallback(() => {
    markersRef.current.forEach(marker => {
      marker.setMap(null);
    });
    markersRef.current = [];
  }, []);

  // Clear directions
  const clearDirections = useCallback(() => {
    if (directionsRendererRef.current) {
      directionsRendererRef.current.setMap(null);
      directionsRendererRef.current = null;
    }
  }, []);

  // Show directions between user and selected bunk
  // Updated showDirections function with better error handling and debugging
const showDirections = useCallback((destinationBunk) => {
  console.log('üß≠ Starting directions calculation...');
  console.log('User location:', userLocation);
  console.log('Destination bunk:', destinationBunk);
  console.log('Map instance:', !!map);
  console.log('Google Maps loaded:', !!window.google);

  if (!map || !userLocation || !window.google) {
    const missing = [];
    if (!map) missing.push('map');
    if (!userLocation) missing.push('userLocation');
    if (!window.google) missing.push('google maps');
    
    console.error('‚ùå Cannot show directions - missing:', missing.join(', '));
    alert(`Cannot show directions. Missing: ${missing.join(', ')}. Please ensure location access is enabled and Google Maps is loaded.`);
    return;
  }

  if (!destinationBunk?.latitude || !destinationBunk?.longitude) {
    console.error('‚ùå Invalid destination coordinates:', destinationBunk);
    alert('Invalid destination coordinates. Please try another station.');
    return;
  }

  // Clear any existing directions
  clearDirections();

  try {
    const directionsService = new window.google.maps.DirectionsService();
    const directionsRenderer = new window.google.maps.DirectionsRenderer({
      suppressMarkers: false,
      polylineOptions: {
        strokeColor: '#4285f4',
        strokeWeight: 5,
        strokeOpacity: 0.8
      },
      markerOptions: {
        origin: {
          icon: {
            url: 'data:image/svg+xml;charset=UTF-8,' + encodeURIComponent(`
              <svg width="32" height="32" viewBox="0 0 32 32" xmlns="http://www.w3.org/2000/svg">
                <circle cx="16" cy="16" r="12" fill="#2563eb" stroke="white" stroke-width="3"/>
                <circle cx="16" cy="16" r="4" fill="white"/>
              </svg>
            `),
            scaledSize: new window.google.maps.Size(32, 32)
          }
        },
        destination: {
          icon: {
            url: 'data:image/svg+xml;charset=UTF-8,' + encodeURIComponent(`
              <svg width="32" height="32" viewBox="0 0 32 32" xmlns="http://www.w3.org/2000/svg">
                <circle cx="16" cy="16" r="12" fill="#10b981" stroke="white" stroke-width="2"/>
                <path d="M12 16l4 4 8-8" stroke="white" stroke-width="2" fill="none"/>
              </svg>
            `),
            scaledSize: new window.google.maps.Size(32, 32)
          }
        }
      }
    });

    directionsRenderer.setMap(map);
    directionsRendererRef.current = directionsRenderer;

    // Multiple travel modes to try
    const travelModes = [
      window.google.maps.TravelMode.DRIVING,
      window.google.maps.TravelMode.WALKING,
      window.google.maps.TravelMode.TRANSIT
    ];

    const tryDirectionsWithMode = (modeIndex = 0) => {
      if (modeIndex >= travelModes.length) {
        console.error('‚ùå All travel modes failed');
        alert('Could not calculate route with any travel mode. The destination might be unreachable or there might be a network issue.');
        return;
      }

      const travelMode = travelModes[modeIndex];
      console.log(`üöó Trying travel mode: ${travelMode}`);

      const request = {
        origin: new window.google.maps.LatLng(userLocation.lat, userLocation.lng),
        destination: new window.google.maps.LatLng(destinationBunk.latitude, destinationBunk.longitude),
        travelMode: travelMode,
        unitSystem: window.google.maps.UnitSystem.METRIC,
        avoidHighways: false,
        avoidTolls: false
      };

      console.log('üìç Directions request:', request);

      directionsService.route(request, (result, status) => {
        console.log('üìç Directions response status:', status);
        console.log('üìç Directions result:', result);

        if (status === window.google.maps.DirectionsStatus.OK) {
          console.log('‚úÖ Directions successful!');
          directionsRenderer.setDirections(result);
          
          // Extract route information
          const route = result.routes[0];
          const leg = route.legs[0];
          const drivingDistance = leg.distance.value / 1000; // Convert to km
          const drivingTime = leg.duration.text;
          const routeSummary = route.summary || 'Via main roads';
          
          console.log('üõ£Ô∏è Route details:', {
            distance: leg.distance.text,
            duration: drivingTime,
            summary: routeSummary
          });
          
          // Update the bunk with driving distance
          setBunkLocations(prevBunks => 
            prevBunks.map(bunk => 
              bunk._id === destinationBunk._id 
                ? { 
                    ...bunk, 
                    drivingDistance: drivingDistance, 
                    drivingTime: drivingTime,
                    routeSummary: routeSummary
                  }
                : bunk
            )
          );

          // Create enhanced info window
          const infoContent = `
            <div style="max-width: 320px; font-family: Arial, sans-serif; line-height: 1.4;">
              <h3 style="margin: 0 0 12px 0; color: #1f2937; font-size: 16px; font-weight: bold;">
                üó∫Ô∏è Route to ${destinationBunk.name}
              </h3>
              <div style="background: #f3f4f6; padding: 8px; border-radius: 6px; margin-bottom: 12px;">
                <p style="margin: 4px 0; color: #374151; font-size: 14px;">
                  üìç <strong>Distance:</strong> ${leg.distance.text}
                </p>
                <p style="margin: 4px 0; color: #374151; font-size: 14px;">
                  ‚è±Ô∏è <strong>Duration:</strong> ${drivingTime}
                </p>
                <p style="margin: 4px 0; color: #374151; font-size: 14px;">
                  üöó <strong>Via:</strong> ${routeSummary}
                </p>
                <p style="margin: 4px 0; color: #374151; font-size: 14px;">
                  üö¶ <strong>Mode:</strong> ${travelMode.charAt(0).toUpperCase() + travelMode.slice(1).toLowerCase()}
                </p>
              </div>
              <div style="display: flex; gap: 8px; flex-wrap: wrap;">
                <button onclick="window.clearDirectionsFromRoute()" 
                        style="background: #ef4444; color: white; padding: 6px 12px; border: none; border-radius: 4px; cursor: pointer; font-size: 12px; font-weight: 500;">
                  ‚ùå Clear Route
                </button>
                <button onclick="window.openInGoogleMaps('${destinationBunk.latitude}', '${destinationBunk.longitude}', '${encodeURIComponent(destinationBunk.name)}')" 
                        style="background: #10b981; color: white; padding: 6px 12px; border: none; border-radius: 4px; cursor: pointer; font-size: 12px; font-weight: 500;">
                  üó∫Ô∏è Open in Google Maps
                </button>
                <button onclick="window.startNavigation('${destinationBunk.latitude}', '${destinationBunk.longitude}', '${encodeURIComponent(destinationBunk.name)}')"
                        style="background: #3b82f6; color: white; padding: 6px 12px; border: none; border-radius: 4px; cursor: pointer; font-size: 12px; font-weight: 500;">
                  üß≠ Start Navigation
                </button>
              </div>
            </div>
          `;
          
          // Position info window at destination
          const destinationPosition = new window.google.maps.LatLng(destinationBunk.latitude, destinationBunk.longitude);
          
          if (infoWindowRef.current) {
            infoWindowRef.current.setContent(infoContent);
            infoWindowRef.current.setPosition(destinationPosition);
            infoWindowRef.current.open(map);
          }

          // Fit map to show the entire route
          const bounds = new window.google.maps.LatLngBounds();
          route.legs.forEach(leg => {
            leg.steps.forEach(step => {
              bounds.extend(step.start_location);
              bounds.extend(step.end_location);
            });
          });
          map.fitBounds(bounds);

          console.log('‚úÖ Route displayed successfully');
          
        } else {
          console.log(`‚ùå Directions failed with status: ${status}, trying next mode...`);
          
          // Log specific error details
          const errorMessages = {
            'ZERO_RESULTS': 'No route could be found between the origin and destination.',
            'OVER_QUERY_LIMIT': 'Too many directions requests. Please try again later.',
            'REQUEST_DENIED': 'Directions request was denied.',
            'INVALID_REQUEST': 'Invalid directions request.',
            'UNKNOWN_ERROR': 'Unknown error occurred.',
            'NOT_FOUND': 'Origin or destination could not be found.',
            'MAX_WAYPOINTS_EXCEEDED': 'Too many waypoints in the request.',
            'MAX_ROUTE_LENGTH_EXCEEDED': 'Route is too long.'
          };
          
          console.log(`Error details: ${errorMessages[status] || status}`);
          
          // Try next travel mode
          setTimeout(() => tryDirectionsWithMode(modeIndex + 1), 500);
        }
      });
    };

    // Start with the first travel mode
    tryDirectionsWithMode(0);

  } catch (error) {
    console.error('‚ùå Error in showDirections:', error);
    alert(`Error calculating route: ${error.message}`);
  }
}, [map, userLocation, clearDirections]);

// Enhanced global functions for info window interactions
useEffect(() => {
  window.showDirectionsToStation = (bunkId) => {
    console.log('üéØ Showing directions to station:', bunkId);
    const bunk = bunkLocations.find(b => b._id === bunkId);
    if (bunk) {
      showDirections(bunk);
    } else {
      console.error('‚ùå Bunk not found:', bunkId);
    }
  };

  window.clearDirectionsFromRoute = () => {
    console.log('üßπ Clearing directions');
    clearDirections();
    if (infoWindowRef.current) {
      infoWindowRef.current.close();
    }
  };

  window.openInGoogleMaps = (lat, lng, name) => {
    console.log('üó∫Ô∏è Opening in Google Maps:', { lat, lng, name });
    const url = `https://www.google.com/maps/dir/?api=1&destination=${lat},${lng}&destination_place_id=${encodeURIComponent(name)}`;
    window.open(url, '_blank');
  };

  window.startNavigation = (lat, lng, name) => {
    console.log('üß≠ Starting navigation:', { lat, lng, name });
    // Try to open in navigation apps
    const navUrl = `https://www.google.com/maps/dir/?api=1&destination=${lat},${lng}&travelmode=driving`;
    window.open(navUrl, '_blank');
  };

  return () => {
    delete window.showDirectionsToStation;
    delete window.clearDirectionsFromRoute;
    delete window.openInGoogleMaps;
    delete window.startNavigation;
  };
}, [bunkLocations, showDirections, clearDirections]);

// Enhanced handleShowDirections with better debugging
const handleShowDirections = useCallback((bunk) => {
  console.log('üéØ Handle show directions called:', bunk.name);
  
  if (!userLocation) {
    console.log('‚ùå No user location');
    alert('Location access is required to show directions. Please enable location access and refresh the page.');
    return;
  }

  if (!mapsLoaded) {
    console.log('‚ùå Maps not loaded');
    alert('Google Maps is still loading. Please wait a moment and try again.');
    return;
  }

  console.log('‚úÖ Prerequisites met, switching to map view...');
  
  // Switch to map view
  setViewMode('map');
  setSelectedBunk(bunk);
  
  // Wait for map to initialize, then show directions
  const showDirectionsAfterMapLoad = () => {
    console.log('üó∫Ô∏è Attempting to show directions, map ready:', !!map);
    if (map) {
      showDirections(bunk);
    } else {
      console.log('‚è≥ Map not ready, waiting longer...');
      setTimeout(() => {
        if (map) {
          showDirections(bunk);
        } else {
          alert('Map failed to load. Please refresh the page and try again.');
        }
      }, 2000);
    }
  };
  
  setTimeout(showDirectionsAfterMapLoad, 800);
}, [userLocation, mapsLoaded, map, showDirections]);

// Enhanced getCurrentLocation with better error handling

  // Initialize or reinitialize map
  const initializeMap = useCallback((forceReinit = false) => {
    if (mapsLoaded && mapRef.current && viewMode === 'map' && window.google && (!map || forceReinit)) {
      // Clear existing map if reinitializing
      if (map && forceReinit) {
        clearMarkers();
        clearDirections();
        if (userMarkerRef.current) {
          userMarkerRef.current.setMap(null);
          userMarkerRef.current = null;
        }
        if (infoWindowRef.current) {
          infoWindowRef.current.close();
        }
      }

      const center = userLocation || { lat: 12.9716, lng: 77.5946 };
      
      const mapInstance = new window.google.maps.Map(mapRef.current, {
        center: center,
        zoom: userLocation ? 13 : 11,
        styles: [
          {
            featureType: 'poi',
            elementType: 'labels',
            stylers: [{ visibility: 'off' }]
          }
        ]
      });

      const infoWindow = new window.google.maps.InfoWindow();
      infoWindowRef.current = infoWindow;
      setMap(mapInstance);

      // Add user location marker
      updateUserLocationMarker(mapInstance);

      return mapInstance;
    }
    return map;
  }, [mapsLoaded, viewMode, userLocation, map, updateUserLocationMarker, clearMarkers, clearDirections]);

  // Initialize map when switching to map view
  useEffect(() => {
    if (viewMode === 'map') {
      const mapInstance = initializeMap();
      // If we have a selected bunk and directions should be shown
      if (selectedBunk && mapInstance && userLocation) {
        setTimeout(() => {
          showDirections(selectedBunk);
        }, 1000);
      }
    }
  }, [initializeMap, viewMode, selectedBunk, userLocation, showDirections]);

  // Update user marker when location changes
  useEffect(() => {
    if (map && userLocation) {
      updateUserLocationMarker(map);
    }
  }, [map, userLocation, updateUserLocationMarker]);

  // Update markers when bunk locations change
  useEffect(() => {
    if (map && bunkLocations.length > 0 && window.google) {
      clearMarkers();
      
      const newMarkers = bunkLocations.map(bunk => {
        const marker = new window.google.maps.Marker({
          position: { lat: bunk.latitude, lng: bunk.longitude },
          map: map,
          title: bunk.name,
          icon: {
            url: bunk.slotsAvailable > 0 
              ? 'data:image/svg+xml;charset=UTF-8,' + encodeURIComponent(`
                <svg width="32" height="32" viewBox="0 0 32 32" xmlns="http://www.w3.org/2000/svg">
                  <circle cx="16" cy="16" r="12" fill="#10b981" stroke="white" stroke-width="2"/>
                  <path d="M12 16l4 4 8-8" stroke="white" stroke-width="2" fill="none"/>
                </svg>
              `)
              : 'data:image/svg+xml;charset=UTF-8,' + encodeURIComponent(`
                <svg width="32" height="32" viewBox="0 0 32 32" xmlns="http://www.w3.org/2000/svg">
                  <circle cx="16" cy="16" r="12" fill="#ef4444" stroke="white" stroke-width="2"/>
                  <path d="M20 12l-8 8M12 12l8 8" stroke="white" stroke-width="2"/>
                </svg>
              `),
            scaledSize: new window.google.maps.Size(32, 32)
          }
        });

        marker.addListener('click', () => {
          setSelectedBunk(bunk);
          
          const infoContent = `
            <div style="max-width: 300px; font-family: Arial, sans-serif;">
              <h3 style="margin: 0 0 10px 0; color: #1f2937; font-size: 16px;">${bunk.name}</h3>
              <p style="margin: 5px 0; color: #6b7280; font-size: 14px;">${bunk.address}</p>
              <p style="margin: 5px 0; color: #6b7280; font-size: 14px;">üìû ${bunk.phone}</p>
              <p style="margin: 5px 0; color: #6b7280; font-size: 14px;">‚è∞ ${bunk.operatingHours}</p>
              <p style="margin: 5px 0; font-weight: bold; color: ${bunk.slotsAvailable > 0 ? '#10b981' : '#ef4444'};">
                ‚ö° ${bunk.slotsAvailable} slots available
              </p>
              ${bunk.connectorTypes ? `
                <div style="margin-top: 8px;">
                  <small style="color: #6b7280;">Connectors: ${bunk.connectorTypes.join(', ')}</small>
                </div>
              ` : ''}
              ${bunk.distance ? `
                <div style="margin-top: 8px;">
                  <small style="color: #6b7280;">üìç Distance: ${bunk.distance.toFixed(1)} km away</small>
                </div>
              ` : ''}
              ${userLocation ? `
                <div style="margin-top: 10px;">
                  <button onclick="window.showDirectionsToStation('${bunk._id}')" 
                          style="background: #3b82f6; color: white; padding: 5px 10px; border: none; border-radius: 4px; cursor: pointer; font-size: 12px;">
                    üß≠ Show Route
                  </button>
                </div>
              ` : ''}
            </div>
          `;
          
          if (infoWindowRef.current) {
            infoWindowRef.current.setContent(infoContent);
            infoWindowRef.current.open(map, marker);
          }
        });

        return marker;
      });

      markersRef.current = newMarkers;

      // Fit map to show all markers including user location
      if (newMarkers.length > 0) {
        const bounds = new window.google.maps.LatLngBounds();
        
        // Add user location to bounds if available
        if (userLocation) {
          bounds.extend(userLocation);
        }
        
        // Add all bunk locations to bounds
        newMarkers.forEach(marker => {
          bounds.extend(marker.getPosition());
        });
        
        // Fit the map to show all markers
        map.fitBounds(bounds);
        
        // Set a maximum zoom level
        const listener = window.google.maps.event.addListener(map, 'idle', () => {
          if (map.getZoom() > 15) map.setZoom(15);
          window.google.maps.event.removeListener(listener);
        });
      }
    }
  }, [map, bunkLocations, userLocation, clearMarkers]);

  // Add global functions for info window interactions
  useEffect(() => {
    window.showDirectionsToStation = (bunkId) => {
      const bunk = bunkLocations.find(b => b._id === bunkId);
      if (bunk) {
        showDirections(bunk);
      }
    };

    window.clearDirectionsFromRoute = () => {
      clearDirections();
      if (infoWindowRef.current) {
        infoWindowRef.current.close();
      }
    };

    window.openInGoogleMaps = (lat, lng, name) => {
      const url = `https://www.google.com/maps/dir/?api=1&destination=${lat},${lng}&destination_place_id=${encodeURIComponent(name)}`;
      window.open(url, '_blank');
    };

    return () => {
      delete window.showDirectionsToStation;
      delete window.clearDirectionsFromRoute;
      delete window.openInGoogleMaps;
    };
  }, [bunkLocations, showDirections, clearDirections]);

  // Cleanup on unmount
  useEffect(() => {
    return () => {
      clearMarkers();
      clearDirections();
      if (userMarkerRef.current) {
        userMarkerRef.current.setMap(null);
      }
      if (infoWindowRef.current) {
        infoWindowRef.current.close();
      }
    };
  }, [clearMarkers, clearDirections]);

  // Fetch EV bunk locations
  const fetchBunkLocations = async () => {
    try {
      setLoading(true);
      setError(null);
      
      console.log('üîç Fetching bunk locations...');
      
      const response = await fetch('/api/bunks', {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
        },
      });

      console.log('üì° Response status:', response.status);
      console.log('üì° Response ok:', response.ok);

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();
      console.log('üì¶ Raw API response:', data);
      
      let bunks = [];
      if (data.success) {
        bunks = data.data || [];
        console.log('‚úÖ Using data.data:', bunks.length, 'items');
      } else if (Array.isArray(data)) {
        bunks = data;
        console.log('‚úÖ Using array data:', bunks.length, 'items');
      } else {
        console.log('‚ùå Unexpected data format:', typeof data);
      }

      console.log('üè™ Final bunks array:', bunks);
      console.log('üè™ Bunks count:', bunks.length);

      if (userLocation && bunks.length > 0) {
        bunks = addDistanceToBunks(bunks, userLocation.lat, userLocation.lng);
        console.log('üìç Added distances to bunks');
      }

      setBunkLocations(bunks);
      console.log('‚úÖ Set bunk locations in state');
      
    } catch (error) {
      console.error('‚ùå Error fetching bunk locations:', error);
      console.error('‚ùå Error details:', {
        message: error.message,
        stack: error.stack
      });
      setError(error.message);
      setBunkLocations([]);
    } finally {
      setLoading(false);
    }
  };

  const fetchAvailableBunks = async () => {
    try {
      setLoading(true);
      setError(null);
      
      const response = await fetch('/api/bunks/available');
      
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();
      let bunks = data.data || [];

      if (userLocation && bunks.length > 0) {
        bunks = addDistanceToBunks(bunks, userLocation.lat, userLocation.lng);
      }

      setBunkLocations(bunks);
    } catch (error) {
      console.error('Error fetching available bunks:', error);
      setError(error.message);
    } finally {
      setLoading(false);
    }
  };

  const handleEditBunk = (bunkId) => {
    navigate(`/admin/edit-bunk/${bunkId}`);
  };

  const handleViewModeChange = (mode) => {
    if (mode === 'list' && map) {
      setMap(null);
      clearMarkers();
      clearDirections();
      if (userMarkerRef.current) {
        userMarkerRef.current.setMap(null);
        userMarkerRef.current = null;
      }
      if (infoWindowRef.current) {
        infoWindowRef.current.close();
      }
    }
    setViewMode(mode);
    setSelectedBunk(null); // Clear selected bunk when switching views
  };

  // Auto-get user location on component mount
  useEffect(() => {
    getCurrentLocation().catch(error => {
      console.log('Could not get user location:', error);
      // Continue without location - distances won't be shown
    });
    fetchBunkLocations();
  }, [getCurrentLocation]);

  // Recalculate distances when user location changes
  useEffect(() => {
    if (userLocation && bunkLocations.length > 0) {
      const bunksWithDistance = addDistanceToBunks(bunkLocations, userLocation.lat, userLocation.lng);
      setBunkLocations(bunksWithDistance);
    }
  }, [userLocation]);

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto mb-4"></div>
          <p className="text-gray-600">Loading EV charging locations...</p>
        </div>
      </div>
    );
  }

  if (error && bunkLocations.length === 0) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="text-center p-6 bg-red-50 rounded-lg max-w-md">
          <div className="text-red-600 mb-4">
            <Zap className="h-12 w-12 mx-auto mb-2" />
            <h3 className="font-semibold text-lg">Connection Error</h3>
          </div>
          <p className="text-red-700 mb-4">{error}</p>
          <button 
            onClick={fetchBunkLocations}
            className="bg-red-600 text-white px-4 py-2 rounded hover:bg-red-700 transition-colors"
          >
            Try Again
          </button>
        </div>
      </div>
    );
  }

  return (
    <div>
      <AdminNavbar/>
      <div className="container mx-auto p-6">
        <div className="mb-6">
          <h1 className="text-3xl font-bold text-gray-800 mb-4">EV Charging Stations</h1>
          
          {/* Location Permission Status */}
          {locationPermission !== 'unknown' && (
            <div className={`mb-4 p-3 rounded-lg text-sm ${
              locationPermission === 'granted' 
                ? 'bg-green-50 text-green-700 border border-green-200' 
                : 'bg-yellow-50 text-yellow-700 border border-yellow-200'
            }`}>
              {locationPermission === 'granted' ? (
                <div className="flex items-center gap-2">
                  <Navigation className="h-4 w-4" />
                  Location access granted - showing personalized results with distances
                  {userLocation && (
                    <span className="text-xs bg-green-100 px-2 py-1 rounded">
                      üìç {userLocation.lat.toFixed(4)}, {userLocation.lng.toFixed(4)}
                    </span>
                  )}
                </div>
              ) : (
                <div className="flex items-center gap-2">
                  <MapPin className="h-4 w-4" />
                  Location access denied - using default location (Bangalore). Enable location for directions.
                </div>
              )}
            </div>
          )}

          {/* Error message for non-fatal errors */}
          {error && bunkLocations.length > 0 && (
            <div className="mb-4 p-3 rounded-lg text-sm bg-yellow-50 text-yellow-700 border border-yellow-200">
              <div className="flex items-center gap-2">
                <MapPin className="h-4 w-4" />
                {error}
              </div>
            </div>
          )}
          
          {/* View Mode Toggle */}
          <div className="flex gap-2 mb-4">
            <button
              onClick={() => handleViewModeChange('list')}
              className={`flex items-center gap-2 px-4 py-2 rounded-lg transition-colors ${
                viewMode === 'list' 
                  ? 'bg-blue-600 text-white' 
                  : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
              }`}
            >
              <List className="h-4 w-4" />
              List View
            </button>
            <button
              onClick={() => handleViewModeChange('map')}
              className={`flex items-center gap-2 px-4 py-2 rounded-lg transition-colors ${
                viewMode === 'map' 
                  ? 'bg-blue-600 text-white' 
                  : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
              }`}
            >
              <Map className="h-4 w-4" />
              Map View
            </button>
            <button
              onClick={fetchAvailableBunks}
              className="flex items-center gap-2 px-4 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700 transition-colors"
            >
              <Zap className="h-4 w-4" />
              Available Only
            </button>
            <button
onClick={fetchBunkLocations}
              className="flex items-center gap-2 px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors"
            >
              <MapPin className="h-4 w-4" />
              Refresh All
            </button>
          </div>
          
          {/* Stats */}
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">
            <div className="bg-blue-50 p-4 rounded-lg">
              <div className="flex items-center gap-2">
                <MapPin className="h-5 w-5 text-blue-600" />
                <span className="font-semibold text-blue-800">Total Stations</span>
              </div>
              <p className="text-2xl font-bold text-blue-600">{bunkLocations.length}</p>
            </div>
            <div className="bg-green-50 p-4 rounded-lg">
              <div className="flex items-center gap-2">
                <Zap className="h-5 w-5 text-green-600" />
                <span className="font-semibold text-green-800">Available</span>
              </div>
              <p className="text-2xl font-bold text-green-600">
                {bunkLocations.filter(bunk => bunk.slotsAvailable > 0).length}
              </p>
            </div>
            <div className="bg-red-50 p-4 rounded-lg">
              <div className="flex items-center gap-2">
                <Clock className="h-5 w-5 text-red-600" />
                <span className="font-semibold text-red-800">Occupied</span>
              </div>
              <p className="text-2xl font-bold text-red-600">
                {bunkLocations.filter(bunk => bunk.slotsAvailable === 0).length}
              </p>
            </div>
          </div>
        </div>

        {/* Content based on view mode */}
        {viewMode === 'list' ? (
          /* List View */
          <div className="space-y-4">
            {bunkLocations.length === 0 ? (
              <div className="text-center py-12">
                <Zap className="h-16 w-16 text-gray-400 mx-auto mb-4" />
                <h3 className="text-lg font-medium text-gray-600">No charging stations found</h3>
                <p className="text-gray-500">Try refreshing or check your connection</p>
              </div>
            ) : (
              bunkLocations.map((bunk) => (
                <div key={bunk._id} className="bg-white rounded-lg shadow-md p-6 border-l-4 border-l-blue-500">
                  <div className="flex justify-between items-start">
                    <div className="flex-1">
                      <div className="flex items-center gap-2 mb-2">
                        <h3 className="text-xl font-semibold text-gray-800">{bunk.name}</h3>
                        <span className={`px-2 py-1 rounded-full text-xs font-medium ${
                          bunk.slotsAvailable > 0 
                            ? 'bg-green-100 text-green-800' 
                            : 'bg-red-100 text-red-800'
                        }`}>
                          {bunk.slotsAvailable > 0 ? 'Available' : 'Occupied'}
                        </span>
                      </div>
                      
                      <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
                        <div className="space-y-2">
                          <div className="flex items-center gap-2">
                            <MapPin className="h-4 w-4 text-gray-500" />
                            <span className="text-gray-600">{bunk.address}</span>
                          </div>
                          <div className="flex items-center gap-2">
                            <Phone className="h-4 w-4 text-gray-500" />
                            <span className="text-gray-600">{bunk.phone}</span>
                          </div>
                          <div className="flex items-center gap-2">
                            <Clock className="h-4 w-4 text-gray-500" />
                            <span className="text-gray-600">{bunk.operatingHours}</span>
                          </div>
                        </div>
                        
                        <div className="space-y-2">
                          <div className="flex items-center gap-2">
                            <Zap className="h-4 w-4 text-gray-500" />
                            <span className="text-gray-600">
                              {bunk.slotsAvailable} of {bunk.totalSlots} slots available
                            </span>
                          </div>
                          {bunk.connectorTypes && (
                            <div className="text-sm text-gray-500">
                              Connectors: {bunk.connectorTypes.join(', ')}
                            </div>
                          )}
                          {bunk.distance && (
                            <div className="text-sm text-blue-600 font-medium">
                              üìç {bunk.distance.toFixed(1)} km away
                            </div>
                          )}
                          {bunk.drivingDistance && (
                            <div className="text-sm text-green-600">
                              üöó {bunk.drivingDistance.toFixed(1)} km via road ({bunk.drivingTime})
                            </div>
                          )}
                        </div>
                      </div>
                    </div>
                    
                    <div className="flex gap-2 ml-4">
                      {userLocation && (
                        <button
                          onClick={() => handleShowDirections(bunk)}
                          className="flex items-center gap-1 px-3 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors text-sm"
                        >
                          <Navigation className="h-4 w-4" />
                          Directions
                        </button>
                      )}
                      <button
                        onClick={() => handleEditBunk(bunk._id)}
                        className="flex items-center gap-1 px-3 py-2 bg-gray-600 text-white rounded-lg hover:bg-gray-700 transition-colors text-sm"
                      >
                        <Edit3 className="h-4 w-4" />
                        Edit
                      </button>
                    </div>
                  </div>
                </div>
              ))
            )}
          </div>
        ) : (
          /* Map View */
          <div className="bg-white rounded-lg shadow-md overflow-hidden">
            <div className="h-96 md:h-[600px] relative">
              <div 
                ref={mapRef} 
                className="w-full h-full"
                style={{ minHeight: '400px' }}
              />
              {!mapsLoaded && (
                <div className="absolute inset-0 flex items-center justify-center bg-gray-100">
                  <div className="text-center">
                    <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600 mx-auto mb-2"></div>
                    <p className="text-gray-600">Loading Google Maps...</p>
                  </div>
                </div>
              )}
            </div>
            
            {/* Map controls */}
            <div className="p-4 bg-gray-50 border-t">
              <div className="flex flex-wrap gap-2 text-sm">
                <div className="flex items-center gap-1">
                  <div className="w-3 h-3 bg-green-500 rounded-full"></div>
                  <span>Available</span>
                </div>
                <div className="flex items-center gap-1">
                  <div className="w-3 h-3 bg-red-500 rounded-full"></div>
                  <span>Occupied</span>
                </div>
                <div className="flex items-center gap-1">
                  <div className="w-3 h-3 bg-blue-500 rounded-full"></div>
                  <span>Your Location</span>
                </div>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default EvBunkMapView;
