import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import Pagination from '../common/Pagination';
import BookingFilters from './BookingFilters';
import BookingItem from './BookingItem';
import LoadingSpinner from '../common/LoadingSpinner';
import ErrorAlert from '../common/ErrorAlert';
import NoDataMessage from '../common/NoDataMessage';

const AdminBookingList = () => {
  // State for bookings and pagination
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [currentPage, setCurrentPage] = useState(1);
  const [limit, setLimit] = useState(10);
  const [totalPages, setTotalPages] = useState(1);
  const [totalBookings, setTotalBookings] = useState(0);
  
  // State for filters
  const [filters, setFilters] = useState({
    status: '',
    startDate: '',
    endDate: '',
    search: ''
  });

  // Function to fetch bookings
  const fetchBookings = useCallback(async () => {
    try {
      setLoading(true);
      setError(null);

      // Construct query params
      const params = new URLSearchParams();
      params.append('page', currentPage);
      params.append('limit', limit);

      // Add filters if they exist
      if (filters.status) params.append('status', filters.status);
      if (filters.startDate) params.append('startDate', filters.startDate);
      if (filters.endDate) params.append('endDate', filters.endDate);
      if (filters.search) params.append('search', filters.search);

      // Log the request URL for debugging
      console.log(`Requesting: /api/bookings?${params.toString()}`);

      // Make API call
      const response = await axios.get(`/api/bookings?${params.toString()}`);
      
      // Log the full response for debugging
      console.log('API response:', response);

      if (response && response.data && response.data.success) {
        setBookings(response.data.data.bookings || []);
        setTotalPages(response.data.data.pagination?.pages || 1);
        setTotalBookings(response.data.data.pagination?.total || 0);
      } else {
        console.error('Unsuccessful response:', response);
        setError('Failed to fetch bookings - check data format');
      }
    } catch (err) {
      console.error('Error fetching bookings:', err);
      
      // Enhanced error logging
      if (err.response) {
        // The request was made and the server responded with a status code outside of 2xx
        console.error('Response data:', err.response.data);
        console.error('Response status:', err.response.status);
      } else if (err.request) {
        // The request was made but no response was received
        console.error('No response received:', err.request);
      } else {
        // Something happened in setting up the request
        console.error('Request setup error:', err.message);
      }
      
      setError(err.response?.data?.message || 'An error occurred while fetching bookings');
    } finally {
      setLoading(false);
    }
  }, [currentPage, limit, filters]);

  // Fetch bookings when component mounts or filters/pagination changes
  useEffect(() => {
    fetchBookings();
  }, [fetchBookings]);

  // Handle filter changes
  const handleFilterChange = (newFilters) => {
    setFilters(newFilters);
    setCurrentPage(1); // Reset to first page when filters change
  };

  // Handle page changes
  const handlePageChange = (page) => {
    setCurrentPage(page);
  };
  
  // Function to handle changes to the limit/page size (if implemented)
  const handleLimitChange = (newLimit) => {
    setLimit(newLimit);
    setCurrentPage(1); // Reset to first page when limit changes
  };

  // Function to handle booking status changes
  const handleStatusChange = async (bookingId, newStatus) => {
    try {
      const response = await axios.patch(`/api/bookings/${bookingId}/status`, {
        status: newStatus
      });
      
      if (response.data.success) {
        // Update the booking in the local state
        setBookings(bookings.map(booking => 
          booking._id === bookingId 
            ? { ...booking, status: newStatus } 
            : booking
        ));
      } else {
        setError('Failed to update booking status');
      }
    } catch (err) {
      console.error('Error updating booking status:', err);
      setError(err.response?.data?.message || 'An error occurred while updating status');
    }
  };

  return (
    <div className="admin-booking-list">
      <h2 className="text-2xl font-bold mb-4">Manage Bookings</h2>
      
      {/* Filters */}
      <BookingFilters 
        filters={filters} 
        onFilterChange={handleFilterChange} 
      />
      
      {/* Stats Summary */}
      <div className="stats-summary mb-4 p-3 bg-gray-50 rounded-md">
        <p>Total Bookings: <span className="font-semibold">{totalBookings}</span></p>
      </div>
      
      {/* Error Message */}
      {error && <ErrorAlert message={error} />}
      
      {/* Loading Spinner */}
      {loading ? (
        <LoadingSpinner />
      ) : (
        <>
          {/* Bookings List */}
          {bookings.length > 0 ? (
            <div className="bookings-list">
              {bookings.map(booking => (
                <BookingItem 
                  key={booking._id} 
                  booking={booking} 
                  onStatusChange={handleStatusChange}
                  isAdmin={true}
                />
              ))}
            </div>
          ) : (
            <NoDataMessage message="No bookings found matching your filters." />
          )}
          
          {/* Pagination */}
          <Pagination 
            currentPage={currentPage}
            totalPages={totalPages}
            onPageChange={handlePageChange}
          />
          
          {/* Optional: Page Size Selector 
          <div className="page-size-selector mt-4">
            <label htmlFor="limit" className="mr-2">Items per page:</label>
            <select 
              id="limit" 
              value={limit} 
              onChange={(e) => handleLimitChange(Number(e.target.value))}
              className="border rounded px-2 py-1"
            >
              <option value={5}>5</option>
              <option value={10}>10</option>
              <option value={25}>25</option>
              <option value={50}>50</option>
            </select>
          </div>
          */}
        </>
      )}
    </div>
  );
};

export default AdminBookingList;
