const express = require('express');
const connectDB = require('./config/db');
const cors = require('cors');
const authRoutes = require('./routes/auth');
const adminRoutes = require('./routes/adminRoutes'); // Assuming this is also CommonJS
require('dotenv').config();

const app = express();

// Connect to the database
connectDB();

// Middleware
app.use(express.json()); // Parse JSON request bodies
app.use(cors()); // Enable CORS for frontend-backend communication
app.use("/api/admin", adminRoutes);

// Routes
app.use('/api/auth', authRoutes);

// Start the server
const PORT = process.env.PORT || 5000;
app.listen(PORT, () => {
  console.log(`Server running on port ${PORT}`);
});

// MongoDB Connection (You have two connection attempts, let's simplify)
// The connectDB() function likely handles the Mongoose connection.
// Remove the duplicate Mongoose connection here to avoid potential issues.
