import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';

const EditEvBunk = () => {
  const { id } = useParams(); // Getting the Bunk ID from the URL
  const navigate = useNavigate();
  const token = localStorage.getItem('adminToken'); // Assuming token stored here

  const [formData, setFormData] = useState({
    name: '',
    address: '',
    phone: '',
    slotsAvailable: '',
    latitude: '',
    longitude: '',
    operatingHours: '',
    connectorTypes: '',
  });

  const [error, setError] = useState('');
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchBunkDetails = async () => {
      try {
        // Corrected endpoint to match Express routes
        const response = await axios.get(`/api/ev-bunks/${id}`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setFormData(response.data);
      } catch (err) {
        console.error('Error fetching bunk details:', err);
        setError('Failed to load bunk details.');
      } finally {
        setLoading(false);
      }
    };

    if (token && id) {
      fetchBunkDetails();
    } else {
      setLoading(false);
      setError('Missing authentication token or bunk ID');
    }
  }, [token, id]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setMessage('');

    try {
      // Corrected endpoint to match Express routes
      await axios.put(
        `/api/ev-bunks/${id}`,
        formData,
        {
          headers: {
            Authorization: `Bearer ${token}`,
            'Content-Type': 'application/json',
          },
        }
      );
      setMessage('EV Bunk updated successfully!');
      setTimeout(() => {
        navigate('/admin/view-bunks'); // After update, navigate back to bunk list
      }, 1500);
    } catch (err) {
      console.error('Error updating bunk:', err);
      setError('Failed to update EV Bunk: ' + (err.response?.data?.message || err.message));
    }
  };

  if (loading) {
    return (
      <div className="max-w-3xl mx-auto p-8">
        <div className="text-center">Loading bunk details...</div>
      </div>
    );
  }

  return (
    <div className="max-w-3xl mx-auto p-8 bg-white shadow-lg rounded-lg">
      <h2 className="text-3xl font-semibold text-center text-gray-800 mb-6">Edit EV Bunk</h2>

      {/* Back Button */}
      <div className="mb-6">
        <button
          onClick={() => navigate('/admin/view-bunks')}
          className="py-2 px-4 bg-gray-600 text-white rounded hover:bg-gray-700"
        >
          ‚Üê Back to Bunks List
        </button>
      </div>

      {message && <p className="text-green-600 mb-4 text-center font-medium">{message}</p>}
      {error && <p className="text-red-600 mb-4 text-center font-medium">{error}</p>}

      <form onSubmit={handleSubmit} className="space-y-6">
        <div>
          <label htmlFor="name" className="block text-sm font-medium text-gray-700">
            Bunk Name *
          </label>
          <input
            type="text"
            id="name"
            name="name"
            value={formData.name}
            onChange={handleChange}
            required
            className="mt-1 block w-full p-3 border border-gray-300 rounded-md focus:ring-indigo-500 focus:border-indigo-500"
          />
        </div>

        <div>
          <label htmlFor="address" className="block text-sm font-medium text-gray-700">
            Address *
          </label>
          <input
            type="text"
            id="address"
            name="address"
            value={formData.address}
            onChange={handleChange}
            required
            className="mt-1 block w-full p-3 border border-gray-300 rounded-md focus:ring-indigo-500 focus:border-indigo-500"
          />
        </div>

        <div>
          <label htmlFor="phone" className="block text-sm font-medium text-gray-700">
            Phone Number *
          </label>
          <input
            type="text"
            id="phone"
            name="phone"
            value={formData.phone}
            onChange={handleChange}
            required
            className="mt-1 block w-full p-3 border border-gray-300 rounded-md focus:ring-indigo-500 focus:border-indigo-500"
          />
        </div>

        <div>
          <label htmlFor="slotsAvailable" className="block text-sm font-medium text-gray-700">
            Slots Available *
          </label>
          <input
            type="number"
            id="slotsAvailable"
            name="slotsAvailable"
            value={formData.slotsAvailable}
            onChange={handleChange}
            required
            min="0"
            className="mt-1 block w-full p-3 border border-gray-300 rounded-md focus:ring-indigo-500 focus:border-indigo-500"
          />
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <div>
            <label htmlFor="latitude" className="block text-sm font-medium text-gray-700">
              Latitude *
            </label>
            <input
              type="number"
              step="any"
              id="latitude"
              name="latitude"
              value={formData.latitude}
              onChange={handleChange}
              required
              className="mt-1 block w-full p-3 border border-gray-300 rounded-md focus:ring-indigo-500 focus:border-indigo-500"
            />
          </div>

          <div>
            <label htmlFor="longitude" className="block text-sm font-medium text-gray-700">
              Longitude *
            </label>
            <input
              type="number"
              step="any"
              id="longitude"
              name="longitude"
              value={formData.longitude}
              onChange={handleChange}
              required
              className="mt-1 block w-full p-3 border border-gray-300 rounded-md focus:ring-indigo-500 focus:border-indigo-500"
            />
          </div>
        </div>

        <div>
          <label htmlFor="operatingHours" className="block text-sm font-medium text-gray-700">
            Operating Hours
          </label>
          <input
            type="text"
            id="operatingHours"
            name="operatingHours"
            value={formData.operatingHours}
            onChange={handleChange}
            placeholder="e.g., 9:00 AM - 10:00 PM"
            className="mt-1 block w-full p-3 border border-gray-300 rounded-md focus:ring-indigo-500 focus:border-indigo-500"
          />
        </div>

        <div>
          <label htmlFor="connectorTypes" className="block text-sm font-medium text-gray-700">
            Connector Types (comma separated)
          </label>
          <input
            type="text"
            id="connectorTypes"
            name="connectorTypes"
            value={formData.connectorTypes}
            onChange={handleChange}
            placeholder="e.g., Type 1, Type 2, CCS"
            className="mt-1 block w-full p-3 border border-gray-300 rounded-md focus:ring-indigo-500 focus:border-indigo-500"
          />
        </div>

        <div className="flex gap-4">
          <button
            type="submit"
            className="flex-1 py-3 px-6 bg-indigo-600 text-white font-semibold rounded-md hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-indigo-500"
          >
            Update EV Bunk
          </button>
          <button
            type="button"
            onClick={() => navigate('/admin/view-bunks')}
            className="flex-1 py-3 px-6 bg-gray-500 text-white font-semibold rounded-md hover:bg-gray-600 focus:outline-none focus:ring-2 focus:ring-gray-500"
          >
            Cancel
          </button>
        </div>
      </form>
    </div>
  );
};

export default EditEvBunk;
