import React, { useState, useEffect, useRef, useCallback } from 'react';
import { MapPin, Zap, Clock, Phone, Map, List, Edit3, Navigation, AlertCircle } from 'lucide-react';

const EvBunkMapView = () => {
  const [bunkLocations, setBunkLocations] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedBunk, setSelectedBunk] = useState(null);
  const [viewMode, setViewMode] = useState('list');
  const [map, setMap] = useState(null);
  const [userLocation, setUserLocation] = useState(null);
  const [locationPermission, setLocationPermission] = useState('unknown');
  const [mapsLoaded, setMapsLoaded] = useState(false);
  
  const mapRef = useRef(null);
  const infoWindowRef = useRef(null);
  const markersRef = useRef([]);
  const userMarkerRef = useRef(null);
  const googleMapsScriptRef = useRef(null);

  // Mock API key - replace with your actual key
  const GOOGLE_MAPS_API_KEY = 'YOUR_GOOGLE_MAPS_API_KEY';

  // Calculate distance between two coordinates using Haversine formula
  const calculateDistance = (lat1, lon1, lat2, lon2) => {
    const R = 6371; // Radius of the Earth in kilometers
    const dLat = (lat2 - lat1) * Math.PI / 180;
    const dLon = (lon2 - lon1) * Math.PI / 180;
    const a = 
      Math.sin(dLat/2) * Math.sin(dLat/2) +
      Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) * 
      Math.sin(dLon/2) * Math.sin(dLon/2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1-a));
    const distance = R * c;
    return distance;
  };

  // Mock data for demonstration
  const mockBunkData = [
    {
      _id: '1',
      name: 'Central Mall Charging Hub',
      address: '123 Main Street, Downtown',
      phone: '+1-555-0123',
      operatingHours: '24/7',
      latitude: 12.9716,
      longitude: 77.5946,
      slotsAvailable: 3,
      totalSlots: 5,
      connectorTypes: ['Type 2', 'CCS', 'CHAdeMO'],
      status: 'available'
    },
    {
      _id: '2',
      name: 'Tech Park Express Charging',
      address: '456 Innovation Drive, Tech District',
      phone: '+1-555-0456',
      operatingHours: '6:00 AM - 10:00 PM',
      latitude: 12.9616,
      longitude: 77.6046,
      slotsAvailable: 0,
      totalSlots: 4,
      connectorTypes: ['Type 2', 'CCS'],
      status: 'full'
    },
    {
      _id: '3',
      name: 'Highway Rest Stop Chargers',
      address: '789 Highway 101, Mile Marker 25',
      phone: '+1-555-0789',
      operatingHours: '24/7',
      latitude: 12.9816,
      longitude: 77.5846,
      slotsAvailable: 2,
      totalSlots: 3,
      connectorTypes: ['Type 2', 'CCS', 'Tesla'],
      status: 'available'
    }
  ];

  // Normalize bunk data to ensure required properties exist
  const normalizeBunkData = (bunk) => {
    return {
      _id: bunk._id || bunk.id || Math.random().toString(36),
      name: bunk.name || bunk.stationName || 'Unnamed Station',
      address: bunk.address || bunk.location || 'Address not available',
      phone: bunk.phone || bunk.phoneNumber || bunk.contact || 'Not available',
      operatingHours: bunk.operatingHours || bunk.hours || '24/7',
      latitude: parseFloat(bunk.latitude || bunk.lat || 0),
      longitude: parseFloat(bunk.longitude || bunk.lng || bunk.lon || 0),
      slotsAvailable: parseInt(bunk.slotsAvailable || bunk.availableSlots || bunk.slots || 0),
      totalSlots: parseInt(bunk.totalSlots || bunk.slotsAvailable || 0),
      connectorTypes: bunk.connectorTypes || bunk.connectors || [],
      distance: bunk.distance || null,
      status: bunk.status || (bunk.slotsAvailable > 0 ? 'available' : 'full')
    };
  };

  // Add distance calculation to bunks
  const addDistanceToBunks = useCallback((bunks, userLat, userLng) => {
    if (!userLat || !userLng || !Array.isArray(bunks)) return bunks;
    
    return bunks.map(bunk => {
      const normalizedBunk = normalizeBunkData(bunk);
      return {
        ...normalizedBunk,
        distance: calculateDistance(userLat, userLng, normalizedBunk.latitude, normalizedBunk.longitude)
      };
    }).sort((a, b) => a.distance - b.distance);
  }, []);

  // Get user's current location
  const getCurrentLocation = useCallback(() => {
    return new Promise((resolve, reject) => {
      if (!navigator.geolocation) {
        reject(new Error('Geolocation is not supported by this browser.'));
        return;
      }

      navigator.geolocation.getCurrentPosition(
        (position) => {
          const location = {
            lat: position.coords.latitude,
            lng: position.coords.longitude
          };
          setUserLocation(location);
          setLocationPermission('granted');
          resolve(location);
        },
        (error) => {
          console.error('Error getting location:', error);
          setLocationPermission('denied');
          reject(error);
        },
        {
          enableHighAccuracy: true,
          timeout: 10000,
          maximumAge: 300000
        }
      );
    });
  }, []);

  // Load Google Maps script
  useEffect(() => {
    const loadGoogleMaps = () => {
      return new Promise((resolve, reject) => {
        if (window.google && window.google.maps) {
          setMapsLoaded(true);
          resolve();
          return;
        }

        if (googleMapsScriptRef.current) {
          googleMapsScriptRef.current.addEventListener('load', () => {
            setMapsLoaded(true);
            resolve();
          });
          googleMapsScriptRef.current.addEventListener('error', reject);
          return;
        }

        const existingScript = document.querySelector(`script[src*="maps.googleapis.com"]`);
        if (existingScript) {
          if (window.google && window.google.maps) {
            setMapsLoaded(true);
            resolve();
          } else {
            existingScript.addEventListener('load', () => {
              setMapsLoaded(true);
              resolve();
            });
            existingScript.addEventListener('error', reject);
          }
          return;
        }

        // For demo purposes, we'll simulate Google Maps loading
        // In production, replace with actual Google Maps script
        setTimeout(() => {
          console.log('Google Maps would be loaded here');
          setMapsLoaded(true);
          resolve();
        }, 1000);
      });
    };

    loadGoogleMaps().catch(error => {
      console.error('Failed to load Google Maps:', error);
      setError('Failed to load Google Maps');
    });
  }, []);

  // Clear existing markers
  const clearMarkers = useCallback(() => {
    markersRef.current.forEach(marker => {
      if (marker && marker.setMap) {
        marker.setMap(null);
      }
    });
    markersRef.current = [];
  }, []);

  // Mock API functions
  const fetchBunkLocations = async () => {
    try {
      setLoading(true);
      setError(null);
      
      // Simulate API delay
      await new Promise(resolve => setTimeout(resolve, 1000));
      
      let bunks = mockBunkData.map(bunk => normalizeBunkData(bunk));

      if (userLocation && bunks.length > 0) {
        bunks = addDistanceToBunks(bunks, userLocation.lat, userLocation.lng);
      }

      setBunkLocations(bunks);
    } catch (error) {
      console.error('Error fetching bunk locations:', error);
      setError(`Failed to fetch charging stations: ${error.message}`);
      setBunkLocations([]);
    } finally {
      setLoading(false);
    }
  };

  const fetchAvailableBunks = async () => {
    try {
      setLoading(true);
      setError(null);
      
      await new Promise(resolve => setTimeout(resolve, 500));
      
      let bunks = mockBunkData
        .filter(bunk => bunk.slotsAvailable > 0)
        .map(bunk => normalizeBunkData(bunk));

      if (userLocation && bunks.length > 0) {
        bunks = addDistanceToBunks(bunks, userLocation.lat, userLocation.lng);
      }

      setBunkLocations(bunks);
    } catch (error) {
      console.error('Error fetching available bunks:', error);
      setError(`Failed to fetch available stations: ${error.message}`);
    } finally {
      setLoading(false);
    }
  };

  const fetchNearbyBunks = async (lat, lng, radius = 10) => {
    try {
      setLoading(true);
      setError(null);
      
      await new Promise(resolve => setTimeout(resolve, 500));
      
      let bunks = mockBunkData
        .map(bunk => normalizeBunkData(bunk))
        .map(bunk => ({
          ...bunk,
          distance: calculateDistance(lat, lng, bunk.latitude, bunk.longitude)
        }))
        .filter(bunk => bunk.distance <= radius && bunk.latitude && bunk.longitude)
        .sort((a, b) => a.distance - b.distance);

      setBunkLocations(bunks);
      
      if (bunks.length === 0) {
        setError(`No charging stations found within ${radius}km of your location`);
      }
    } catch (error) {
      console.error('Error fetching nearby bunks:', error);
      setError(`Failed to fetch nearby stations: ${error.message}`);
      setBunkLocations([]);
    } finally {
      setLoading(false);
    }
  };

  // Handle nearby button click with geolocation
  const handleNearbyClick = async () => {
    try {
      setLoading(true);
      const location = await getCurrentLocation();
      await fetchNearbyBunks(location.lat, location.lng);
      setViewMode('map');
    } catch (error) {
      console.error('Error getting location:', error);
      const fallbackLocation = { lat: 12.9716, lng: 77.5946 };
      setUserLocation(fallbackLocation);
      await fetchNearbyBunks(fallbackLocation.lat, fallbackLocation.lng);
      alert('Could not get your location. Showing nearby stations from default location.');
    }
  };

  // Handle edit button click
  const handleEditBunk = (bunkId) => {
    alert(`Edit functionality for bunk ${bunkId} would be implemented here`);
  };

  // Handle view mode change
  const handleViewModeChange = (mode) => {
    if (mode === 'list' && map) {
      setMap(null);
      clearMarkers();
      if (userMarkerRef.current) {
        userMarkerRef.current = null;
      }
    }
    setViewMode(mode);
  };

  // Initialize component
  useEffect(() => {
    fetchBunkLocations();
  }, []);

  // Recalculate distances when user location changes
  useEffect(() => {
    if (userLocation && bunkLocations.length > 0) {
      const needsDistanceCalculation = bunkLocations.some(bunk => !bunk.distance);
      if (needsDistanceCalculation) {
        const bunksWithDistance = addDistanceToBunks(bunkLocations, userLocation.lat, userLocation.lng);
        setBunkLocations(bunksWithDistance);
      }
    }
  }, [userLocation, addDistanceToBunks]);

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen bg-gray-50">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto mb-4"></div>
          <p className="text-gray-600">Loading EV charging locations...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="flex items-center justify-center min-h-screen bg-gray-50">
        <div className="text-center p-6 bg-red-50 rounded-lg max-w-md border border-red-200">
          <div className="text-red-600 mb-4">
            <AlertCircle className="h-12 w-12 mx-auto mb-2" />
            <h3 className="font-semibold text-lg">Connection Error</h3>
          </div>
          <p className="text-red-700 mb-4">{error}</p>
          <button 
            onClick={fetchBunkLocations}
            className="bg-red-600 text-white px-4 py-2 rounded hover:bg-red-700 transition-colors"
          >
            Try Again
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <div className="bg-white shadow-sm border-b">
        <div className="container mx-auto px-6 py-4">
          <h1 className="text-2xl font-bold text-gray-800">EV Charging Stations</h1>
        </div>
      </div>

      <div className="container mx-auto p-6">
        {/* Location Permission Status */}
        {locationPermission !== 'unknown' && (
          <div className={`mb-4 p-3 rounded-lg text-sm ${
            locationPermission === 'granted' 
              ? 'bg-green-50 text-green-700 border border-green-200' 
              : 'bg-yellow-50 text-yellow-700 border border-yellow-200'
          }`}>
            {locationPermission === 'granted' ? (
              <div className="flex items-center gap-2">
                <Navigation className="h-4 w-4" />
                Location access granted - showing personalized results with distances
              </div>
            ) : (
              <div className="flex items-center gap-2">
                <MapPin className="h-4 w-4" />
                Location access denied - using default location
              </div>
            )}
          </div>
        )}
        
        {/* View Mode Toggle */}
        <div className="flex gap-2 mb-4">
          <button
            onClick={() => handleViewModeChange('list')}
            className={`flex items-center gap-2 px-4 py-2 rounded transition-colors ${
              viewMode === 'list' 
                ? 'bg-blue-600 text-white' 
                : 'bg-white text-gray-700 hover:bg-gray-50 border border-gray-300'
            }`}
          >
            <List className="h-4 w-4" />
            List View
          </button>
          <button
            onClick={() => handleViewModeChange('map')}
            className={`flex items-center gap-2 px-4 py-2 rounded transition-colors ${
              viewMode === 'map' 
                ? 'bg-blue-600 text-white' 
                : 'bg-white text-gray-700 hover:bg-gray-50 border border-gray-300'
            }`}
            disabled={!mapsLoaded}
          >
            <Map className="h-4 w-4" />
            {mapsLoaded ? 'Map View' : 'Loading Maps...'}
          </button>
        </div>
        
        {/* Filter Buttons */}
        <div className="flex flex-wrap gap-2 mb-6">
          <button 
            onClick={fetchBunkLocations}
            className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 transition-colors flex items-center gap-2"
            disabled={loading}
          >
            <Zap className="h-4 w-4" />
            All Stations
          </button>
          <button 
            onClick={fetchAvailableBunks}
            className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700 transition-colors flex items-center gap-2"
            disabled={loading}
          >
            <Zap className="h-4 w-4" />
            Available Only
          </button>
          <button 
            onClick={handleNearbyClick}
            className="bg-purple-600 text-white px-4 py-2 rounded hover:bg-purple-700 transition-colors flex items-center gap-2"
            disabled={loading}
          >
            <Navigation className="h-4 w-4" />
            {loading ? 'Finding...' : 'Nearby Stations'}
          </button>
        </div>

        <p className="text-gray-600 mb-6">
          Found {bunkLocations.length} charging station{bunkLocations.length !== 1 ? 's' : ''}
          {userLocation && bunkLocations.some(b => b.distance) && (
            <span className="text-sm text-gray-500 ml-2">
              (sorted by distance from your location)
            </span>
          )}
        </p>

        {/* Map View */}
        {viewMode === 'map' && (
          <div className="mb-6">
            <div 
              ref={mapRef}
              className="w-full h-96 rounded-lg border border-gray-300 bg-gray-100 flex items-center justify-center"
            >
              {mapsLoaded ? (
                <div className="text-center text-gray-600">
                  <Map className="h-16 w-16 mx-auto mb-4 text-gray-400" />
                  <p>Interactive map would be displayed here</p>
                  <p className="text-sm mt-2">Google Maps integration required</p>
                </div>
              ) : (
                <div className="text-center text-gray-600">
                  <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600 mx-auto mb-2"></div>
                  <p>Loading Google Maps...</p>
                </div>
              )}
            </div>
            <div className="mt-2 text-sm text-gray-600 flex items-center gap-4">
              <div className="flex items-center gap-1">
                <div className="w-4 h-4 bg-green-500 rounded-full"></div>
                <span>Available</span>
              </div>
              <div className="flex items-center gap-1">
                <div className="w-4 h-4 bg-red-500 rounded-full"></div>
                <span>Full</span>
              </div>
              {userLocation && (
                <div className="flex items-center gap-1">
                  <div className="w-4 h-4 bg-blue-500 rounded-full"></div>
                  <span>Your Location</span>
                </div>
              )}
            </div>
          </div>
        )}

        {/* List View */}
        {viewMode === 'list' && (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {bunkLocations.length === 0 ? (
              <div className="col-span-full text-center py-12 bg-white rounded-lg shadow-sm">
                <Zap className="h-16 w-16 text-gray-400 mx-auto mb-4" />
                <h3 className="text-xl font-semibold text-gray-600 mb-2">No Charging Stations Found</h3>
                <p className="text-gray-500">Try adjusting your search criteria.</p>
              </div>
            ) : (
              bunkLocations.map((bunk) => (
                <div 
                  key={bunk._id} 
                  className="bg-white rounded-lg shadow-sm hover:shadow-md transition-shadow duration-300 p-6 border border-gray-200"
                >
                  <div className="flex items-start justify-between mb-4">
                    <div className="flex items-center min-w-0 flex-1">
                      <MapPin className="h-5 w-5 text-blue-600 mr-2 flex-shrink-0" />
                      <h3 className="font-semibold text-lg text-gray-800 truncate">{bunk.name}</h3>
                    </div>
                    <div className="flex items-center gap-2 ml-2">
                      <button
                        onClick={() => handleEditBunk(bunk._id)}
                        className="p-1 text-gray-600 hover:text-blue-600 hover:bg-blue-50 rounded transition-colors"
                        title="Edit Station"
                      >
                        <Edit3 className="h-4 w-4" />
                      </button>
                      <span className={`px-2 py-1 rounded-full text-xs font-medium whitespace-nowrap ${
                        bunk.slotsAvailable > 0 
                          ? 'bg-green-100 text-green-800' 
                          : 'bg-red-100 text-red-800'
                      }`}>
                        {bunk.slotsAvailable > 0 ? 'Available' : 'Full'}
                      </span>
                    </div>
                  </div>

                  <div className="space-y-3">
                    <p className="text-gray-600 text-sm line-clamp-2">{bunk.address}</p>
                    
                    <div className="flex items-center text-sm text-gray-600">
                      <Phone className="h-4 w-4 mr-2 flex-shrink-0" />
                      <span className="truncate">{bunk.phone}</span>
                    </div>

                    <div className="flex items-center text-sm text-gray-600">
                      <Clock className="h-4 w-4 mr-2 flex-shrink-0" />
                      <span>{bunk.operatingHours}</span>
                    </div>

                    <div className="flex items-center text-sm">
                      <Zap className="h-4 w-4 mr-2 text-yellow-600 flex-shrink-0" />
                      <span className="font-medium">{bunk.slotsAvailable} of {bunk.totalSlots} slots available</span>
                    </div>

                    {bunk.connectorTypes && bunk.connectorTypes.length > 0 && (
                      <div className="flex flex-wrap gap-1 mt-2">
                        {bunk.connectorTypes.map((connector, index) => (
                          <span 
                            key={index}
                            className="bg-blue-100 text-blue-800 text-xs px-2 py-1 rounded"
                          >
                            {connector}
                          </span>
                        ))}
                      </div>
                    )}

                    {bunk.distance && (
                      <p className="text-sm font-medium text-purple-600 flex items-center gap-1">
                        <Navigation className="h-3 w-3" />
                        {bunk.distance.toFixed(1)} km away
                      </p>
                    )}
                  </div>

                  <div className="flex gap-2 mt-4">
                    <button 
                      onClick={() => setSelectedBunk(bunk)}
                      className="flex-1 bg-blue-600 text-white py-2 px-4 rounded hover:bg-blue-700 transition-colors text-sm"
                    >
                      View Details
                    </button>
                    <button 
                      onClick={() => handleEditBunk(bunk._id)}
                      className="bg-orange-600 text-white py-2 px-3 rounded hover:bg-orange-700 transition-colors flex items-center"
                    >
                      <Edit3 className="h-4 w-4" />
                    </button>
                    <button 
                      onClick={() => {
                        handleViewModeChange('map');
                      }}
                      className="bg-gray-600 text-white py-2 px-3 rounded hover:bg-gray-700 transition-colors"
                      disabled={!mapsLoaded}
                    >
                      <Map className="h-4 w-4" />
                    </button>
                  </div>
                </div>
              ))
            )}
          </div>
        )}

        {/* Modal for selected bunk details */}
        {selectedBunk && (
          <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
            <div className="bg-white rounded-lg max-w-md w-full p-6 max-h-[90vh] overflow-y-auto">
              <div className="flex items-center justify-between mb-4">
                <h2 className="text-xl font-bold text-gray-800">{selectedBunk.name}</h2>
                <div className="flex items-center gap-2">
                  <button
                    onClick={() => handleEditBunk(selectedBunk._id)}
                    className="p-2 text-gray-600 hover:text-blue-600 hover:bg-blue-50 rounded transition-colors"
                    title="Edit Station"
                  >
                    <Edit3 className="h-4 w-4" />
                  </button>
                  <button 
                    onClick={() => setSelectedBunk(null)}
                    className="text-gray-500 hover:text-gray-700 text-xl font-bold w-6 h-6 flex items-center justify-center"
                  >
                    ×
                  </button>
                </div>
              </div>
              
              <div className="space-y-3 text-sm">
                <div>
                  <strong className="text-gray-700">Address:</strong>
                  <p className="text-gray-600 mt-1">{selectedBunk.address}</p>
                </div>
                
                <div>
                  <strong className="text-gray-700">Phone:</strong>
                  <p className="text-gray-600 mt-1">{selectedBunk.phone}</p>
                </div>
                
                <div>
                  <strong className="text-gray-700">Operating Hours:</strong>
                  <p className="text-gray-600 mt-1">{selectedBunk.operatingHours}</p>
                </div>
                
                <div>
                  <strong className="text-gray-700">Available Slots:</strong>
                  <p className="text-gray-600 mt-1">{selectedBunk.slotsAvailable} of {selectedBunk.totalSlots}</p>
                </div>
                
                <div>
                  <strong className="text-gray-700">Coordinates:</strong>
                  <p className="text-gray-600 mt-1">{selectedBunk.latitude.toFixed(6)}, {selectedBunk.longitude.toFixed(6)}</p>
                </div>
                
                {selectedBunk.connectorTypes && selectedBunk.connectorTypes.length > 0 && (
                  <div>
                    <strong className="text-gray-700">Connector Types:</strong>
                    <div className="flex flex-wrap gap-1 mt-2">
                      {selectedBunk.connectorTypes.map((connector, index) => (
                        <span 
                          key={index}
                          className="bg-blue-100 text-blue-800 text-xs px-2 py-1 rounded"
                        >
                          {connector}
                        </span>
                      ))}
                    </div>
                  </div>
                )}

                {selectedBunk.distance && (
                  <div>
                    <strong className="text-gray-700">Distance:</strong>
                    <p className="text-purple-600 mt-1 flex items-center gap-1">
                      <Navigation className="h-4 w-4" />
                      {selectedBunk.distance.toFixed(1)} km away
                    </p>
                  </div>
                )}
              </div>

              <div className="flex gap-2 mt-6 pt-4 border-t">
                <button
                  onClick={() => {
                    const url = `https://www.google.com/maps/dir/?api=1&destination=${selectedBunk.latitude},${selectedBunk.longitude}`;
                    window.open(url, '_blank');
                  }}
                  className="flex-1 bg-green-600 text-white py-2 px-4 rounded hover:bg-green-700 transition-colors text-sm"
                >
                  Get Directions
                </button>
                <button
                  onClick={() => handleEditBunk(selectedBunk._id)}
                  className="bg-orange-600 text-white py-2 px-4 rounded hover:bg-orange-700 transition-colors flex items-center gap-1 text-sm"
                >
                  <Edit3 className="h-4 w-4" />
                  Edit
                </button>
                <button
                  onClick={() => setSelectedBunk(null)}
                  className="bg-gray-600 text-white py-2 px-4 rounded hover:bg-gray-700 transition-colors text-sm"
                >
                  Close
                </button>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default EvBunkMapView;
