import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate, useParams, useLocation } from 'react-router-dom';

const EditBunk = () => {
  const navigate = useNavigate();
  const { id } = useParams();
  const location = useLocation();
  
  // Get data from navigation state (Method 1)
  const bunkDataFromState = location.state?.bunkData;
  
  const [formData, setFormData] = useState({
    name: '',
    bunkId: '',
    address: '',
    phone: '',
    slotsAvailable: '',
    operatingHours: '',
    connectorTypes: '',
    latitude: '',
    longitude: ''
  });
  
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  useEffect(() => {
    // Method 1: Use data from navigation state
    if (bunkDataFromState) {
      setFormData({
        name: bunkDataFromState.name || '',
        bunkId: bunkDataFromState.bunkId || '',
        address: bunkDataFromState.address || '',
        phone: bunkDataFromState.phone || '',
        slotsAvailable: bunkDataFromState.slotsAvailable || '',
        operatingHours: bunkDataFromState.operatingHours || '',
        connectorTypes: bunkDataFromState.connectorTypes || '',
        latitude: bunkDataFromState.latitude || '',
        longitude: bunkDataFromState.longitude || ''
      });
    } 
    // Method 2: Check sessionStorage (fallback)
    else {
      const storedData = sessionStorage.getItem('editBunkData');
      if (storedData) {
        const parsedData = JSON.parse(storedData);
        setFormData({
          name: parsedData.name || '',
          bunkId: parsedData.bunkId || '',
          address: parsedData.address || '',
          phone: parsedData.phone || '',
          slotsAvailable: parsedData.slotsAvailable || '',
          operatingHours: parsedData.operatingHours || '',
          connectorTypes: parsedData.connectorTypes || '',
          latitude: parsedData.latitude || '',
          longitude: parsedData.longitude || ''
        });
        // Clean up sessionStorage
        sessionStorage.removeItem('editBunkData');
      }
      // Method 3: Fetch from API (fallback if no data passed)
      else {
        fetchBunkData();
      }
    }
  }, [id, bunkDataFromState]);

  // Fallback: Fetch data from API if not passed via state
  const fetchBunkData = async () => {
    setLoading(true);
    try {
      const response = await axios.get(`/api/admin/ev-bunks/${id}`, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`,
        },
      });
      const bunk = response.data;
      setFormData({
        name: bunk.name || '',
        bunkId: bunk.bunkId || '',
        address: bunk.address || '',
        phone: bunk.phone || '',
        slotsAvailable: bunk.slotsAvailable || '',
        operatingHours: bunk.operatingHours || '',
        connectorTypes: bunk.connectorTypes || '',
        latitude: bunk.latitude || '',
        longitude: bunk.longitude || ''
      });
    } catch (err) {
      setError('Error fetching bunk data: ' + (err.response?.data?.message || err.message));
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      await axios.put(`/api/admin/ev-bunks/${id}`, formData, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`,
          'Content-Type': 'application/json',
        },
      });
      navigate('/admin/view-bunks'); // Navigate back to view bunks
    } catch (err) {
      setError('Error updating bunk: ' + (err.response?.data?.message || err.message));
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return <div className="text-center mt-10">Loading...</div>;
  }

  return (
    <div className="max-w-2xl mx-auto p-6">
      <h2 className="text-3xl font-bold text-center mb-8">Edit EV Bunk</h2>

      {/* Back Button */}
      <div className="mb-8">
        <button
          onClick={() => navigate('/admin/view-bunks')}
          className="py-2 px-4 bg-gray-700 text-white rounded hover:bg-gray-800"
        >
          ← Back to View Bunks
        </button>
      </div>

      {/* Error Message */}
      {error && <p className="text-red-600 mb-4">{error}</p>}

      {/* Edit Form */}
      <form onSubmit={handleSubmit} className="bg-white p-6 rounded-lg shadow">
        <div className="mb-4">
          <label className="block text-gray-700 text-sm font-bold mb-2">
            Bunk Name
          </label>
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleChange}
            className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:border-blue-500"
            required
          />
        </div>

        <div className="mb-4">
          <label className="block text-gray-700 text-sm font-bold mb-2">
            Bunk ID
          </label>
          <input
            type="text"
            name="bunkId"
            value={formData.bunkId}
            onChange={handleChange}
            className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:border-blue-500"
            required
          />
        </div>

        <div className="mb-4">
          <label className="block text-gray-700 text-sm font-bold mb-2">
            Address
          </label>
          <input
            type="text"
            name="address"
            value={formData.address}
            onChange={handleChange}
            className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:border-blue-500"
            required
          />
        </div>

        <div className="mb-4">
          <label className="block text-gray-700 text-sm font-bold mb-2">
            Phone
          </label>
          <input
            type="text"
            name="phone"
            value={formData.phone}
            onChange={handleChange}
            className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:border-blue-500"
            required
          />
        </div>

        <div className="mb-4">
          <label className="block text-gray-700 text-sm font-bold mb-2">
            Available Slots
          </label>
          <input
            type="number"
            name="slotsAvailable"
            value={formData.slotsAvailable}
            onChange={handleChange}
            className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:border-blue-500"
            required
          />
        </div>

        <div className="mb-4">
          <label className="block text-gray-700 text-sm font-bold mb-2">
            Operating Hours
          </label>
          <input
            type="text"
            name="operatingHours"
            value={formData.operatingHours}
            onChange={handleChange}
            className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:border-blue-500"
            placeholder="e.g., 9:00 AM - 10:00 PM"
          />
        </div>

        <div className="mb-4">
          <label className="block text-gray-700 text-sm font-bold mb-2">
            Connector Types
          </label>
          <input
            type="text"
            name="connectorTypes"
            value={formData.connectorTypes}
            onChange={handleChange}
            className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:border-blue-500"
            placeholder="e.g., Type 1, Type 2, CCS"
          />
        </div>

        <div className="mb-4">
          <label className="block text-gray-700 text-sm font-bold mb-2">
            Latitude
          </label>
          <input
            type="number"
            step="any"
            name="latitude"
            value={formData.latitude}
            onChange={handleChange}
            className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:border-blue-500"
            required
          />
        </div>

        <div className="mb-6">
          <label className="block text-gray-700 text-sm font-bold mb-2">
            Longitude
          </label>
          <input
            type="number"
            step="any"
            name="longitude"
            value={formData.longitude}
            onChange={handleChange}
            className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:border-blue-500"
            required
          />
        </div>

        <div className="flex gap-4">
          <button
            type="submit"
            disabled={loading}
            className="flex-1 bg-blue-500 text-white py-2 px-4 rounded-lg hover:bg-blue-600 disabled:opacity-50"
          >
            {loading ? 'Updating...' : 'Update Bunk'}
          </button>
          <button
            type="button"
            onClick={() => navigate('/admin/view-bunks')}
            className="flex-1 bg-gray-500 text-white py-2 px-4 rounded-lg hover:bg-gray-600"
          >
            Cancel
          </button>
        </div>
