import React, { useEffect, useState } from 'react';
import axios from 'axios';
import RescheduleBookingForm from './RescheduleBookingForm';

const UserBookings = ({ userId }) => {
  const [bookings, setBookings] = useState([]);
  const [selectedBooking, setSelectedBooking] = useState(null);
  const [showRescheduleForm, setShowRescheduleForm] = useState(false);

  useEffect(() => {
    const fetchBookings = async () => {
      try {
        const response = await axios.get(`/api/bookings/user/${userId}`);
        setBookings(response.data);
      } catch (error) {
        console.error('Error fetching user bookings:', error);
      }
    };

    fetchBookings();
  }, [userId]);

  const handleCancel = async (bookingId) => {
    try {
      await axios.delete(`/api/bookings/${bookingId}`);
      setBookings((prev) => prev.filter((booking) => booking._id !== bookingId));
    } catch (error) {
      console.error('Error cancelling booking:', error);
    }
  };

  const handleRescheduleClick = (booking) => {
    setSelectedBooking(booking);
    setShowRescheduleForm(true);
  };

  const handleReschedule = async (bookingId, newSlot) => {
    try {
      const response = await axios.put(`/api/bookings/${bookingId}/reschedule`, {
        slot: newSlot,
      });
      setBookings((prev) =>
        prev.map((booking) =>
          booking._id === bookingId ? response.data : booking
        )
      );
      setShowRescheduleForm(false);
      setSelectedBooking(null);
    } catch (error) {
      console.error('Error rescheduling booking:', error);
    }
  };

  return (
    <div className="p-4">
      <h2 className="text-2xl font-semibold mb-4">My Bookings</h2>
      {bookings.length === 0 ? (
        <p>No bookings found.</p>
      ) : (
        <ul className="space-y-4">
          {bookings.map((booking) => (
            <li
              key={booking._id}
              className="border border-gray-300 rounded-lg p-4 shadow"
            >
              <p><strong>Bunk Name:</strong> {booking.bunk?.name}</p>
              <p><strong>Location:</strong> {booking.bunk?.location}</p>
              <p><strong>Slot:</strong> {booking.slot}</p>
              <p><strong>Status:</strong> {booking.status}</p>

              <div className="mt-2 flex gap-2">
                <button
                  onClick={() => handleCancel(booking._id)}
                  className="px-4 py-1 bg-red-500 text-white rounded hover:bg-red-600"
                >
                  Cancel
                </button>
                <button
                  onClick={() => handleRescheduleClick(booking)}
                  className="px-4 py-1 bg-yellow-500 text-white rounded hover:bg-yellow-600"
                >
                  Reschedule
                </button>
              </div>
            </li>
          ))}
        </ul>
      )}

      {showRescheduleForm && selectedBooking && (
        <div className="mt-6 border-t pt-4">
          <h3 className="text-xl font-semibold mb-2">Reschedule Booking</h3>
          <RescheduleBookingForm
            booking={selectedBooking}
            onReschedule={handleReschedule}
            onCancel={() => {
              setShowRescheduleForm(false);
              setSelectedBooking(null);
            }}
          />
        </div>
      )}
    </div>
  );
};

export default UserBookings;
