import React, { useState, useEffect, useRef, useCallback } from 'react';
import { MapPin, Zap, Clock, Phone, Map, List, Edit3, Navigation } from 'lucide-react';
import AdminNavbar from "../common/navbars/AdminNavbar";

const EvBunkMapView = () => {
  const [bunkLocations, setBunkLocations] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedBunk, setSelectedBunk] = useState(null);
  const [viewMode, setViewMode] = useState('list');
  const [map, setMap] = useState(null);
  const [userLocation, setUserLocation] = useState(null);
  const [locationPermission, setLocationPermission] = useState('unknown');
  const [mapsLoaded, setMapsLoaded] = useState(false);
  const [showNearbyOnly, setShowNearbyOnly] = useState(false);
  
  const mapRef = useRef(null);
  const infoWindowRef = useRef(null);
  const markersRef = useRef([]);
  const userMarkerRef = useRef(null);
  const directionsRendererRef = useRef(null);

  const GOOGLE_MAPS_API_KEY = 'AIzaSyDozw7FDv161gMDT9lE-U0cSGZuWjYhyvw';

  // Calculate distance between two coordinates using Haversine formula
  const calculateDistance = (lat1, lon1, lat2, lon2) => {
    const R = 6371; // Radius of the Earth in kilometers
    const dLat = (lat2 - lat1) * Math.PI / 180;
    const dLon = (lon2 - lon1) * Math.PI / 180;
    const a = 
      Math.sin(dLat/2) * Math.sin(dLat/2) +
      Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) * 
      Math.sin(dLon/2) * Math.sin(dLon/2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1-a));
    const distance = R * c;
    return distance;
  };

  // Add distance calculation to bunks
  const addDistanceToBunks = (bunks, userLat, userLng) => {
    return bunks.map(bunk => ({
      ...bunk,
      distance: calculateDistance(userLat, userLng, bunk.latitude, bunk.longitude)
    })).sort((a, b) => a.distance - b.distance);
  };

  // Get user's current location with better error handling
  const getCurrentLocation = useCallback(() => {
    return new Promise((resolve, reject) => {
      if (!navigator.geolocation) {
        reject(new Error('Geolocation is not supported by this browser.'));
        return;
      }

      console.log('🔍 Requesting user location...');
      
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const location = {
            lat: position.coords.latitude,
            lng: position.coords.longitude
          };
          console.log('📍 Location obtained:', location);
          setUserLocation(location);
          setLocationPermission('granted');
          resolve(location);
        },
        (error) => {
          console.error('❌ Location error:', error);
          setLocationPermission('denied');
          
          // Provide more specific error messages
          let errorMessage = 'Location access denied';
          switch(error.code) {
            case error.PERMISSION_DENIED:
              errorMessage = 'Location access denied by user';
              break;
            case error.POSITION_UNAVAILABLE:
              errorMessage = 'Location information unavailable';
              break;
            case error.TIMEOUT:
              errorMessage = 'Location request timed out';
              break;
          }
          
          reject(new Error(errorMessage));
        },
        {
          enableHighAccuracy: true,
          timeout: 15000, // Increased timeout
          maximumAge: 300000
        }
      );
    });
  }, []);

  // Load Google Maps script with async loading
  useEffect(() => {
    const loadGoogleMaps = () => {
      return new Promise((resolve, reject) => {
        if (window.google && window.google.maps) {
          setMapsLoaded(true);
          resolve();
          return;
        }

        const existingScript = document.querySelector(`script[src*="maps.googleapis.com"]`);
        if (existingScript) {
          existingScript.addEventListener('load', () => {
            setMapsLoaded(true);
            resolve();
          });
          existingScript.addEventListener('error', reject);
          return;
        }

        const script = document.createElement('script');
        script.src = `https://maps.googleapis.com/maps/api/js?key=${GOOGLE_MAPS_API_KEY}&libraries=places&loading=async`;
        script.async = true;
        script.defer = true;
        
        script.onload = () => {
          setMapsLoaded(true);
          resolve();
        };
        script.onerror = reject;
        
        document.head.appendChild(script);
      });
    };

    loadGoogleMaps().catch(error => {
      console.error('Failed to load Google Maps:', error);
      setError('Failed to load Google Maps');
    });
  }, [GOOGLE_MAPS_API_KEY]);

  // Clear existing markers
  const clearMarkers = useCallback(() => {
    markersRef.current.forEach(marker => {
      marker.setMap(null);
    });
    markersRef.current = [];
  }, []);

  // Clear directions
  const clearDirections = useCallback(() => {
    if (directionsRendererRef.current) {
      directionsRendererRef.current.setMap(null);
      directionsRendererRef.current = null;
    }
  }, []);

  // Show directions between user and selected bunk
  const showDirections = useCallback((destinationBunk) => {
    if (!map || !userLocation || !window.google) return;

    clearDirections();

    const directionsService = new window.google.maps.DirectionsService();
    const directionsRenderer = new window.google.maps.DirectionsRenderer({
      suppressMarkers: false,
      polylineOptions: {
        strokeColor: '#4285f4',
        strokeWeight: 4,
        strokeOpacity: 0.8
      }
    });

    directionsRenderer.setMap(map);
    directionsRendererRef.current = directionsRenderer;

    const request = {
      origin: userLocation,
      destination: { lat: destinationBunk.latitude, lng: destinationBunk.longitude },
      travelMode: window.google.maps.TravelMode.DRIVING
    };

    directionsService.route(request, (result, status) => {
      if (status === 'OK') {
        directionsRenderer.setDirections(result);
        
        const route = result.routes[0];
        const leg = route.legs[0];
        const drivingDistance = leg.distance.value / 1000;
        
        setBunkLocations(prevBunks => 
          prevBunks.map(bunk => 
            bunk._id === destinationBunk._id 
              ? { ...bunk, drivingDistance: drivingDistance }
              : bunk
          )
        );
      } else {
        console.error('Directions request failed:', status);
      }
    });
  }, [map, userLocation, clearDirections]);

  // Add or update user location marker using AdvancedMarkerElement
  const updateUserLocationMarker = useCallback((mapInstance) => {
    if (userLocation && mapInstance && window.google) {
      if (userMarkerRef.current) {
        userMarkerRef.current.map = null;
      }

      // Create custom icon element
      const iconElement = document.createElement('div');
      iconElement.innerHTML = `
        <div style="
          width: 32px; 
          height: 32px; 
          background: #2563eb; 
          border: 3px solid white; 
          border-radius: 50%; 
          position: relative;
          box-shadow: 0 2px 6px rgba(0,0,0,0.3);
        ">
          <div style="
            width: 8px; 
            height: 8px; 
            background: white; 
            border-radius: 50%; 
            position: absolute; 
            top: 50%; 
            left: 50%; 
            transform: translate(-50%, -50%);
          "></div>
        </div>
      `;

      // Use AdvancedMarkerElement if available, fallback to regular Marker
      if (window.google.maps.marker && window.google.maps.marker.AdvancedMarkerElement) {
        const userMarker = new window.google.maps.marker.AdvancedMarkerElement({
          position: userLocation,
          map: mapInstance,
          title: 'Your Location',
          content: iconElement
        });
        userMarkerRef.current = userMarker;
      } else {
        // Fallback to regular marker
        const userMarker = new window.google.maps.Marker({
          position: userLocation,
          map: mapInstance,
          title: 'Your Location',
          icon: {
            url: 'data:image/svg+xml;charset=UTF-8,' + encodeURIComponent(`
              <svg width="32" height="32" viewBox="0 0 32 32" xmlns="http://www.w3.org/2000/svg">
                <circle cx="16" cy="16" r="12" fill="#2563eb" stroke="white" stroke-width="3"/>
                <circle cx="16" cy="16" r="4" fill="white"/>
              </svg>
            `),
            scaledSize: new window.google.maps.Size(32, 32)
          },
          zIndex: 1000
        });
        userMarkerRef.current = userMarker;
      }
    }
  }, [userLocation]);

  // Initialize map
  useEffect(() => {
    if (mapsLoaded && mapRef.current && viewMode === 'map' && !map) {
      const center = userLocation || { lat: 12.9716, lng: 77.5946 };
      
      const mapInstance = new window.google.maps.Map(mapRef.current, {
        center: center,
        zoom: 12,
        mapId: 'ev-charging-map', // Required for AdvancedMarkerElement
        styles: [
          {
            featureType: 'poi',
            elementType: 'labels',
            stylers: [{ visibility: 'off' }]
          }
        ]
      });

      const infoWindow = new window.google.maps.InfoWindow();
      infoWindowRef.current = infoWindow;
      setMap(mapInstance);

      updateUserLocationMarker(mapInstance);
    }
  }, [mapsLoaded, viewMode, userLocation, map, updateUserLocationMarker]);

  // Update user marker when location changes
  useEffect(() => {
    if (map && userLocation) {
      updateUserLocationMarker(map);
    }
  }, [map, userLocation, updateUserLocationMarker]);

  // Update markers when bunk locations change
  useEffect(() => {
    if (map && bunkLocations.length > 0) {
      clearMarkers();
      clearDirections();
      
      const newMarkers = bunkLocations.map(bunk => {
        // Create custom marker content
        const markerElement = document.createElement('div');
        markerElement.innerHTML = `
          <div style="
            width: 32px; 
            height: 32px; 
            background: ${bunk.slotsAvailable > 0 ? '#10b981' : '#ef4444'}; 
            border: 2px solid white; 
            border-radius: 50%; 
            display: flex; 
            align-items: center; 
            justify-content: center;
            box-shadow: 0 2px 6px rgba(0,0,0,0.3);
            cursor: pointer;
          ">
            <svg width="16" height="16" viewBox="0 0 16 16" fill="white">
              ${bunk.slotsAvailable > 0 
                ? '<path d="M6 8l2 2 4-4" stroke="white" stroke-width="2" fill="none"/>'
                : '<path d="M10 6L6 10M6 6l4 4" stroke="white" stroke-width="2"/>'
              }
            </svg>
          </div>
        `;

        let marker;
        
        // Use AdvancedMarkerElement if available
        if (window.google.maps.marker && window.google.maps.marker.AdvancedMarkerElement) {
          marker = new window.google.maps.marker.AdvancedMarkerElement({
            position: { lat: bunk.latitude, lng: bunk.longitude },
            map: map,
            title: bunk.name,
            content: markerElement
          });
        } else {
          // Fallback to regular Marker
          marker = new window.google.maps.Marker({
            position: { lat: bunk.latitude, lng: bunk.longitude },
            map: map,
            title: bunk.name,
            icon: {
              url: bunk.slotsAvailable > 0 
                ? 'data:image/svg+xml;charset=UTF-8,' + encodeURIComponent(`
                  <svg width="32" height="32" viewBox="0 0 32 32" xmlns="http://www.w3.org/2000/svg">
                    <circle cx="16" cy="16" r="12" fill="#10b981" stroke="white" stroke-width="2"/>
                    <path d="M12 16l4 4 8-8" stroke="white" stroke-width="2" fill="none"/>
                  </svg>
                `)
                : 'data:image/svg+xml;charset=UTF-8,' + encodeURIComponent(`
                  <svg width="32" height="32" viewBox="0 0 32 32" xmlns="http://www.w3.org/2000/svg">
                    <circle cx="16" cy="16" r="12" fill="#ef4444" stroke="white" stroke-width="2"/>
                    <path d="M20 12l-8 8M12 12l8 8" stroke="white" stroke-width="2"/>
                  </svg>
                `),
              scaledSize: new window.google.maps.Size(32, 32)
            }
          });
        }

        marker.addListener('click', () => {
          setSelectedBunk(bunk);
          
          const infoContent = `
            <div style="max-width: 300px; font-family: Arial, sans-serif;">
              <h3 style="margin: 0 0 10px 0; color: #1f2937; font-size: 16px;">${bunk.name}</h3>
              <p style="margin: 5px 0; color: #6b7280; font-size: 14px;">${bunk.address}</p>
              <p style="margin: 5px 0; color: #6b7280; font-size: 14px;">📞 ${bunk.phone}</p>
              <p style="margin: 5px 0; color: #6b7280; font-size: 14px;">⏰ ${bunk.operatingHours}</p>
              <p style="margin: 5px 0; font-weight: bold; color: ${bunk.slotsAvailable > 0 ? '#10b981' : '#ef4444'};">
                ⚡ ${bunk.slotsAvailable} slots available
              </p>
              ${bunk.connectorTypes ? `
                <div style="margin-top: 8px;">
                  <small style="color: #6b7280;">Connectors: ${bunk.connectorTypes.join(', ')}</small>
                </div>
              ` : ''}
              ${bunk.distance ? `
                <div style="margin-top: 8px;">
                  <small style="color: #6b7280;">Distance: ${bunk.distance.toFixed(1)} km away</small>
                </div>
              ` : ''}
              ${userLocation ? `
                <div style="margin-top: 10px;">
                  <button onclick="window.showDirectionsToStation('${bunk._id}')" 
                          style="background: #3b82f6; color: white; padding: 5px 10px; border: none; border-radius: 4px; cursor: pointer; font-size: 12px;">
                    Show Route
                  </button>
                </div>
              ` : ''}
            </div>
          `;
          
          if (infoWindowRef.current) {
            infoWindowRef.current.setContent(infoContent);
            infoWindowRef.current.open(map, marker);
          }
        });

        return marker;
      });

      markersRef.current = newMarkers;

      // Fit map to show all markers including user location
      if (newMarkers.length > 0) {
        const bounds = new window.google.maps.LatLngBounds();
        
        if (userLocation) {
          bounds.extend(userLocation);
        }
        
        bunkLocations.forEach(bunk => {
          bounds.extend({ lat: bunk.latitude, lng: bunk.longitude });
        });
        
        map.fitBounds(bounds);
        
        const listener = window.google.maps.event.addListener(map, 'idle', () => {
          if (map.getZoom() > 15) map.setZoom(15);
          window.google.maps.event.removeListener(listener);
        });
      }
    }
  }, [map, bunkLocations, userLocation, clearMarkers, clearDirections]);

  // Add global function for showing directions
  useEffect(() => {
    window.showDirectionsToStation = (bunkId) => {
      const bunk = bunkLocations.find(b => b._id === bunkId);
      if (bunk) {
        showDirections(bunk);
      }
    };

    return () => {
      delete window.showDirectionsToStation;
    };
  }, [bunkLocations, showDirections]);

  // Cleanup on unmount
  useEffect(() => {
    return () => {
      clearMarkers();
      clearDirections();
      if (userMarkerRef.current) {
        if (userMarkerRef.current.setMap) {
          userMarkerRef.current.setMap(null);
        } else if (userMarkerRef.current.map) {
          userMarkerRef.current.map = null;
        }
      }
      if (infoWindowRef.current) {
        infoWindowRef.current.close();
      }
    };
  }, [clearMarkers, clearDirections]);

  // Load sample data for testing
  const loadSampleData = () => {
    const sampleBunks = [
      {
        _id: '1',
        name: 'Central Mall Charging Hub',
        address: 'MG Road, Bangalore, Karnataka 560001',
        phone: '+91 9876543210',
        operatingHours: '24/7',
        latitude: 12.9716,
        longitude: 77.5946,
        slotsAvailable: 3,
        totalSlots: 5,
        connectorTypes: ['CCS', 'CHAdeMO', 'Type 2'],
        amenities: ['WiFi', 'Cafe', 'Restroom'],
        pricing: '₹10/kWh'
      },
      {
        _id: '2',
        name: 'Tech Park Express Station',
        address: 'Electronic City, Bangalore, Karnataka 560100',
        phone: '+91 9876543211',
        operatingHours: '6 AM - 10 PM',
        latitude: 12.8456,
        longitude: 77.6632,
        slotsAvailable: 0,
        totalSlots: 3,
        connectorTypes: ['Type 2', 'CCS'],
        amenities: ['Parking', 'Security'],
        pricing: '₹12/kWh'
      },
      {
        _id: '3',
        name: 'Airport Link Charging Point',
        address: 'Hebbal, Bangalore, Karnataka 560024',
        phone: '+91 9876543212',
        operatingHours: '24/7',
        latitude: 13.0359,
        longitude: 77.5971,
        slotsAvailable: 2,
        totalSlots: 4,
        connectorTypes: ['CHAdeMO', 'CCS'],
        amenities: ['Food Court', 'WiFi'],
        pricing: '₹15/kWh'
      }
    ];
    
    setBunkLocations(sampleBunks);
    setError(null);
    setLoading(false);
    console.log('🧪 Loaded sample data:', sampleBunks);
  };

  // Enhanced fetch functions with better debugging
  const fetchBunkLocations = async () => {
    try {
      setLoading(true);
      setError(null);
      setShowNearbyOnly(false);
      
      console.log('🔍 Fetching all bunk locations...');
      
      const response = await fetch('/api/bunks', {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
        },
      });

      console.log('📡 Response status:', response.status, response.statusText);

      if (!response.ok) {
        const errorText = await response.text();
        throw new Error(`Server returned ${response.status}: ${errorText}`);
      }

      const data = await response.json();
      console.log('📦 Raw API response:', data);
      
      let bunks = [];
      if (data && data.success && Array.isArray(data.data)) {
        bunks = data.data;
        console.log('✅ Using data.data format');
      } else if (data && Array.isArray(data.bunks)) {
        bunks = data.bunks;
        console.log('✅ Using data.bunks format');
      } else if (Array.isArray(data)) {
        bunks = data;
        console.log('✅ Using direct array format');
      } else {
        console.log('❌ Unexpected response format:', typeof data, data);
        throw new Error('No charging stations found in response');
      }

      console.log('🏪 Found bunks:', bunks.length);

      if (userLocation && bunks.length > 0) {
        bunks = addDistanceToBunks(bunks, userLocation.lat, userLocation.lng);
        console.log('📍 Added distances to bunks');
      }

      setBunkLocations(bunks);
      
      if (bunks.length === 0) {
        setError('No charging stations found. Try the sample data to test the interface.');
      }
      
    } catch (error) {
      console.error('❌ Error fetching bunk locations:', error);
      setError(`Failed to fetch charging stations: ${error.message}`);
      setBunkLocations([]);
    } finally {
      setLoading(false);
    }
  };

  const fetchAvailableBunks = async () => {
    try {
      setLoading(true);
      setError(null);
      setShowNearbyOnly(false);
      
      console.log('🔍 Fetching available bunks...');
      
      const response = await fetch('/api/bunks/available');
      
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();
      let bunks = data.data || [];

      if (userLocation && bunks.length > 0) {
        bunks = addDistanceToBunks(bunks, userLocation.lat, userLocation.lng);
      }

      setBunkLocations(bunks);
    } catch (error) {
      console.error('Error fetching available bunks:', error);
      setError(error.message);
    } finally {
      setLoading(false);
    }
  };

  // Fixed nearby bunks function
  const fetchNearbyBunks = async (lat, lng, radius = 15) => {
    try {
      setLoading(true);
      setError(null);
      setShowNearbyOnly(true);
      
      console.log('🔍 Fetching nearby bunks for location:', { lat, lng, radius });
      
      let userLat = lat;
      let userLng = lng;
      
      if (!userLat || !userLng) {
        console.log('📍 No coordinates provided, getting user location...');
        try {
          const location = await getCurrentLocation();
          userLat = location.lat;
          userLng = location.lng;
          console.log('📍 Got user location:', { userLat, userLng });
        } catch (locationError) {
          console.warn('⚠️ Could not get user location:', locationError);
          userLat = 12.9716;
          userLng = 77.5946;
          console.log('📍 Using default location (Bangalore)');
        }
      }

      let bunks = [];
      
      // Try the nearby API endpoint first
      try {
        console.log('🌐 Trying nearby API endpoint...');
        const nearbyUrl = `/api/bunks/nearby?latitude=${userLat}&longitude=${userLng}&radius=${radius}`;
        console.log('🔗 URL:', nearbyUrl);
        
        const response = await fetch(nearbyUrl);
        console.log('📡 Nearby API response:', response.status, response.statusText);
        
        if (response.ok) {
          const data = await response.json();
          console.log('📦 Nearby API data:', data);
          bunks = data.data || data.bunks || [];
          console.log('✅ Got bunks from nearby API:', bunks.length);
        } else {
          throw new Error(`Nearby API returned ${response.status}`);
        }
      } catch (apiError) {
        console.log('⚠️ Nearby API failed:', apiError.message);
        console.log('🔄 Falling back to local calculation...');
        
        // Fallback: fetch all bunks and calculate distance locally
        const allBunksResponse = await fetch('/api/bunks');
        console.log('📡 All bunks response:', allBunksResponse.status);
        
        if (allBunksResponse.ok) {
          const allData = await allBunksResponse.json();
          console.log('📦 All bunks data:', allData);
          
          const allBunks = allData.success ? (allData.data || []) : (Array.isArray(allData) ? allData : []);
          console.log('🏪 Total bunks for filtering:', allBunks.length);
          
          // Calculate distances and filter by radius
          bunks = allBunks
            .map(bunk => ({
              ...bunk,
              distance: calculateDistance(userLat, userLng, bunk.latitude, bunk.longitude)
            }))
            .filter(bunk => bunk.distance <= radius)
            .sort((a, b) => a.distance - b.distance);
            
          console.log('📍 Filtered nearby bunks:', bunks.length, 'within', radius, 'km');
        } else {
          throw new Error('Failed to fetch all bunks for nearby calculation');
        }
      }

      // Ensure all bunks have distance calculated
      if (bunks.length > 0) {
        bunks = bunks.map(bunk => ({
          ...bunk,
          distance: bunk.distance || calculateDistance(userLat, userLng, bunk.latitude, bunk.longitude)
        })).sort((a, b) => (a.distance || 0) - (b.distance || 0));
      }

      console.log('✅ Final nearby bunks:', bunks.length);
      setBunkLocations(bunks);
      
      // Update user location state
      setUserLocation({ lat: userLat, lng: userLng });
      
      // Switch to map view to show results
      setViewMode('map');
      
      if (bunks.length === 0) {
        setError(`No charging stations found within ${radius}km of your location. Try expanding the search radius.`);
      }
      
    } catch (error) {
      console.error('❌ Error fetching nearby bunks:', error);
      setError(`Failed to find nearby stations: ${error.message}`);
      setBunkLocations([]);
    } finally {
      setLoading(false);
    }
  };

  // Handle nearby button click
  const handleNearbyClick = async () => {
    console.log('🎯 Nearby button clicked');
    
    try {
      setLoading(true);
      setError('Getting your location...');
      
      // Get user location first
      const location = await getCurrentLocation();
      console.log('📍 Location for nearby search:', location);
      
      // Clear the location message
      setError(null);
      
      // Search for nearby bunks
      await fetchNearbyBunks(location.lat, location.lng, 15);
      
    } catch (error) {
      console.error('❌ Nearby search error:', error);
      
      // Use fallback location and inform user
      const fallbackLocation = { lat: 12.9716, lng: 77.5946 };
      setUserLocation(fallbackLocation);
      
      try {
        await fetchNearbyBunks(fallbackLocation.lat, fallbackLocation.lng, 15);
        setError('Could not get your exact location. Showing nearby stations from Bangalore center.');
      } catch (fallbackError) {
        console.error('❌ Fallback also failed:', fallbackError);
        setError('Failed to fetch nearby stations. Try the sample data or check your internet connection.');
      }
    }
  };

  const handleViewModeChange = (mode) => {
    if (mode === 'list' && map) {
      setMap(null);
      clearMarkers();
      clearDirections();
      if (userMarkerRef.current) {
        if (userMarkerRef.current.setMap) {
          userMarkerRef.current.setMap(null);
        } else if (userMarkerRef.current.map) {
          userMarkerRef.current.map = null;
        }
        userMarkerRef.current = null;
      }
      if (infoWindowRef.current) {
        infoWindowRef.current.close();
      }
    }
    setViewMode(mode);
  };

  // Initial load
  useEffect(() => {
    fetchBunkLocations();
  }, []);

  // Recalculate distances when user location changes
  useEffect(() => {
    if (userLocation && bunkLocations.length > 0 && !showNearbyOnly) {
      const bunksWithDistance = addDistanceToBunks(bunkLocations, userLocation.lat, userLocation.lng);
      setBunkLocations(bunksWithDistance);
    }
  }, [userLocation]);

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto mb-4"></div>
          <p className="text-gray-600">
            {showNearbyOnly ? 'Finding nearby charging stations...' : 'Loading EV charging locations...'}
          </p>
        </div>
      </div>
    );
  }

  if (error && bunkLocations.length === 0) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="text-center p-6 bg-red-50 rounded-lg max-w-md">
          <div className="text-red-600 mb-4">
            <Zap className="h-12 w-12 mx-auto mb-2" />
            <h3 className="font-semibold text-lg">Connection Error</h3>
          </div>
          <p className="text-red-700 mb-4">{error}</p>
          <button 
            onClick={fetchBunkLocations}
            className="bg-red-600 text-white px-4 py-2 rounded hover:bg-red-700 transition-colors"
          >
            Try Again
          </button>
        </div>
      </div>
    );
  }

  return (
    <div>
      <AdminNavbar/>
      <div className="container mx-auto p-6">
        <div className="mb-6">
          <h1 className="text-3xl font-bold text-gray-800 mb-4">EV Charging Stations</h1>
          
          {/* Location Permission Status */}
          {locationPermission !== 'unknown' && (
            <div className={`mb-4 p-3 rounded-lg text-sm ${
              locationPermission === 'granted' 
                ? 'bg-green-50 text-green-700 border border-green-200' 
                : 'bg-yellow-50 text-yellow-700 border border-yellow-200'
            }`}>
              {locationPermission === 'granted' ? (
                <div className="flex items-center gap-2">
                  <Navigation className="h-4 w-4" />
                  Location access granted - showing personalized results with distances
                  {userLocation && (
                    <span className="text-xs bg-green-100 px-2 py-1 rounded">
                      {userLocation.lat.toFixed(4)}, {userLocation.lng.toFixed(4)}
                    </span>
                  )}
                </div>
              ) : (
                <div className="flex items-center gap-2">
                  <MapPin className="h-4 w-4" />
                  Location access denied - using default location (Bangalore)
                </div>
              )}
            </div>
          )}

          {/* Error message for non-fatal errors */}
          {error && bunkLocations.length > 0 && (
            <div className="mb-4 p-3 rounded-lg text-sm bg-yellow-50 text-yellow-700 border border-yellow-200">
              <div className="flex items-center gap-2">
                <MapPin className="h-4 w-4" />
                {error}
              </div>
            </div>
          )}
          
          {/* View Mode Toggle */}
          <div className="flex gap-2 mb-4">
            <button
              onClick={() => handleViewModeChange('list')}
              className={`flex items-center gap-2 px-4 py-2 rounded transition-colors ${
                viewMode === 'list' 
                  ? 'bg-blue-600 text-white' 
                  : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
              }`}
            >
              <List className="h-4 w-4" />
              List View
            </button>
            <button
              onClick={() => handleViewModeChange('map')}
              className={`flex items-center gap-2 px-4 py-2 rounded transition-colors ${
                viewMode === 'map' 
                  ? 'bg-blue-600 text-white' 
                  : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
              }`}
              disabled={!mapsLoaded}
            >
              <Map className="h-4 w-4" />
              {mapsLoaded ? 'Map View' : 'Loading Maps...'}
            </button>
          </div>
          
          {/* Filter Buttons */}
          <div className="flex flex-wrap gap-2 mb-4">
            <button 
              onClick={fetchBunkLocations}
              className={`px-4 py-2 rounded transition-colors flex items-center gap-2 ${
                !showNearbyOnly 
                  ? 'bg-blue-600 text-white' 
                  : 'bg-gray-200 text-gray-700 hover:bg-blue-600 hover:text-white'
              }`}
              disabled={loading}
            >
              <Zap className="h-4 w-4" />
              All Stations
            </button>
            <button 
              onClick={fetchAvailableBunks}
              className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700 transition-colors flex items-center gap-2"
              disabled={loading}
            >
              <Zap className="h-4 w-4" />
              Available Only
            </button>
            <button 
              onClick={handleNearbyClick}
              className={`px-4 py-2 rounded transition-colors flex items-center gap-2 ${
                showNearbyOnly 
                  ? 'bg-purple-600 text-white' 
                  : 'bg-purple-600 text-white hover:bg-purple-700'
              }`}
              disabled={loading}
            >
              <Navigation className="h-4 w-4" />
              {loading ? 'Finding...' : 'Nearby Stations'}
            </button>
          </div>

          <p className="text-gray-600">
            Found {bunkLocations.length} charging station{bunkLocations.length !== 1 ? 's' : ''}
            {showNearbyOnly && (
              <span className="text-purple-600 font-medium"> (within 15km of your location)</span>
            )}
            {userLocation && bunkLocations.some(b => b.distance) && (
              <span className="text-sm text-gray-500 ml-2">
                (sorted by distance from your location)
              </span>
            )}
          </p>
        </div>

        {/* Map View */}
        {viewMode === 'map' && (
          <div className="mb-6">
            <div 
              ref={mapRef}
              className="w-full h-96 rounded-lg border border-gray-300"
              style={{ minHeight: '500px' }}
            >
              {!mapsLoaded && (
                <div className="flex items-center justify-center h-full bg-gray-100 rounded-lg">
                  <p className="text-gray-600">Loading Google Maps...</p>
                </div>
              )}
            </div>
            <div className="mt-2 text-sm text-gray-600 flex items-center gap-4 flex-wrap">
              <div className="flex items-center gap-1">
                <div className="w-4 h-4 bg-green-500 rounded-full"></div>
                <span>Available</span>
              </div>
              <div className="flex items-center gap-1">
                <div className="w-4 h-4 bg-red-500 rounded-full"></div>
                <span>Full</span>
              </div>
              {userLocation && (
                <div className="flex items-center gap-1">
                  <div className="w-4 h-4 bg-blue-500 rounded-full"></div>
                  <span>Your Location</span>
                </div>
              )}
              <div className="text-xs text-gray-500">
                Click on markers for details and route options
              </div>
            </div>
          </div>
        )}

        {/* List View */}
        {viewMode === 'list' && (
          <div className="grid gap-6 md:grid-cols-2 lg:grid-cols-3">
            {bunkLocations.map((bunk) => (
              <div
                key={bunk._id}
                className={`bg-white rounded-lg shadow-md border hover:shadow-lg transition-shadow duration-200 ${
                  selectedBunk?._id === bunk._id ? 'ring-2 ring-blue-500' : ''
                }`}
                onClick={() => setSelectedBunk(bunk)}
              >
                <div className="p-6">
                  <div className="flex items-start justify-between mb-3">
                    <h3 className="text-lg font-semibold text-gray-800 leading-tight">
                      {bunk.name}
                    </h3>
                    <div className="flex items-center gap-2">
                      {bunk.distance && (
                        <span className="text-xs bg-blue-100 text-blue-700 px-2 py-1 rounded-full">
                          {bunk.distance.toFixed(1)} km
                        </span>
                      )}
                      <button
                        onClick={(e) => {
                          e.stopPropagation();
                          handleEditBunk(bunk._id);
                        }}
                        className="p-1 text-gray-400 hover:text-blue-600 transition-colors"
                        title="Edit Station"
                      >
                        <Edit3 className="h-4 w-4" />
                      </button>
                    </div>
                  </div>

                  <div className="space-y-2 mb-4">
                    <div className="flex items-start gap-2 text-sm text-gray-600">
                      <MapPin className="h-4 w-4 mt-0.5 text-gray-400 flex-shrink-0" />
                      <span className="break-words">{bunk.address}</span>
                    </div>
                    
                    <div className="flex items-center gap-2 text-sm text-gray-600">
                      <Phone className="h-4 w-4 text-gray-400 flex-shrink-0" />
                      <span>{bunk.phone}</span>
                    </div>
                    
                    <div className="flex items-center gap-2 text-sm text-gray-600">
                      <Clock className="h-4 w-4 text-gray-400 flex-shrink-0" />
                      <span>{bunk.operatingHours}</span>
                    </div>
                  </div>

                  <div className="flex items-center justify-between mb-4">
                    <div className={`flex items-center gap-2 px-3 py-1 rounded-full text-sm font-medium ${
                      bunk.slotsAvailable > 0 
                        ? 'bg-green-100 text-green-700' 
                        : 'bg-red-100 text-red-700'
                    }`}>
                      <Zap className="h-4 w-4" />
                      <span>
                        {bunk.slotsAvailable} slot{bunk.slotsAvailable !== 1 ? 's' : ''} available
                      </span>
                    </div>
                    
                    {bunk.totalSlots && (
                      <span className="text-xs text-gray-500">
                        of {bunk.totalSlots} total
                      </span>
                    )}
                  </div>

                  {bunk.connectorTypes && bunk.connectorTypes.length > 0 && (
                    <div className="mb-4">
                      <div className="flex flex-wrap gap-1">
                        {bunk.connectorTypes.map((connector, index) => (
                          <span
                            key={index}
                            className="text-xs bg-gray-100 text-gray-600 px-2 py-1 rounded"
                          >
                            {connector}
                          </span>
                        ))}
                      </div>
                    </div>
                  )}

                  {bunk.amenities && bunk.amenities.length > 0 && (
                    <div className="mb-4">
                      <p className="text-xs text-gray-500 mb-1">Amenities:</p>
                      <div className="flex flex-wrap gap-1">
                        {bunk.amenities.map((amenity, index) => (
                          <span
                            key={index}
                            className="text-xs bg-blue-50 text-blue-600 px-2 py-1 rounded"
                          >
                            {amenity}
                          </span>
                        ))}
                      </div>
                    </div>
                  )}

                  {userLocation && (
                    <div className="flex gap-2 pt-3 border-t border-gray-100">
                      <button
                        onClick={(e) => {
                          e.stopPropagation();
                          setViewMode('map');
                          setTimeout(() => {
                            if (map) {
                              map.setCenter({ lat: bunk.latitude, lng: bunk.longitude });
                              map.setZoom(15);
                            }
                          }, 100);
                        }}
                        className="flex-1 bg-blue-600 text-white px-3 py-2 rounded text-sm hover:bg-blue-700 transition-colors flex items-center justify-center gap-1"
                      >
                        <Map className="h-3 w-3" />
                        View on Map
                      </button>
                      
                      <button
                        onClick={(e) => {
                          e.stopPropagation();
                          showDirections(bunk);
                          setViewMode('map');
                        }}
                        className="flex-1 bg-green-600 text-white px-3 py-2 rounded text-sm hover:bg-green-700 transition-colors flex items-center justify-center gap-1"
                      >
                        <Navigation className="h-3 w-3" />
                        Get Directions
                      </button>
                    </div>
                  )}

                  {!userLocation && (
                    <div className="pt-3 border-t border-gray-100">
                      <button
                        onClick={(e) => {
                          e.stopPropagation();
                          setViewMode('map');
                          setTimeout(() => {
                            if (map) {
                              map.setCenter({ lat: bunk.latitude, lng: bunk.longitude });
                              map.setZoom(15);
                            }
                          }, 100);
                        }}
                        className="w-full bg-blue-600 text-white px-3 py-2 rounded text-sm hover:bg-blue-700 transition-colors flex items-center justify-center gap-1"
                      >
                        <Map className="h-3 w-3" />
                        View on Map
                      </button>
                    </div>
                  )}

                  {bunk.pricing && (
                    <div className="mt-3 pt-3 border-t border-gray-100">
                      <p className="text-xs text-gray-500 mb-1">Pricing:</p>
                      <p className="text-sm text-gray-700">{bunk.pricing}</p>
                    </div>
                  )}

                  {bunk.lastUpdated && (
                    <div className="mt-2">
                      <p className="text-xs text-gray-400">
                        Last updated: {new Date(bunk.lastUpdated).toLocaleString()}
                      </p>
                    </div>
                  )}
                </div>
              </div>
            ))}
          </div>
        )}

        {/* No Results */}
        {!loading && bunkLocations.length === 0 && (
          <div className="text-center py-12">
            <Zap className="h-16 w-16 text-gray-300 mx-auto mb-4" />
            <h3 className="text-lg font-medium text-gray-600 mb-2">
              No charging stations found
            </h3>
            <p className="text-gray-500 mb-6">
              {showNearbyOnly 
                ? 'Try expanding your search radius or check your location settings.'
                : 'There are no EV charging stations available at the moment.'}
            </p>
            <div className="flex justify-center gap-2">
              <button
                onClick={fetchBunkLocations}
                className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 transition-colors"
              >
                Refresh
              </button>
              {showNearbyOnly && (
                <button
                  onClick={() => {
                    setShowNearbyOnly(false);
                    fetchBunkLocations();
                  }}
                  className="bg-gray-600 text-white px-4 py-2 rounded hover:bg-gray-700 transition-colors"
                >
                  Show All Stations
                </button>
              )}
            </div>
          </div>
        )}

        {/* Selected Bunk Details Modal */}
        {selectedBunk && viewMode === 'list' && (
          <div 
            className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4"
            onClick={() => setSelectedBunk(null)}
          >
            <div 
              className="bg-white rounded-lg max-w-md w-full max-h-96 overflow-y-auto"
              onClick={(e) => e.stopPropagation()}
            >
              <div className="p-6">
                <div className="flex items-start justify-between mb-4">
                  <h2 className="text-xl font-semibold text-gray-800">
                    {selectedBunk.name}
                  </h2>
                  <button
                    onClick={() => setSelectedBunk(null)}
                    className="text-gray-400 hover:text-gray-600 text-xl"
                  >
                    ×
                  </button>
                </div>

                <div className="space-y-3">
                  <div className="flex items-start gap-3">
                    <MapPin className="h-5 w-5 text-gray-400 mt-0.5 flex-shrink-0" />
                    <span className="text-gray-700">{selectedBunk.address}</span>
                  </div>
                  
                  <div className="flex items-center gap-3">
                    <Phone className="h-5 w-5 text-gray-400 flex-shrink-0" />
                    <span className="text-gray-700">{selectedBunk.phone}</span>
                  </div>
                  
                  <div className="flex items-center gap-3">
                    <Clock className="h-5 w-5 text-gray-400 flex-shrink-0" />
                    <span className="text-gray-700">{selectedBunk.operatingHours}</span>
                  </div>

                  <div className={`flex items-center gap-3 p-3 rounded-lg ${
                    selectedBunk.slotsAvailable > 0 
                      ? 'bg-green-50 text-green-700' 
                      : 'bg-red-50 text-red-700'
                  }`}>
                    <Zap className="h-5 w-5 flex-shrink-0" />
                    <span className="font-medium">
                      {selectedBunk.slotsAvailable} slots available
                      {selectedBunk.totalSlots && ` of ${selectedBunk.totalSlots}`}
                    </span>
                  </div>

                  {selectedBunk.distance && (
                    <div className="bg-blue-50 text-blue-700 p-3 rounded-lg">
                      <span className="font-medium">
                        Distance: {selectedBunk.distance.toFixed(1)} km away
                      </span>
                    </div>
                  )}

                  {selectedBunk.connectorTypes && selectedBunk.connectorTypes.length > 0 && (
                    <div>
                      <p className="font-medium text-gray-700 mb-2">Available Connectors:</p>
                      <div className="flex flex-wrap gap-2">
                        {selectedBunk.connectorTypes.map((connector, index) => (
                          <span
                            key={index}
                            className="bg-gray-100 text-gray-700 px-3 py-1 rounded-full text-sm"
                          >
                            {connector}
                          </span>
                        ))}
                      </div>
                    </div>
                  )}

                  {selectedBunk.amenities && selectedBunk.amenities.length > 0 && (
                    <div>
                      <p className="font-medium text-gray-700 mb-2">Amenities:</p>
                      <div className="flex flex-wrap gap-2">
                        {selectedBunk.amenities.map((amenity, index) => (
                          <span
                            key={index}
                            className="bg-blue-100 text-blue-700 px-3 py-1 rounded-full text-sm"
                          >
                            {amenity}
                          </span>
                        ))}
                      </div>
                    </div>
                  )}

                  {selectedBunk.pricing && (
                    <div>
                      <p className="font-medium text-gray-700 mb-1">Pricing:</p>
                      <p className="text-gray-600">{selectedBunk.pricing}</p>
                    </div>
                  )}
                </div>

                <div className="flex gap-3 mt-6 pt-4 border-t border-gray-200">
                  <button
                    onClick={() => {
                      setSelectedBunk(null);
                      setViewMode('map');
                      setTimeout(() => {
                        if (map) {
                          map.setCenter({ lat: selectedBunk.latitude, lng: selectedBunk.longitude });
                          map.setZoom(15);
                        }
                      }, 100);
                    }}
                    className="flex-1 bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 transition-colors flex items-center justify-center gap-2"
                  >
                    <Map className="h-4 w-4" />
                    View on Map
                  </button>
                  
                  {userLocation && (
                    <button
                      onClick={() => {
                        showDirections(selectedBunk);
                        setSelectedBunk(null);
                        setViewMode('map');
                      }}
                      className="flex-1 bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700 transition-colors flex items-center justify-center gap-2"
                    >
                      <Navigation className="h-4 w-4" />
                      Get Directions
                    </button>
                  )}
                </div>
              </div>
            </div>
          </div>
        )}

        {/* Quick Stats Summary */}
        {bunkLocations.length > 0 && (
          <div className="mt-8 bg-gray-50 rounded-lg p-6">
            <h3 className="text-lg font-semibold text-gray-800 mb-4">Quick Stats</h3>
            <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
              <div className="text-center">
                <div className="text-2xl font-bold text-blue-600">
                  {bunkLocations.length}
                </div>
                <div className="text-sm text-gray-600">Total Stations</div>
              </div>
              
              <div className="text-center">
                <div className="text-2xl font-bold text-green-600">
                  {bunkLocations.filter(b => b.slotsAvailable > 0).length}
                </div>
                <div className="text-sm text-gray-600">Available Now</div>
              </div>
              
              <div className="text-center">
                <div className="text-2xl font-bold text-purple-600">
                  {bunkLocations.reduce((sum, b) => sum + (b.slotsAvailable || 0), 0)}
                </div>
                <div className="text-sm text-gray-600">Free Slots</div>
              </div>
              
              <div className="text-center">
                <div className="text-2xl font-bold text-orange-600">
                  {bunkLocations.reduce((sum, b) => sum + (b.totalSlots || b.slotsAvailable || 0), 0)}
                </div>
                <div className="text-sm text-gray-600">Total Slots</div>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default EvBunkMapView;
