// components/Profile/GoogleMapPicker.jsx
import { useEffect, useRef, useState } from 'react';
import { useLoadGoogleMaps } from '../../components/common/';

const GOOGLE_MAPS_API_KEY = 'AIzaSyDozw7FDv161gMDT9lE-U0cSGZuWjYhyvw';

const GoogleMapPicker = ({ defaultLocation, onLocationSelect }) => {
  const mapRef = useRef(null);
  const markerRef = useRef(null);
  const [map, setMap] = useState(null);
  const loaded = useLoadGoogleMaps(GOOGLE_MAPS_API_KEY);

  useEffect(() => {
    if (!loaded || map) return;

    const center = defaultLocation || { lat: 13.3499, lng: 74.7421 }; // Brahmavar

    const newMap = new window.google.maps.Map(mapRef.current, {
      center,
      zoom: 14,
    });

    const marker = new window.google.maps.Marker({
      position: center,
      map: newMap,
      draggable: true,
    });

    marker.addListener('dragend', (e) => {
      const latLng = {
        lat: e.latLng.lat(),
        lng: e.latLng.lng(),
      };
      onLocationSelect(latLng);
    });

    newMap.addListener('click', (e) => {
      const latLng = {
        lat: e.latLng.lat(),
        lng: e.latLng.lng(),
      };
      marker.setPosition(latLng);
      onLocationSelect(latLng);
    });

    markerRef.current = marker;
    setMap(newMap);
  }, [loaded]);

  return (
    <div>
      <div ref={mapRef} style={{ height: '350px', borderRadius: '12px' }} />
    </div>
  );
};

export default GoogleMapPicker;
