import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { Menu, X } from "lucide-react";

const UserNavbar = () => {
  const [isOpen, setIsOpen] = useState(false);
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem("userToken");
    navigate("/user/login");
  };

  const toggleMenu = () => {
    setIsOpen(!isOpen);
  };

  return (
    <nav className="bg-blue-600 text-white shadow-md sticky top-0 z-50">
      <div className="max-w-7xl mx-auto px-4 py-4 flex items-center justify-between">
        {/* Logo / Title */}
        <div className="text-2xl font-semibold tracking-wide">
          <Link to="/user/dashboard">User Panel</Link>
        </div>

        {/* Mobile Menu Button */}
        <div className="md:hidden">
          <button onClick={toggleMenu}>
            {isOpen ? <X className="w-6 h-6" /> : <Menu className="w-6 h-6" />}
          </button>
        </div>

        {/* Desktop Menu */}
        <div className="hidden md:flex space-x-8 items-center text-lg font-medium">
          <Link to="/user/dashboard" className="hover:text-yellow-300">Dashboard</Link>
          <Link to="/user/book-slot" className="hover:text-yellow-300">Book Slot</Link>
          <Link to="/user/view-my-bookings" className="hover:text-yellow-300">View Bookings</Link>
          <Link to="/user/view-bunk-locations" className="hover:text-yellow-300">View Bunk Locations</Link>
          <Link to="/user/profile" className="hover:text-yellow-300">My Profile</Link>
          <button onClick={handleLogout} className="hover:text-red-300">Logout</button>
        </div>
      </div>

      {/* Mobile Menu */}
      {isOpen && (
        <div className="md:hidden px-4 pb-4 bg-blue-600 text-white space-y-3 text-base font-medium">
          <Link to="/user/dashboard" onClick={toggleMenu} className="block hover:text-yellow-300">Dashboard</Link>
          <Link to="/user/book-slot" onClick={toggleMenu} className="block hover:text-yellow-300">Book Slot</Link>
          <Link to="/user/view-my-bookings" onClick={toggleMenu} className="block hover:text-yellow-300">View Bookings</Link>
          <Link to="/user/view-bunk-locations" onClick={toggleMenu} className="block hover:text-yellow-300">View Bunk Locations</Link>
          <Link to="/user/profile" onClick={toggleMenu} className="block hover:text-yellow-300">My Profile</Link>
          <button
            onClick={() => {
              toggleMenu();
              handleLogout();
            }}
            className="block text-left hover:text-red-300"
          >
            Logout
          </button>
        </div>
      )}
    </nav>
  );
};

export default UserNavbar;
