// src/components/BookingManagement.jsx - React component for booking management
import React, { useState, useEffect, useCallback } from 'react';
import { fetchBookings } from '../services/bookingService';

const BookingManagement = () => {
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [page, setPage] = useState(1);
  const [limit] = useState(10);
  const [filters, setFilters] = useState({
    status: '',
    startDate: '',
    endDate: ''
  });
  const [searchTerm, setSearchTerm] = useState('');

  // Function to load bookings
  const loadBookings = useCallback(async () => {
    try {
      setLoading(true);
      setError(null);
      
      // Apply filters
      const filterParams = {};
      if (filters.status) filterParams.status = filters.status;
      if (filters.startDate) filterParams.startDate = filters.startDate;
      if (filters.endDate) filterParams.endDate = filters.endDate;
      if (searchTerm) filterParams.search = searchTerm;
      
      const data = await fetchBookings(page, limit, filterParams);
      setBookings(data.bookings || []);
    } catch (err) {
      console.error('Error fetching bookings:', err);
      setError(err.message || 'Failed to fetch bookings');
    } finally {
      setLoading(false);
    }
  }, [page, limit, filters, searchTerm]);

  // Load bookings on component mount and when dependencies change
  useEffect(() => {
    loadBookings();
  }, [loadBookings]);

  // Handle filter changes
  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters(prev => ({
      ...prev,
      [name]: value
    }));
  };

  // Handle search
  const handleSearch = (e) => {
    e.preventDefault();
    // Reset to first page when searching
    setPage(1);
    loadBookings();
  };

  // Handle pagination
  const handlePageChange = (newPage) => {
    setPage(newPage);
  };

  // Manual refresh button handler
  const handleRefresh = () => {
    loadBookings();
  };

  return (
    <div className="booking-management">
      <h1>Booking Management</h1>
      
      {/* Filters */}
      <div className="filters">
        <h2>Filters</h2>
        <div className="filter-row">
          <div className="filter-group">
            <label>Status</label>
            <select 
              name="status" 
              value={filters.status} 
              onChange={handleFilterChange}
            >
              <option value="">All Statuses</option>
              <option value="pending">Pending</option>
              <option value="confirmed">Confirmed</option>
              <option value="cancelled">Cancelled</option>
              <option value="completed">Completed</option>
            </select>
          </div>
          
          <div className="filter-group">
            <label>Start Date</label>
            <input 
              type="date" 
              name="startDate" 
              value={filters.startDate} 
              onChange={handleFilterChange}
            />
          </div>
          
          <div className="filter-group">
            <label>End Date</label>
            <input 
              type="date" 
              name="endDate" 
              value={filters.endDate} 
              onChange={handleFilterChange}
            />
          </div>
          
          <div className="filter-group">
            <label>Search</label>
            <div className="search-container">
              <input 
                type="text" 
                placeholder="Search by user, booking ID..." 
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
              />
              <button onClick={handleSearch}>Search</button>
            </div>
          </div>
        </div>
      </div>
      
      {/* Refresh button */}
      <div className="actions">
        <button onClick={handleRefresh} disabled={loading}>
          {loading ? 'Loading...' : 'Refresh'}
        </button>
      </div>
      
      {/* Error message */}
      {error && (
        <div className="error-message">
          {error}
        </div>
      )}
      
      {/* Bookings table */}
      {loading ? (
        <div className="loading">Loading bookings...</div>
      ) : bookings.length > 0 ? (
        <div className="booking-table">
          <table>
            <thead>
              <tr>
                <th>Booking ID</th>
                <th>User</th>
                <th>Date</th>
                <th>Status</th>
                <th>Amount</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {bookings.map(booking => (
                <tr key={booking._id}>
                  <td>{booking._id}</td>
                  <td>{booking.user?.name || 'Unknown'}</td>
                  <td>{new Date(booking.date).toLocaleDateString()}</td>
                  <td>
                    <span className={`status ${booking.status}`}>
                      {booking.status}
                    </span>
                  </td>
                  <td>${booking.amount?.toFixed(2) || '0.00'}</td>
                  <td>
                    <button>View</button>
                    <button>Edit</button>
                    <button>Delete</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
          
          {/* Pagination */}
          <div className="pagination">
            <button 
              onClick={() => handlePageChange(page - 1)}
              disabled={page === 1}
            >
              Previous
            </button>
            <span>Page {page}</span>
            <button 
              onClick={() => handlePageChange(page + 1)}
              disabled={bookings.length < limit}
            >
              Next
            </button>
          </div>
        </div>
      ) : (
        <div className="no-bookings">
          No bookings found. Try adjusting your filters.
        </div>
      )}
    </div>
  );
};

export default BookingManagement;
