import React, { useState, useEffect } from "react";
import axios from "axios";
import { Clock, MapPin, Calendar, Zap, CheckCircle, AlertCircle, Battery, Car, Star, Users, Wifi, CreditCard } from "lucide-react";

const BookingForm = () => {
  const [bunks, setBunks] = useState([]);
  const [selectedBunk, setSelectedBunk] = useState("");
  const [selectedDate, setSelectedDate] = useState("");
  const [availableSlots, setAvailableSlots] = useState([]);
  const [selectedSlot, setSelectedSlot] = useState("");
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState({ text: "", type: "" });
  const [selectedBunkDetails, setSelectedBunkDetails] = useState(null);
  const [step, setStep] = useState(1);

  // Fetch all bunks on component mount
  useEffect(() => {
    const fetchBunks = async () => {
      try {
        const res = await axios.get("/api/bunks");
        setBunks(res.data);
      } catch (err) {
        console.error("Error fetching bunks:", err);
        setMessage({ text: "Failed to load EV bunks", type: "error" });
      }
    };
    
    fetchBunks();
  }, []);

  // Update selected bunk details when bunk changes
  useEffect(() => {
    if (selectedBunk) {
      const bunkDetails = bunks.find(bunk => bunk._id === selectedBunk);
      setSelectedBunkDetails(bunkDetails);
      setStep(2);
    } else {
      setSelectedBunkDetails(null);
      setStep(1);
    }
  }, [selectedBunk, bunks]);

  // Update step when date is selected
  useEffect(() => {
    if (selectedDate && selectedBunk) {
      setStep(3);
    }
  }, [selectedDate, selectedBunk]);

  // Fetch available slots when bunk or date changes
  useEffect(() => {
    const fetchAvailableSlots = async () => {
      if (!selectedBunk || !selectedDate) return;
      
      setLoading(true);
      try {
        const res = await axios.get(`/api/bookings/available-slots/${selectedBunk}/${selectedDate}`);
        setAvailableSlots(res.data.data.availableSlots || []);
        setSelectedSlot("");
      } catch (err) {
        console.error("Error fetching available slots:", err);
        setMessage({ text: "Failed to load available slots", type: "error" });
      } finally {
        setLoading(false);
      }
    };
    
    fetchAvailableSlots();
  }, [selectedBunk, selectedDate]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!selectedBunk || !selectedSlot) {
      setMessage({ text: "Please select a bunk and time slot", type: "error" });
      return;
    }
    
    setLoading(true);
    try {
      const checkRes = await axios.post("/api/bookings/check-availability", {
        bunkId: selectedBunk,
        slotTime: selectedSlot
      });
      
      if (!checkRes.data.data.available) {
        setMessage({ text: "Sorry, this slot was just booked. Please select another.", type: "error" });
        return;
      }
      
      await axios.post("/api/bookings/create", {
        bunkId: selectedBunk,
        slotTime: selectedSlot
      }, {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` }
      });
      
      setMessage({ text: "Slot booked successfully!", type: "success" });
      setSelectedSlot("");
      
      const res = await axios.get(`/api/bookings/available-slots/${selectedBunk}/${selectedDate}`);
      setAvailableSlots(res.data.data.availableSlots || []);
      
    } catch (err) {
      console.error("Booking error:", err);
      
      if (err.response?.status === 401) {
        setMessage({ text: "Please log in to book a slot", type: "error" });
      } else {
        setMessage({ text: err.response?.data?.message || "Booking failed", type: "error" });
      }
    } finally {
      setLoading(false);
    }
  };

  const formatTimeSlot = (slotData) => {
    if (!slotData) return "";

    if (typeof slotData === 'string' && slotData.includes('T')) {
      const date = new Date(slotData);
      const startHour = date.getHours();
      const startMinute = date.getMinutes();
      const endHour = startHour + 1;
      
      return `${formatTime(startHour, startMinute)} - ${formatTime(endHour, startMinute)}`;
    }
    
    if (typeof slotData === 'object' && slotData.startTime && slotData.endTime) {
      return `${formatTimeFromString(slotData.startTime)} - ${formatTimeFromString(slotData.endTime)}`;
    }
    
    if (typeof slotData === 'string' && slotData.includes(':')) {
      const [hours, minutes] = slotData.split(':');
      const startHour = parseInt(hours, 10);
      const startMinute = parseInt(minutes, 10);
      const endHour = startHour + 1;
      
      return `${formatTime(startHour, startMinute)} - ${formatTime(endHour, startMinute)}`;
    }
    
    if (typeof slotData === 'number') {
      return `${formatTime(slotData, 0)} - ${formatTime(slotData + 1, 0)}`;
    }
    
    return slotData.toString();
  };

  const formatTime = (hours, minutes = 0) => {
    const ampm = hours >= 12 ? 'PM' : 'AM';
    const displayHour = hours % 12 || 12;
    const displayMinute = minutes.toString().padStart(2, '0');
    return `${displayHour}:${displayMinute} ${ampm}`;
  };

  const formatTimeFromString = (timeString) => {
    if (!timeString) return "";
    
    if (timeString.includes('T')) {
      const date = new Date(timeString);
      return formatTime(date.getHours(), date.getMinutes());
    }
    
    const [hours, minutes] = timeString.split(':');
    return formatTime(parseInt(hours, 10), parseInt(minutes, 10));
  };

  const getTodayDate = () => {
    return new Date().toISOString().split('T')[0];
  };

  const getMaxDate = () => {
    const maxDate = new Date();
    maxDate.setDate(maxDate.getDate() + 30);
    return maxDate.toISOString().split('T')[0];
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-900 via-purple-900 to-slate-900 py-8 px-4 relative overflow-hidden">
      {/* Animated Background Elements */}
      <div className="absolute inset-0 overflow-hidden">
        <div className="absolute -top-40 -right-40 w-80 h-80 bg-purple-500 rounded-full mix-blend-multiply filter blur-xl opacity-70 animate-pulse"></div>
        <div className="absolute -bottom-40 -left-40 w-80 h-80 bg-blue-500 rounded-full mix-blend-multiply filter blur-xl opacity-70 animate-pulse delay-1000"></div>
        <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 w-80 h-80 bg-indigo-500 rounded-full mix-blend-multiply filter blur-xl opacity-50 animate-pulse delay-500"></div>
      </div>

      <div className="max-w-4xl mx-auto relative">
        {/* Header */}
        <div className="text-center mb-12">
          <div className="w-20 h-20 bg-gradient-to-br from-blue-400 via-purple-500 to-pink-500 rounded-3xl flex items-center justify-center mx-auto mb-6 shadow-2xl transform hover:scale-110 transition-all duration-300">
            <Zap className="h-10 w-10 text-white animate-pulse" />
          </div>
          <h1 className="text-5xl font-bold bg-gradient-to-r from-white via-blue-100 to-purple-100 bg-clip-text text-transparent mb-4 animate-fade-in">
            EV Charging Hub
          </h1>
          <p className="text-xl text-gray-300">Reserve your premium charging experience</p>
          
          {/* Progress Steps */}
          <div className="flex justify-center mt-8 space-x-4">
            {[1, 2, 3].map((stepNum) => (
              <div key={stepNum} className="flex items-center">
                <div className={`w-10 h-10 rounded-full flex items-center justify-center font-semibold transition-all duration-300 ${
                  step >= stepNum 
                    ? 'bg-gradient-to-r from-green-400 to-blue-500 text-white shadow-lg' 
                    : 'bg-gray-600 text-gray-300'
                }`}>
                  {stepNum}
                </div>
                {stepNum < 3 && (
                  <div className={`w-16 h-1 mx-2 transition-all duration-300 ${
                    step > stepNum ? 'bg-gradient-to-r from-green-400 to-blue-500' : 'bg-gray-600'
                  }`}></div>
                )}
              </div>
            ))}
          </div>
        </div>

        {/* Main Form Card */}
        <div className="bg-white/10 backdrop-blur-lg rounded-3xl shadow-2xl border border-white/20 overflow-hidden">
          <div className="p-8">
            <form onSubmit={handleSubmit} className="space-y-8">
              {/* Step 1: Bunk Selection */}
              <div className="space-y-6">
                <label className="flex items-center text-lg font-semibold text-white mb-4">
                  <div className="w-8 h-8 bg-gradient-to-r from-blue-500 to-purple-500 rounded-lg flex items-center justify-center mr-3">
                    <MapPin className="h-4 w-4 text-white" />
                  </div>
                  Choose Your Charging Station
                </label>
                
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  {bunks.map(bunk => (
                    <div
                      key={bunk._id}
                      onClick={() => setSelectedBunk(bunk._id)}
                      className={`p-6 rounded-2xl border-2 cursor-pointer transition-all duration-300 transform hover:scale-105 ${
                        selectedBunk === bunk._id
                          ? 'bg-gradient-to-br from-green-400/20 to-blue-500/20 border-green-400 shadow-2xl'
                          : 'bg-white/5 border-white/20 hover:bg-white/10 hover:border-white/40'
                      }`}
                    >
                      <div className="flex items-start justify-between mb-3">
                        <div className="flex items-center">
                          <div className="w-12 h-12 bg-gradient-to-br from-blue-500 to-purple-600 rounded-xl flex items-center justify-center mr-3">
                            <Car className="h-6 w-6 text-white" />
                          </div>
                          <div>
                            <h3 className="font-bold text-white text-lg">{bunk.name}</h3>
                            <p className="text-gray-300 text-sm">{bunk.address || bunk.location}</p>
                          </div>
                        </div>
                        <div className="flex items-center bg-green-500/20 px-3 py-1 rounded-full">
                          <span className="text-green-400 text-sm font-medium">
                            {bunk.slotsAvailable || 8} slots
                          </span>
                        </div>
                      </div>
                      
                      <div className="flex items-center justify-between text-sm text-gray-300">
                        <div className="flex items-center">
                          <Clock className="h-4 w-4 mr-1" />
                          <span>{bunk.operatingHours || '9:00 AM - 9:00 PM'}</span>
                        </div>
                        <div className="flex items-center space-x-2">
                          <Battery className="h-4 w-4 text-green-400" />
                          <Wifi className="h-4 w-4 text-blue-400" />
                          <CreditCard className="h-4 w-4 text-purple-400" />
                        </div>
                      </div>
                      
                      <div className="flex items-center mt-3">
                        <div className="flex text-yellow-400">
                          {[...Array(5)].map((_, i) => (
                            <Star key={i} className="h-4 w-4 fill-current" />
                          ))}
                        </div>
                        <span className="text-gray-300 text-sm ml-2">(4.8)</span>
                      </div>
                    </div>
                  ))}
                </div>
              </div>

              {/* Step 2: Date Selection */}
              {selectedBunkDetails && (
                <div className="space-y-6 animate-fade-in">
                  <label className="flex items-center text-lg font-semibold text-white mb-4">
                    <div className="w-8 h-8 bg-gradient-to-r from-purple-500 to-pink-500 rounded-lg flex items-center justify-center mr-3">
                      <Calendar className="h-4 w-4 text-white" />
                    </div>
                    Select Your Date
                  </label>
                  
                  <input
                    type="date"
                    value={selectedDate}
                    onChange={(e) => setSelectedDate(e.target.value)}
                    className="w-full p-4 bg-white/10 border border-white/20 rounded-2xl focus:ring-2 focus:ring-purple-500 focus:border-transparent transition-all duration-200 text-white placeholder-gray-400 backdrop-blur-sm"
                    min={getTodayDate()}
                    max={getMaxDate()}
                    required
                  />
                </div>
              )}
              
              {/* Step 3: Time Slot Selection */}
              {selectedBunk && selectedDate && (
                <div className="space-y-6 animate-fade-in">
                  <label className="flex items-center text-lg font-semibold text-white mb-4">
                    <div className="w-8 h-8 bg-gradient-to-r from-pink-500 to-red-500 rounded-lg flex items-center justify-center mr-3">
                      <Clock className="h-4 w-4 text-white" />
                    </div>
                    Choose Your Time Slot
                  </label>
                  
                  {loading ? (
                    <div className="text-center py-16">
                      <div className="relative">
                        <div className="animate-spin rounded-full h-16 w-16 border-t-4 border-purple-500 mx-auto mb-4"></div>
                        <div className="absolute inset-0 rounded-full bg-purple-500/20 animate-pulse"></div>
                      </div>
                      <p className="text-white text-lg">Finding perfect slots for you...</p>
                    </div>
                  ) : availableSlots.length > 0 ? (
                    <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4">
                      {availableSlots.map((slot, index) => (
                        <button
                          key={`${slot}-${index}`}
                          type="button"
                          onClick={() => setSelectedSlot(slot)}
                          className={`group p-6 text-sm font-medium rounded-2xl border-2 transition-all duration-300 transform hover:scale-105 ${
                            selectedSlot === slot
                              ? 'bg-gradient-to-br from-green-400 to-emerald-500 text-white border-green-400 shadow-2xl'
                              : 'bg-white/5 text-white border-white/20 hover:bg-white/10 hover:border-white/40 hover:shadow-xl'
                          }`}
                        >
                          <div className="flex flex-col items-center space-y-2">
                            <div className={`w-12 h-12 rounded-xl flex items-center justify-center ${
                              selectedSlot === slot 
                                ? 'bg-white/20' 
                                : 'bg-gradient-to-br from-blue-500 to-purple-600 group-hover:from-purple-500 group-hover:to-pink-500'
                            }`}>
                              <Clock className="h-6 w-6" />
                            </div>
                            <div className="text-center">
                              <div className="font-bold text-lg">{formatTimeSlot(slot)}</div>
                              <div className="text-xs opacity-75">Available Now</div>
                            </div>
                          </div>
                        </button>
                      ))}
                    </div>
                  ) : (
                    <div className="text-center py-16 bg-white/5 rounded-2xl border border-white/20">
                      <AlertCircle className="h-16 w-16 text-gray-400 mx-auto mb-4" />
                      <h3 className="text-xl font-bold text-white mb-2">No Available Slots</h3>
                      <p className="text-gray-300">Try selecting a different date for available slots.</p>
                    </div>
                  )}
                </div>
              )}
              
              {/* Submit Button */}
              {selectedSlot && (
                <div className="animate-fade-in">
                  <button
                    type="submit"
                    disabled={loading || !selectedSlot}
                    className={`w-full py-6 rounded-2xl text-white font-bold text-lg transition-all duration-300 transform hover:scale-105 ${
                      loading || !selectedSlot
                        ? 'bg-gray-600 cursor-not-allowed'
                        : 'bg-gradient-to-r from-green-500 via-blue-500 to-purple-500 hover:from-green-600 hover:via-blue-600 hover:to-purple-600 shadow-2xl hover:shadow-green-500/25'
                    }`}
                  >
                    {loading ? (
                      <div className="flex items-center justify-center">
                        <div className="animate-spin rounded-full h-6 w-6 border-b-2 border-white mr-3"></div>
                        Securing Your Slot...
                      </div>
                    ) : (
                      <div className="flex items-center justify-center">
                        <CheckCircle className="h-6 w-6 mr-3" />
                        Book My Charging Slot
                      </div>
                    )}
                  </button>
                </div>
              )}
            </form>
          </div>
          
          {/* Status Message */}
          {message.text && (
            <div className={`px-8 py-6 border-t border-white/20 ${
              message.type === 'success' 
                ? 'bg-green-500/10' 
                : 'bg-red-500/10'
            }`}>
              <div className={`flex items-center ${
                message.type === 'success' ? 'text-green-400' : 'text-red-400'
              }`}>
                {message.type === 'success' ? (
                  <CheckCircle className="h-6 w-6 mr-3" />
                ) : (
                  <AlertCircle className="h-6 w-6 mr-3" />
                )}
                <span className="font-semibold text-lg">{message.text}</span>
              </div>
            </div>
          )}
        </div>

        {/* Enhanced Info Cards */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mt-8">
          <div className="bg-white/10 backdrop-blur-lg rounded-2xl p-6 border border-white/20">
            <div className="w-12 h-12 bg-gradient-to-br from-blue-500 to-purple-600 rounded-xl flex items-center justify-center mb-4">
              <Battery className="h-6 w-6 text-white" />
            </div>
            <h3 className="font-bold text-white mb-2">Fast Charging</h3>
            <p className="text-gray-300 text-sm">Up to 150kW charging speed for quick top-ups</p>
          </div>
          
          <div className="bg-white/10 backdrop-blur-lg rounded-2xl p-6 border border-white/20">
            <div className="w-12 h-12 bg-gradient-to-br from-green-500 to-emerald-600 rounded-xl flex items-center justify-center mb-4">
              <Users className="h-6 w-6 text-white" />
            </div>
            <h3 className="font-bold text-white mb-2">24/7 Support</h3>
            <p className="text-gray-300 text-sm">Round-the-clock assistance for all your needs</p>
          </div>
          
          <div className="bg-white/10 backdrop-blur-lg rounded-2xl p-6 border border-white/20">
            <div className="w-12 h-12 bg-gradient-to-br from-purple-500 to-pink-600 rounded-xl flex items-center justify-center mb-4">
              <Wifi className="h-6 w-6 text-white" />
            </div>
            <h3 className="font-bold text-white mb-2">Premium Amenities</h3>
            <p className="text-gray-300 text-sm">Free WiFi, refreshments, and comfortable waiting areas</p>
          </div>
        </div>
      </div>

      <style jsx>{`
        @keyframes fade-in {
          from { opacity: 0; transform: translateY(20px); }
          to { opacity: 1; transform: translateY(0); }
        }
        .animate-fade-in {
          animation: fade-in 0.6s ease-out;
        }
      `}</style>
    </div>
  );
};

export default BookingForm;
