import React, { useState, useEffect } from "react";
import axios from "axios";

const BookingForm = () => {
  const [bunks, setBunks] = useState([]); // Ensure it's an array
  const [selectedBunk, setSelectedBunk] = useState("");
  const [slotTime, setSlotTime] = useState("");
  const [message, setMessage] = useState("");
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const fetchBunks = async () => {
      try {
        const res = await axios.get("/api/bunks"); // Adjust if your route differs
        // If response is an array: res.data
        // If response is { bunks: [...] }: res.data.bunks
        setBunks(Array.isArray(res.data) ? res.data : res.data.bunks || []);
      } catch (error) {
        console.error("Failed to load bunks:", error);
        setBunks([]);
        setMessage("Error loading EV bunk list.");
      }
    };

    fetchBunks();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage("");

    try {
      const token = localStorage.getItem("token"); // Ensure token exists
      const res = await axios.post(
        "/api/bookings/create", // Ensure your route matches the backend
        {
          bunkId: selectedBunk,
          slotTime,
          userId: localStorage.getItem("userId"), // Only if required
        },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );

      setMessage("Slot booked successfully!");
      setSelectedBunk("");
      setSlotTime("");
    } catch (err) {
      console.error("Booking failed:", err);
      setMessage(err?.response?.data?.message || "Booking failed.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="max-w-md mx-auto p-6 bg-white rounded-lg shadow">
      <h2 className="text-2xl font-semibold mb-4">Book Recharge Slot</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <select
          value={selectedBunk}
          onChange={(e) => setSelectedBunk(e.target.value)}
          className="w-full p-2 border rounded"
          required
        >
          <option value="">Select EV Bunk</option>
          {Array.isArray(bunks) &&
            bunks.map((bunk) => (
              <option key={bunk._id} value={bunk._id}>
                {bunk.name} - {bunk.location}
              </option>
            ))}
        </select>

        <input
          type="datetime-local"
          value={slotTime}
          onChange={(e) => setSlotTime(e.target.value)}
          className="w-full p-2 border rounded"
          required
        />

        <button
          type="submit"
          className="w-full bg-green-600 text-white py-2 rounded hover:bg-green-700"
          disabled={loading}
        >
          {loading ? "Booking..." : "Book Slot"}
        </button>

        {message && (
          <p className="text-center mt-2 text-sm text-red-600">{message}</p>
        )}
      </form>
    </div>
  );
};

export default BookingForm;
