import React, { useState } from 'react';

const AddEvBunk = () => {
  const [bunkName, setBunkName] = useState('');
  const [location, setLocation] = useState('');
  const [slots, setSlots] = useState('');
  const [contact, setContact] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission logic here
    console.log({
      bunkName,
      location,
      slots,
      contact
    });
  };

  return (
    <div className="min-h-screen bg-gray-100 flex justify-center items-center py-8">
      <div className="w-full max-w-2xl bg-white p-6 rounded-lg shadow-lg">
        <h1 className="text-3xl font-semibold text-center text-gray-800 mb-6">Add New EV Bunk</h1>
        <form onSubmit={handleSubmit}>
          <div className="space-y-6">
            {/* Bunk Name */}
            <div>
              <label htmlFor="bunkName" className="block text-gray-600 font-medium">Bunk Name</label>
              <input
                id="bunkName"
                type="text"
                value={bunkName}
                onChange={(e) => setBunkName(e.target.value)}
                className="w-full p-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                placeholder="Enter bunk name"
                required
              />
            </div>

            {/* Location */}
            <div>
              <label htmlFor="location" className="block text-gray-600 font-medium">Location</label>
              <input
                id="location"
                type="text"
                value={location}
                onChange={(e) => setLocation(e.target.value)}
                className="w-full p-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                placeholder="Enter bunk location"
                required
              />
            </div>

            {/* Number of Slots */}
            <div>
              <label htmlFor="slots" className="block text-gray-600 font-medium">Number of Slots</label>
              <input
                id="slots"
                type="number"
                value={slots}
                onChange={(e) => setSlots(e.target.value)}
                className="w-full p-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                placeholder="Enter number of slots"
                required
              />
            </div>

            {/* Contact Number */}
            <div>
              <label htmlFor="contact" className="block text-gray-600 font-medium">Contact Number</label>
              <input
                id="contact"
                type="tel"
                value={contact}
                onChange={(e) => setContact(e.target.value)}
                className="w-full p-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                placeholder="Enter contact number"
                required
              />
            </div>

            {/* Submit Button */}
            <div className="mt-6">
              <button
                type="submit"
                className="w-full p-3 bg-blue-500 text-white font-semibold rounded-md hover:bg-blue-600 transition duration-200"
              >
                Add EV Bunk
              </button>
            </div>
          </div>
        </form>
      </div>
    </div>
  );
};

export default AddEvBunk;
