import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { adminAuth } from "../../services/api";

const AdminLogin = ({ setIsAuthenticated }) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // Clear previous errors
    setError("");
    setLoading(true);
    
    try {
      // Call login API
      const response = await adminAuth.login({ email, password });
      
      // Update authentication state
      setIsAuthenticated(true);
      
      // Show success message
      alert("Login successful!");
      
      // Navigate to dashboard
      navigate("/admin/dashboard");
    } catch (err) {
      setError(err.message || "Login failed. Please check your credentials.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container mx-auto mt-16 p-4">
      <h2 className="text-2xl font-semibold text-center mb-6">Admin Login</h2>
      
      {error && (
        <div className="bg-red-500 text-white p-2 mb-4 text-center">
          {error}
        </div>
      )}
      
      <form onSubmit={handleSubmit} className="max-w-md mx-auto bg-white p-6 shadow-lg rounded-md">
        <div className="mb-4">
          <label htmlFor="email" className="block text-sm font-semibold">Email</label>
          <input
            type="email"
            id="email"
            className="w-full p-2 border border-gray-300 rounded-md"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        
        <div className="mb-4">
          <label htmlFor="password" className="block text-sm font-semibold">Password</label>
          <input
            type="password"
            id="password"
            className="w-full p-2 border border-gray-300 rounded-md"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        
        <div className="flex justify-between items-center mb-4">
          <div className="flex items-center">
            <input type="checkbox" id="rememberMe" className="mr-2" />
            <label htmlFor="rememberMe" className="text-sm">Remember Me</label>
          </div>
          <Link to="/admin/forgot-password" className="text-sm text-blue-500">
            Forgot Password?
          </Link>
        </div>
        
        <button 
          type="submit" 
          className="w-full bg-blue-500 text-white p-2 rounded-md"
          disabled={loading}
        >
          {loading ? "Logging in..." : "Login"}
        </button>
        
        <div className="mt-4 text-center">
          <p className="text-sm">
            Don't have an account?{" "}
            <Link to="/admin/register" className="text-blue-500">
              Register here
            </Link>
          </p>
        </div>
      </form>
    </div>
  );
};

export default AdminLogin;
