// components/user/MyBookings.js
import React from 'react';
import { format } from 'date-fns'; // Assuming you're using date-fns

const UserBookings = ({ bookings }) => {
  // Helper function to format dates consistently
  const formatDateTime = (dateString) => {
    const date = new Date(dateString);
    return format(date, 'MMM d, yyyy h:mm a');
  };

  // Helper function to get appropriate status badge color
  const getStatusBadgeClasses = (status) => {
    switch (status) {
      case 'active':
        return 'bg-green-100 text-green-800';
      case 'completed':
        return 'bg-blue-100 text-blue-800';
      case 'cancelled':
        return 'bg-red-100 text-red-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  // If there are no bookings
  if (bookings.length === 0) {
    return (
      <div className="text-center py-8 border border-dashed border-gray-300 rounded-lg">
        <p className="text-gray-500">You have no bookings yet.</p>
        <p className="mt-2">
          <a href="/user/bookings/new" className="text-blue-600 hover:underline">
            Book your first charging slot
          </a>
        </p>
      </div>
    );
  }

  return (
    <div className="overflow-x-auto">
      <table className="min-w-full bg-white">
        <thead>
          <tr className="bg-gray-50 text-gray-600 uppercase text-sm leading-normal">
            <th className="py-3 px-6 text-left">Station</th>
            <th className="py-3 px-6 text-left">Date & Time</th>
            <th className="py-3 px-6 text-left">Status</th>
            <th className="py-3 px-6 text-left">Actions</th>
          </tr>
        </thead>
        <tbody className="text-gray-600 text-sm">
          {bookings.map((booking) => (
            <tr key={booking._id} className="border-b border-gray-200 hover:bg-gray-50">
              <td className="py-4 px-6">
                {booking.bunkId?.name || 'Unknown Station'}
              </td>
              <td className="py-4 px-6">
                <div>
                  <span className="font-medium">Start:</span> {formatDateTime(booking.startTime)}
                </div>
                <div className="text-xs text-gray-500">
                  <span className="font-medium">End:</span> {formatDateTime(booking.endTime)}
                </div>
              </td>
              <td className="py-4 px-6">
                <span className={`px-2 py-1 rounded-full text-xs ${getStatusBadgeClasses(booking.status)}`}>
                  {booking.status.charAt(0).toUpperCase() + booking.status.slice(1)}
                </span>
              </td>
              <td className="py-4 px-6">
                {booking.status === 'active' && (
                  <div className="space-x-2">
                    <button 
                      className="px-3 py-1 bg-blue-600 text-white text-xs rounded hover:bg-blue-700"
                      onClick={() => window.location.href = `/user/bookings/reschedule/${booking._id}`}
                    >
                      Reschedule
                    </button>
                    <button 
                      className="px-3 py-1 bg-red-600 text-white text-xs rounded hover:bg-red-700"
                      onClick={() => window.location.href = `/user/bookings/cancel/${booking._id}`}
                    >
                      Cancel
                    </button>
                  </div>
                )}
                {booking.status !== 'active' && (
                  <span className="text-gray-400">No actions available</span>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default UserBookings;
