            </div>
            <button
              onClick={handleRefresh}
              className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-lg"
            >
              Try Again
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <AdminNavbar />
      
      <div className="container mx-auto px-4 py-6">
        {/* Header */}
        <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center mb-6 gap-4">
          <div>
            <h1 className="text-2xl font-bold text-gray-900 mb-2">EV Charging Stations</h1>
            <p className="text-gray-600">
              {bunkLocations.length} stations found
              {locationPermission === 'granted' && userLocation && (
                <span className="ml-2 text-green-600">üìç Location enabled</span>
              )}
              {locationPermission === 'denied' && (
                <span className="ml-2 text-orange-600">üìç Location access denied</span>
              )}
            </p>
          </div>
          
          <div className="flex gap-2">
            {/* View Mode Toggle */}
            <div className="flex bg-white rounded-lg border overflow-hidden">
              <button
                onClick={() => handleViewModeChange('list')}
                className={`px-4 py-2 flex items-center gap-2 ${
                  viewMode === 'list'
                    ? 'bg-blue-600 text-white'
                    : 'text-gray-600 hover:bg-gray-50'
                }`}
              >
                <List size={18} />
                List
              </button>
              <button
                onClick={() => handleViewModeChange('map')}
                className={`px-4 py-2 flex items-center gap-2 ${
                  viewMode === 'map'
                    ? 'bg-blue-600 text-white'
                    : 'text-gray-600 hover:bg-gray-50'
                } ${!mapsLoaded ? 'opacity-50 cursor-not-allowed' : ''}`}
                disabled={!mapsLoaded}
              >
                <Map size={18} />
                Map
                {!mapsLoaded && <span className="text-xs">(Loading...)</span>}
              </button>
            </div>
            
            {/* Refresh Button */}
            <button
              onClick={handleRefresh}
              className="bg-white border border-gray-300 text-gray-700 px-4 py-2 rounded-lg hover:bg-gray-50 flex items-center gap-2"
            >
              <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 4v5h.582m15.356 2a8.001 8.001 0 01-15.356 2m15.356-2H20v5m-4-5a8.001 8.001 0 01-15.356-2m0 0h4.582M20 20v-5h-.582" />
              </svg>
              Refresh
            </button>
          </div>
        </div>

        {/* Location Permission Banner */}
        {locationPermission === 'denied' && (
          <div className="bg-orange-100 border-l-4 border-orange-500 text-orange-700 p-4 mb-6">
            <div className="flex">
              <div className="flex-shrink-0">
                <MapPin className="h-5 w-5" />
              </div>
              <div className="ml-3">
                <p className="text-sm">
                  <strong>Location access denied.</strong> Enable location access to see distances and get directions to charging stations.
                </p>
              </div>
            </div>
          </div>
        )}

        {/* Content */}
        {viewMode === 'list' ? (
          /* List View */
          <div className="space-y-4">
            {bunkLocations.length === 0 ? (
              <div className="text-center py-12">
                <MapPin className="mx-auto h-12 w-12 text-gray-400 mb-4" />
                <h3 className="text-lg font-medium text-gray-900 mb-2">No charging stations found</h3>
                <p className="text-gray-600">There are no EV charging stations available at the moment.</p>
              </div>
            ) : (
              bunkLocations.map((bunk) => (
                <div key={bunk._id} className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
                  <div className="flex flex-col sm:flex-row justify-between items-start gap-4">
                    <div className="flex-1">
                      <div className="flex items-start justify-between mb-3">
                        <div>
                          <h3 className="text-lg font-semibold text-gray-900 mb-1">{bunk.name}</h3>
                          <p className="text-gray-600 flex items-center gap-1 mb-2">
                            <MapPin size={16} />
                            {bunk.address}
                          </p>
                        </div>
                        <div className="flex items-center gap-2">
                          {bunk.slotsAvailable > 0 ? (
                            <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-green-100 text-green-800">
                              <Zap size={12} className="mr-1" />
                              {bunk.slotsAvailable} Available
                            </span>
                          ) : (
                            <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-red-100 text-red-800">
                              <Zap size={12} className="mr-1" />
                              Full
                            </span>
                          )}
                        </div>
                      </div>

                      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4 text-sm text-gray-600">
                        <div className="flex items-center gap-2">
                          <Phone size={16} />
                          <span>{bunk.phone || 'No phone available'}</span>
                        </div>
                        <div className="flex items-center gap-2">
                          <Clock size={16} />
                          <span>{bunk.operatingHours || '24/7'}</span>
                        </div>
                        {bunk.distance && (
                          <div className="flex items-center gap-2">
                            <Navigation size={16} />
                            <span>{bunk.distance.toFixed(1)} km away</span>
                          </div>
                        )}
                      </div>

                      {bunk.connectorTypes && bunk.connectorTypes.length > 0 && (
                        <div className="mt-3">
                          <p className="text-sm text-gray-600 mb-1">Available Connectors:</p>
                          <div className="flex flex-wrap gap-1">
                            {bunk.connectorTypes.map((connector, index) => (
                              <span
                                key={index}
                                className="inline-flex items-center px-2 py-1 rounded text-xs font-medium bg-blue-100 text-blue-800"
                              >
                                {connector}
                              </span>
                            ))}
                          </div>
                        </div>
                      )}
                    </div>

                    <div className="flex flex-col sm:flex-row gap-2">
                      <button
                        onClick={() => handleEditBunk(bunk._id)}
                        className="inline-flex items-center justify-center px-3 py-2 border border-gray-300 shadow-sm text-sm font-medium rounded-md text-gray-700 bg-white hover:bg-gray-50"
                      >
                        <Edit3 size={16} className="mr-1" />
                        Edit
                      </button>
                      
                      {userLocation && (
                        <button
                          onClick={() => handleShowDirections(bunk)}
                          className="inline-flex items-center justify-center px-3 py-2 border border-transparent text-sm font-medium rounded-md text-white bg-blue-600 hover:bg-blue-700"
                          disabled={!mapsLoaded}
                        >
                          <Navigation size={16} className="mr-1" />
                          Directions
                        </button>
                      )}
                    </div>
                  </div>
                </div>
              ))
            )}
          </div>
        ) : (
          /* Map View */
          <div className="bg-white rounded-lg shadow-sm border border-gray-200 overflow-hidden">
            <div 
              ref={mapRef} 
              className="w-full h-96 sm:h-[500px] lg:h-[600px]"
              style={{ minHeight: '400px' }}
            >
              {!mapsLoaded && (
                <div className="flex items-center justify-center h-full bg-gray-100">
                  <div className="text-center">
                    <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600 mx-auto mb-2"></div>
                    <p className="text-gray-600 text-sm">Loading Google Maps...</p>
                  </div>
                </div>
              )}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default EvBunkMapView;
