import React, { useState, useEffect } from "react";
import axios from "axios"; // Make sure axios is installed
import { Clock, MapPin, Calendar, Zap, CheckCircle, AlertCircle, RefreshCw } from "lucide-react";

const RescheduleBookingForm = ({ booking, onRescheduleSuccess, onCancel }) => {
  const [selectedDate, setSelectedDate] = useState("");
  const [availableSlots, setAvailableSlots] = useState([]);
  const [selectedSlot, setSelectedSlot] = useState("");
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState({ text: "", type: "" });

  // Set default date to booking date when component mounts
  useEffect(() => {
    if (booking && booking.startTime) {
      const bookingDate = new Date(booking.startTime);
      setSelectedDate(bookingDate.toISOString().split('T')[0]);
    } else {
      const mockDate = new Date();
      setSelectedDate(mockDate.toISOString().split('T')[0]);
    }
  }, [booking]);

  // Fetch available slots when date changes
  useEffect(() => {
    const fetchAvailableSlots = async () => {
      if (!selectedDate || !booking?.bunkId?._id) return;
      
      setLoading(true);
      try {
        // Replace with your actual API endpoint
        const res = await axios.get(`/api/bookings/available-slots/${booking.bunkId._id}/${selectedDate}`, {
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('token')}`, // Adjust based on your auth method
            'Content-Type': 'application/json'
          }
        });
        
        setAvailableSlots(res.data.availableSlots || []);
        setSelectedSlot(""); // Reset selected slot when date changes
        setMessage({ text: "", type: "" }); // Clear any previous messages
      } catch (err) {
        console.error("Error fetching available slots:", err);
        setMessage({ 
          text: err.response?.data?.message || "Failed to load available slots", 
          type: "error" 
        });
        setAvailableSlots([]);
      } finally {
        setLoading(false);
      }
    };
    
    fetchAvailableSlots();
  }, [selectedDate, booking]);

  const handleSubmit = async () => {
    if (!selectedSlot) {
      setMessage({ text: "Please select a new time slot", type: "error" });
      return;
    }

    if (!booking?._id) {
      setMessage({ text: "Booking information is missing", type: "error" });
      return;
    }
    
    setLoading(true);
    setMessage({ text: "", type: "" });

    try {
      // Step 1: Check availability before rescheduling
      const checkRes = await axios.post("/api/bookings/check-availability", {
        bunkId: booking.bunkId._id,
        date: selectedDate,
        slotTime: selectedSlot
      }, {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`,
          'Content-Type': 'application/json'
        }
      });

      if (!checkRes.data.available) {
        setMessage({ 
          text: "Sorry, this slot was just booked. Please select another.", 
          type: "error" 
        });
        // Refresh available slots
        const refreshRes = await axios.get(`/api/bookings/available-slots/${booking.bunkId._id}/${selectedDate}`);
        setAvailableSlots(refreshRes.data.availableSlots || []);
        setSelectedSlot("");
        return;
      }

      // Step 2: Create new start and end times
      const startDateTime = new Date(`${selectedDate}T${selectedSlot}:00`);
      const endDateTime = new Date(startDateTime);
      endDateTime.setHours(startDateTime.getHours() + 1); // Add 1 hour for end time

      // Step 3: Make the reschedule API call
      const rescheduleRes = await axios.put(`/api/bookings/reschedule/${booking._id}`, {
        newStartTime: startDateTime.toISOString(),
        newEndTime: endDateTime.toISOString(),
        bunkId: booking.bunkId._id,
        date: selectedDate,
        slotTime: selectedSlot
      }, {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`,
          'Content-Type': 'application/json'
        }
      });

      if (rescheduleRes.data.success) {
        setMessage({ text: "Booking rescheduled successfully!", type: "success" });
        
        // Notify parent component of successful reschedule
        if (onRescheduleSuccess && typeof onRescheduleSuccess === 'function') {
          onRescheduleSuccess({
            ...booking,
            startTime: startDateTime.toISOString(),
            endTime: endDateTime.toISOString(),
            ...rescheduleRes.data.booking // Include any additional data from backend
          });
        }

        // Auto-close after 2 seconds
        setTimeout(() => {
          if (onCancel) onCancel();
        }, 2000);
      } else {
        setMessage({ 
          text: rescheduleRes.data.message || "Reschedule failed", 
          type: "error" 
        });
      }
      
    } catch (err) {
      console.error("Reschedule error:", err);
      
      if (err.response?.status === 401) {
        setMessage({ text: "Please log in to reschedule", type: "error" });
      } else if (err.response?.status === 409) {
        setMessage({ text: "This slot is no longer available", type: "error" });
        // Refresh available slots
        try {
          const refreshRes = await axios.get(`/api/bookings/available-slots/${booking.bunkId._id}/${selectedDate}`);
          setAvailableSlots(refreshRes.data.availableSlots || []);
          setSelectedSlot("");
        } catch (refreshErr) {
          console.error("Error refreshing slots:", refreshErr);
        }
      } else {
        setMessage({ 
          text: err.response?.data?.message || "Reschedule failed. Please try again.", 
          type: "error" 
        });
      }
    } finally {
      setLoading(false);
    }
  };

  // Enhanced time formatting function that handles different slot formats
  const formatTimeSlot = (slotData) => {
    if (!slotData) return "";

    // If slotData is a string that looks like an ISO date
    if (typeof slotData === 'string' && slotData.includes('T')) {
      const date = new Date(slotData);
      const startHour = date.getHours();
      const startMinute = date.getMinutes();
      const endHour = startHour + 1; // Assuming 1-hour slots
      
      return `${formatTime(startHour, startMinute)} - ${formatTime(endHour, startMinute)}`;
    }
    
    // If slotData is an object with start and end times
    if (typeof slotData === 'object' && slotData.startTime && slotData.endTime) {
      return `${formatTimeFromString(slotData.startTime)} - ${formatTimeFromString(slotData.endTime)}`;
    }
    
    // If slotData is just a time string like "14:30"
    if (typeof slotData === 'string' && slotData.includes(':')) {
      const [hours, minutes] = slotData.split(':');
      const startHour = parseInt(hours, 10);
      const startMinute = parseInt(minutes, 10);
      const endHour = startHour + 1;
      
      return `${formatTime(startHour, startMinute)} - ${formatTime(endHour, startMinute)}`;
    }
    
    // If it's a number (hour)
    if (typeof slotData === 'number') {
      return `${formatTime(slotData, 0)} - ${formatTime(slotData + 1, 0)}`;
    }
    
    return slotData.toString();
  };

  // Helper function to format time
  const formatTime = (hours, minutes = 0) => {
    const ampm = hours >= 12 ? 'PM' : 'AM';
    const displayHour = hours % 12 || 12;
    const displayMinute = minutes.toString().padStart(2, '0');
    return `${displayHour}:${displayMinute} ${ampm}`;
  };

  // Helper function to format time from string
  const formatTimeFromString = (timeString) => {
    if (!timeString) return "";
    
    // Handle full ISO string
    if (timeString.includes('T')) {
      const date = new Date(timeString);
      return formatTime(date.getHours(), date.getMinutes());
    }
    
    // Handle time string like "14:30"
    const [hours, minutes] = timeString.split(':');
    return formatTime(parseInt(hours, 10), parseInt(minutes, 10));
  };

  // Format the current booking time for display
  const getCurrentBookingTime = () => {
    if (!booking || !booking.startTime) return "";
    
    const startTime = new Date(booking.startTime);
    const endTime = new Date(booking.endTime);
    
    const startFormatted = formatTime(startTime.getHours(), startTime.getMinutes());
    const endFormatted = formatTime(endTime.getHours(), endTime.getMinutes());
    
    // If both times have the same AM/PM, show it only once
    const startAMPM = startTime.getHours() >= 12 ? 'PM' : 'AM';
    const endAMPM = endTime.getHours() >= 12 ? 'PM' : 'AM';
    
    if (startAMPM === endAMPM) {
      const startWithoutAMPM = startFormatted.replace(` ${startAMPM}`, '');
      return `${startWithoutAMPM} - ${endFormatted}`;
    } else {
      return `${startFormatted} - ${endFormatted}`;
    }
  };

  // Get today's date in YYYY-MM-DD format
  const getTodayDate = () => {
    return new Date().toISOString().split('T')[0];
  };

  // Get max date (30 days from now)
  const getMaxDate = () => {
    const maxDate = new Date();
    maxDate.setDate(maxDate.getDate() + 30);
    return maxDate.toISOString().split('T')[0];
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 via-indigo-50 to-purple-50 py-8 px-4">
      <div className="max-w-2xl mx-auto">
        {/* Header */}
        <div className="text-center mb-8">
          <div className="w-16 h-16 bg-gradient-to-br from-blue-500 to-indigo-600 rounded-2xl flex items-center justify-center mx-auto mb-4">
            <Zap className="h-8 w-8 text-white" />
          </div>
          <h1 className="text-3xl font-bold bg-gradient-to-r from-gray-900 to-gray-700 bg-clip-text text-transparent mb-2">
            Reschedule Your EV Charging Slot
          </h1>
          <p className="text-gray-600">Choose a new time slot for your charging session</p>
        </div>

        {/* Main Form Card */}
        <div className="bg-white rounded-2xl shadow-xl border border-gray-100 overflow-hidden">
          <div className="p-8">
            <div className="space-y-6">
              {/* Current Booking Station */}
              <div>
                <label className="flex items-center text-sm font-semibold text-gray-700 mb-3">
                  <MapPin className="h-4 w-4 mr-2 text-blue-500" />
                  Current Charging Station
                </label>
                <div className="w-full p-4 border border-gray-200 rounded-xl bg-gray-50">
                  <div className="flex items-center justify-between">
                    <div>
                      <p className="font-medium text-gray-900">{booking?.bunkId?.name || 'Central Charging Station'}</p>
                      <p className="text-sm text-gray-600">{booking?.bunkId?.location || 'Downtown Plaza, Main Street'}</p>
                    </div>
                    <div className="text-right">
                      <p className="font-medium text-blue-600">{getCurrentBookingTime()}</p>
                      <p className="text-sm text-gray-600">
                        {booking?.startTime ? new Date(booking.startTime).toLocaleDateString() : new Date().toLocaleDateString()}
                      </p>
                    </div>
                  </div>
                </div>
                
                {/* Station Details */}
                <div className="mt-3 p-4 bg-blue-50 rounded-lg border border-blue-100">
                  <div className="flex items-center justify-between text-sm">
                    <div className="flex items-center text-gray-600">
                      <Clock className="h-4 w-4 mr-2" />
                      <span>Operating Hours: {booking?.bunkId?.operatingHours || '9:00 AM - 9:00 PM'}</span>
                    </div>
                    <div className="flex items-center text-green-600 font-medium">
                      <span>{booking?.bunkId?.slotsAvailable || 0} slots available</span>
                    </div>
                  </div>
                </div>
              </div>
              
              {/* Date Selection */}
              <div>
                <label htmlFor="date" className="flex items-center text-sm font-semibold text-gray-700 mb-3">
                  <Calendar className="h-4 w-4 mr-2 text-blue-500" />
                  Select New Date
                </label>
                <input
                  id="date"
                  type="date"
                  value={selectedDate}
                  onChange={(e) => setSelectedDate(e.target.value)}
                  className="w-full p-4 border border-gray-200 rounded-xl focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all duration-200 bg-gray-50 hover:bg-white"
                  min={getTodayDate()}
                  max={getMaxDate()}
                  required
                />
              </div>
              
              {/* Time Slot Selection */}
              {selectedDate && (
                <div>
                  <label className="flex items-center text-sm font-semibold text-gray-700 mb-3">
                    <Clock className="h-4 w-4 mr-2 text-blue-500" />
                    Select New Time Slot
                  </label>
                  
                  {loading ? (
                    <div className="text-center py-12">
                      <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600 mx-auto mb-4"></div>
                      <p className="text-gray-600">Loading available slots...</p>
                    </div>
                  ) : availableSlots.length > 0 ? (
                    <div className="grid grid-cols-1 sm:grid-cols-2 gap-3">
                      {availableSlots.map((slot, index) => (
                        <button
                          key={`${slot}-${index}`}
                          type="button"
                          onClick={() => setSelectedSlot(slot)}
                          className={`p-4 text-sm font-medium rounded-xl border-2 transition-all duration-200 ${
                            selectedSlot === slot
                              ? 'bg-gradient-to-r from-green-500 to-emerald-500 text-white border-green-500 shadow-lg transform scale-105'
                              : 'bg-white text-gray-700 border-gray-200 hover:bg-gray-50 hover:border-gray-300'
                          }`}
                        >
                          <div className="flex items-center justify-center">
                            <Clock className="h-4 w-4 mr-2" />
                            {formatTimeSlot(slot)}
                          </div>
                        </button>
                      ))}
                    </div>
                  ) : (
                    <div className="text-center py-12 bg-gray-50 rounded-xl">
                      <AlertCircle className="h-12 w-12 text-gray-400 mx-auto mb-4" />
                      <h3 className="text-lg font-medium text-gray-900 mb-2">No Available Slots</h3>
                      <p className="text-gray-500">No charging slots available for this date. Please try another date.</p>
                    </div>
                  )}
                </div>
              )}
              
              {/* Action Buttons */}
              <div className="flex space-x-4">
                <button
                  type="button"
                  onClick={onCancel}
                  disabled={loading}
                  className="flex-1 py-4 px-6 border-2 border-gray-300 rounded-xl text-gray-700 font-semibold hover:bg-gray-50 hover:border-gray-400 transition-all duration-200 disabled:opacity-50 disabled:cursor-not-allowed"
                >
                  Cancel
                </button>
                <button
                  type="submit"
                  onClick={handleSubmit}
                  disabled={loading || !selectedSlot}
                  className={`flex-1 py-4 rounded-xl text-white font-semibold transition-all duration-200 ${
                    loading || !selectedSlot
                      ? 'bg-gray-400 cursor-not-allowed'
                      : 'bg-gradient-to-r from-blue-600 to-indigo-600 hover:from-blue-700 hover:to-indigo-700 shadow-lg hover:shadow-xl transform hover:-translate-y-0.5'
                  }`}
                >
                  {loading ? (
                    <div className="flex items-center justify-center">
                      <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-white mr-2"></div>
                      Processing...
                    </div>
                  ) : (
                    <div className="flex items-center justify-center">
                      <CheckCircle className="h-5 w-5 mr-2" />
                      Reschedule Charging Slot
                    </div>
                  )}
                </button>
              </div>
            </div>
          </div>
          
          {/* Status Message */}
          {message.text && (
            <div className={`px-8 py-4 border-t ${
              message.type === 'success' 
                ? 'bg-green-50 border-green-100' 
                : 'bg-red-50 border-red-100'
            }`}>
              <div className={`flex items-center ${
                message.type === 'success' ? 'text-green-800' : 'text-red-800'
              }`}>
                {message.type === 'success' ? (
                  <CheckCircle className="h-5 w-5 mr-2" />
                ) : (
                  <AlertCircle className="h-5 w-5 mr-2" />
                )}
                <span className="font-medium">{message.text}</span>
              </div>
            </div>
          )}
        </div>

        {/* Info Card */}
        <div className="mt-6 bg-blue-50 rounded-xl p-6 border border-blue-100">
          <h3 className="font-semibold text-blue-900 mb-2">Reschedule Information</h3>
          <ul className="text-sm text-blue-800 space-y-1">
            <li> Each slot is typically 1 hour duration</li>
            <li> Slots are shown in your local time</li>
            <li> You can reschedule up to 30 days in advance</li>
            <li> Your previous booking will be automatically cancelled</li>
          </ul>
        </div>
      </div>
    </div>
  );
};

export default RescheduleBookingForm;
