  const fetchNearbyBunks = async (lat, lng, radius = 10) => {
    try {
      setLoading(true);
      setError(null);
      setShowNearbyOnly(true);
      
      // First try to get user location if not provided
      let userLat = lat;
      let userLng = lng;
      
      if (!userLat || !userLng) {
        try {
          const location = await getCurrentLocation();
          userLat = location.lat;
          userLng = location.lng;
        } catch (locationError) {
          console.warn('Could not get user location:', locationError);
          // Use default location (Bangalore)
          userLat = 12.9716;
          userLng = 77.5946;
        }
      }

      // Try the nearby API endpoint first
      let bunks = [];
      try {
        const response = await fetch(`/api/bunks/nearby?latitude=${userLat}&longitude=${userLng}&radius=${radius}`);
        
        if (response.ok) {
          const data = await response.json();
          bunks = data.data || [];
