                    <span className="text-gray-700">{selectedBunk.phone}</span>
                  </div>
                  
                  <div className="flex items-center gap-3">
                    <Clock className="h-5 w-5 text-gray-400 flex-shrink-0" />
                    <span className="text-gray-700">{selectedBunk.operatingHours}</span>
                  </div>

                  <div className={`flex items-center gap-3 p-3 rounded-lg ${
                    selectedBunk.slotsAvailable > 0 
                      ? 'bg-green-50 text-green-700' 
                      : 'bg-red-50 text-red-700'
                  }`}>
                    <Zap className="h-5 w-5 flex-shrink-0" />
                    <span className="font-medium">
                      {selectedBunk.slotsAvailable} slots available
                      {selectedBunk.totalSlots && ` of ${selectedBunk.totalSlots}`}
                    </span>
                  </div>

                  {selectedBunk.distance && (
                    <div className="bg-blue-50 text-blue-700 p-3 rounded-lg">
                      <span className="font-medium">
                        Distance: {selectedBunk.distance.toFixed(1)} km away
                      </span>
                    </div>
                  )}

                  {selectedBunk.connectorTypes && selectedBunk.connectorTypes.length > 0 && (
                    <div>
                      <p className="font-medium text-gray-700 mb-2">Available Connectors:</p>
                      <div className="flex flex-wrap gap-2">
                        {selectedBunk.connectorTypes.map((connector, index) => (
                          <span
                            key={index}
                            className="bg-gray-100 text-gray-700 px-3 py-1 rounded-full text-sm"
                          >
                            {connector}
                          </span>
                        ))}
                      </div>
                    </div>
                  )}

                  {selectedBunk.amenities && selectedBunk.amenities.length > 0 && (
                    <div>
                      <p className="font-medium text-gray-700 mb-2">Amenities:</p>
                      <div className="flex flex-wrap gap-2">
                        {selectedBunk.amenities.map((amenity, index) => (
                          <span
                            key={index}
                            className="bg-blue-100 text-blue-700 px-3 py-1 rounded-full text-sm"
                          >
                            {amenity}
                          </span>
                        ))}
                      </div>
                    </div>
                  )}

                  {selectedBunk.pricing && (
                    <div>
                      <p className="font-medium text-gray-700 mb-1">Pricing:</p>
                      <p className="text-gray-600">{selectedBunk.pricing}</p>
                    </div>
                  )}
                </div>

                <div className="flex gap-3 mt-6 pt-4 border-t border-gray-200">
                  <button
                    onClick={() => {
                      setSelectedBunk(null);
                      setViewMode('map');
                      setTimeout(() => {
                        if (map) {
                          map.setCenter({ lat: selectedBunk.latitude, lng: selectedBunk.longitude });
                          map.setZoom(15);
                        }
                      }, 100);
                    }}
                    className="flex-1 bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 transition-colors flex items-center justify-center gap-2"
                  >
                    <Map className="h-4 w-4" />
                    View on Map
                  </button>
                  
                  {userLocation && (
                    <button
                      onClick={() => {
                        showDirections(selectedBunk);
                        setSelectedBunk(null);
                        setViewMode('map');
                      }}
                      className="flex-1 bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700 transition-colors flex items-center justify-center gap-2"
                    >
                      <Navigation className="h-4 w-4" />
                      Get Directions
                    </button>
                  )}
                </div>
              </div>
            </div>
          </div>
        )}

        {/* Quick Stats Summary */}
        {bunkLocations.length > 0 && (
          <div className="mt-8 bg-gray-50 rounded-lg p-6">
            <h3 className="text-lg font-semibold text-gray-800 mb-4">Quick Stats</h3>
            <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
              <div className="text-center">
                <div className="text-2xl font-bold text-blue-600">
                  {bunkLocations.length}
                </div>
                <div className="text-sm text-gray-600">Total Stations</div>
              </div>
              
              <div className="text-center">
                <div className="text-2xl font-bold text-green-600">
                  {bunkLocations.filter(b => b.slotsAvailable > 0).length}
                </div>
                <div className="text-sm text-gray-600">Available Now</div>
              </div>
              
              <div className="text-center">
                <div className="text-2xl font-bold text-purple-600">
                  {bunkLocations.reduce((sum, b) => sum + (b.slotsAvailable || 0), 0)}
                </div>
                <div className="text-sm text-gray-600">Free Slots</div>
              </div>
              
              <div className="text-center">
                <div className="text-2xl font-bold text-orange-600">
                  {bunkLocations.reduce((sum, b) => sum + (b.totalSlots || b.slotsAvailable || 0), 0)}
                </div>
                <div className="text-sm text-gray-600">Total Slots</div>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default EvBunkMapView;
