import React, { useState, useEffect } from 'react';
import { Calendar, Clock, MapPin, Edit3, X, CheckCircle, XCircle, AlertCircle, Plus, RefreshCw, Zap, Shield, Clock4 } from 'lucide-react';

// Mock RescheduleBookingForm component for demonstration
const RescheduleBookingForm = ({ booking, onRescheduleSuccess, onCancel }) => (
  <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
    <div className="bg-white p-8 rounded-2xl shadow-2xl max-w-md w-full">
      <h3 className="text-xl font-bold text-gray-900 mb-2">Reschedule Booking</h3>
      <p className="text-gray-600 mb-6">Booking for: {booking?.bunkId?.name}</p>
      <div className="flex gap-3">
        <button 
          onClick={() => onRescheduleSuccess(booking)}
          className="flex-1 px-4 py-3 bg-blue-600 text-white rounded-xl hover:bg-blue-700 transition-colors font-medium"
        >
          Confirm Reschedule
        </button>
        <button 
          onClick={onCancel}
          className="flex-1 px-4 py-3 bg-gray-100 text-gray-700 rounded-xl hover:bg-gray-200 transition-colors font-medium"
        >
          Cancel
        </button>
      </div>
    </div>
  </div>
);

// UserNavbar Mock Component
const UserNavbar = () => (
  <nav className="bg-white shadow-sm border-b border-gray-100">
    <div className="container mx-auto px-4 py-4">
      <div className="flex items-center justify-between">
        <div className="flex items-center gap-3">
          <div className="w-10 h-10 bg-gradient-to-br from-blue-600 to-indigo-600 rounded-xl flex items-center justify-center">
            <Zap className="w-6 h-6 text-white" />
          </div>
          <span className="text-xl font-bold text-gray-900">EVCharge</span>
        </div>
        <div className="flex items-center gap-4">
          <span className="text-gray-600">Welcome back!</span>
        </div>
      </div>
    </div>
  </nav>
);

const UserBookings = ({ bookings, onCancelBooking, onBookingUpdate, showMessage }) => {
  const [rescheduleBooking, setRescheduleBooking] = useState(null);

  // Helper function to format dates in a readable way
  const formatDate = (dateString) => {
    try {
      const options = { 
        year: 'numeric', 
        month: 'short', 
        day: 'numeric', 
        hour: '2-digit', 
        minute: '2-digit'
      };
      return new Date(dateString).toLocaleDateString('en-US', options);
    } catch (error) {
      console.error('Date formatting error:', error);
      return 'Invalid date';
    }
  };

  // Helper function to get appropriate status styling
  const getStatusConfig = (status) => {
    switch(status) {
      case 'active':
        return {
          bg: 'bg-emerald-50',
          text: 'text-emerald-700',
          border: 'border-emerald-200',
          icon: CheckCircle
        };
      case 'completed':
        return {
          bg: 'bg-blue-50',
          text: 'text-blue-700',
          border: 'border-blue-200',
          icon: CheckCircle
        };
      case 'cancelled':
        return {
          bg: 'bg-red-50',
          text: 'text-red-700',
          border: 'border-red-200',
          icon: XCircle
        };
      default:
        return {
          bg: 'bg-gray-50',
          text: 'text-gray-700',
          border: 'border-gray-200',
          icon: AlertCircle
        };
    }
  };

  // Calculate if a booking is upcoming or past
  const isUpcoming = (startTime) => {
    try {
      return new Date(startTime) > new Date();
    } catch (error) {
      return false;
    }
  };

  // Handle booking cancellation with confirmation
  const handleCancelBooking = async (bookingId) => {
    if (!window.confirm('Are you sure you want to cancel this booking?')) return;
    
    if (onCancelBooking) {
      await onCancelBooking(bookingId);
    }
  };

  // Handle reschedule click
  const handleRescheduleClick = (booking) => {
    setRescheduleBooking(booking);
  };

  // Handle reschedule success
  const handleRescheduleSuccess = (updatedBooking) => {
    if (onBookingUpdate) {
      onBookingUpdate(updatedBooking);
    }
    setRescheduleBooking(null);
    if (showMessage) {
      showMessage("success", "Booking rescheduled successfully.");
    }
  };

  // If reschedule form is active, show it
  if (rescheduleBooking) {
    return (
      <RescheduleBookingForm
        booking={rescheduleBooking}
        onRescheduleSuccess={handleRescheduleSuccess}
        onCancel={() => setRescheduleBooking(null)}
      />
    );
  }

  // If no bookings
  if (!bookings || bookings.length === 0) {
    return (
      <div className="text-center py-16">
        <div className="max-w-md mx-auto">
          <div className="w-24 h-24 mx-auto mb-6 bg-gradient-to-br from-blue-100 to-indigo-100 rounded-full flex items-center justify-center">
            <Calendar className="w-12 h-12 text-blue-600" />
          </div>
          <h3 className="text-xl font-semibold text-gray-900 mb-2">No bookings yet</h3>
          <p className="text-gray-600 mb-8">Ready to charge up? Book your first EV charging slot.</p>
          <button 
            onClick={() => window.location.href = '/book'}
            className="inline-flex items-center gap-2 bg-gradient-to-r from-blue-600 to-indigo-600 hover:from-blue-700 hover:to-indigo-700 text-white py-3 px-6 rounded-xl font-medium transition-all duration-200 shadow-lg hover:shadow-xl"
          >
            <Plus className="w-5 h-5" />
            Book Your First Slot
          </button>
        </div>
      </div>
    );
  }

  // Safe access to properties using optional chaining
  const getBookingName = (booking) => {
    return booking?.bunkId?.name || 'Unknown Station';
  };

  const getBookingLocation = (booking) => {
    return booking?.bunkId?.location || 'Location not specified';
  };

  const upcomingBookings = bookings.filter(b => isUpcoming(b.startTime));
  const pastBookings = bookings.filter(b => !isUpcoming(b.startTime));

  return (
    <div className="space-y-8">
      {/* Upcoming bookings section */}
      <div>
        <div className="flex items-center gap-3 mb-6">
          <div className="w-2 h-8 bg-gradient-to-b from-blue-500 to-indigo-500 rounded-full"></div>
          <h2 className="text-2xl font-bold text-gray-900">Upcoming Bookings</h2>
          {upcomingBookings.length > 0 && (
            <span className="bg-blue-100 text-blue-700 px-3 py-1 rounded-full text-sm font-medium">
              {upcomingBookings.length} active
            </span>
          )}
        </div>
        
        {upcomingBookings.length > 0 ? (
          <div className="grid gap-4">
            {upcomingBookings.map(booking => {
              const statusConfig = getStatusConfig(booking.status);
              const StatusIcon = statusConfig.icon;
              
              return (
                <div key={booking._id} className="bg-white rounded-2xl shadow-sm border border-gray-100 hover:shadow-md transition-all duration-200 group">
                  <div className="p-6">
                    <div className="flex items-start justify-between">
                      <div className="flex-1">
                        <div className="flex items-center gap-3 mb-3">
                          <h3 className="text-lg font-semibold text-gray-900">{getBookingName(booking)}</h3>
                          <div className={`flex items-center gap-1.5 px-3 py-1 rounded-full text-sm font-medium border ${statusConfig.bg} ${statusConfig.text} ${statusConfig.border}`}>
                            <StatusIcon className="w-4 h-4" />
                            {booking.status.charAt(0).toUpperCase() + booking.status.slice(1)}
                          </div>
                        </div>
                        
                        <div className="flex items-center gap-2 text-gray-600 mb-4">
                          <MapPin className="w-4 h-4" />
                          <span className="text-sm">{getBookingLocation(booking)}</span>
                        </div>
                        
                        <div className="grid sm:grid-cols-2 gap-4">
                          <div className="flex items-center gap-3 p-3 bg-green-50 rounded-xl">
                            <div className="w-8 h-8 bg-green-100 rounded-lg flex items-center justify-center">
                              <Clock className="w-4 h-4 text-green-600" />
                            </div>
                            <div>
                              <div className="text-xs font-medium text-green-700 uppercase tracking-wide">Start Time</div>
                              <div className="text-sm font-semibold text-green-900">{formatDate(booking.startTime)}</div>
                            </div>
                          </div>
                          
                          <div className="flex items-center gap-3 p-3 bg-orange-50 rounded-xl">
                            <div className="w-8 h-8 bg-orange-100 rounded-lg flex items-center justify-center">
                              <Clock className="w-4 h-4 text-orange-600" />
                            </div>
                            <div>
                              <div className="text-xs font-medium text-orange-700 uppercase tracking-wide">End Time</div>
                              <div className="text-sm font-semibold text-orange-900">{formatDate(booking.endTime)}</div>
                            </div>
                          </div>
                        </div>
                      </div>
                      
                      {booking.status === 'active' && (
                        <div className="flex flex-col gap-2 ml-6">
                          <button
                            onClick={() => handleRescheduleClick(booking)}
                            className="flex items-center gap-2 px-4 py-2 bg-blue-600 hover:bg-blue-700 text-white text-sm font-medium rounded-lg transition-colors duration-200"
                          >
                            <Edit3 className="w-4 h-4" />
                            Reschedule
                          </button>
                          <button
                            onClick={() => handleCancelBooking(booking._id)}
                            className="flex items-center gap-2 px-4 py-2 bg-red-600 hover:bg-red-700 text-white text-sm font-medium rounded-lg transition-colors duration-200"
                          >
                            <X className="w-4 h-4" />
                            Cancel
                          </button>
                        </div>
                      )}
                    </div>
                  </div>
                </div>
              );
            })}
          </div>
        ) : (
          <div className="bg-white rounded-2xl shadow-sm border border-gray-100 p-12 text-center">
            <div className="w-16 h-16 mx-auto mb-4 bg-gray-100 rounded-full flex items-center justify-center">
              <Calendar className="w-8 h-8 text-gray-400" />
            </div>
            <h3 className="text-lg font-medium text-gray-900 mb-2">No upcoming bookings</h3>
            <p className="text-gray-600">Your future charging sessions will appear here.</p>
          </div>
        )}
      </div>
      
      {/* Past bookings section */}
      <div>
        <div className="flex items-center gap-3 mb-6">
          <div className="w-2 h-8 bg-gradient-to-b from-gray-400 to-gray-500 rounded-full"></div>
          <h2 className="text-2xl font-bold text-gray-900">Past Bookings</h2>
          {pastBookings.length > 0 && (
            <span className="bg-gray-100 text-gray-700 px-3 py-1 rounded-full text-sm font-medium">
              {pastBookings.length} completed
            </span>
          )}
        </div>
        
        {pastBookings.length > 0 ? (
          <div className="grid gap-4">
            {pastBookings.map(booking => {
              const statusConfig = getStatusConfig(booking.status);
              const StatusIcon = statusConfig.icon;
              
              return (
                <div key={booking._id} className="bg-white rounded-2xl shadow-sm border border-gray-100 hover:shadow-md transition-all duration-200 opacity-90">
                  <div className="p-6">
                    <div className="flex items-start justify-between">
                      <div className="flex-1">
                        <div className="flex items-center gap-3 mb-3">
                          <h3 className="text-lg font-semibold text-gray-700">{getBookingName(booking)}</h3>
                          <div className={`flex items-center gap-1.5 px-3 py-1 rounded-full text-sm font-medium border ${statusConfig.bg} ${statusConfig.text} ${statusConfig.border}`}>
                            <StatusIcon className="w-4 h-4" />
                            {booking.status.charAt(0).toUpperCase() + booking.status.slice(1)}
                          </div>
                        </div>
                        
                        <div className="flex items-center gap-2 text-gray-500 mb-4">
                          <MapPin className="w-4 h-4" />
                          <span className="text-sm">{getBookingLocation(booking)}</span>
                        </div>
                        
                        <div className="grid sm:grid-cols-2 gap-4">
                          <div className="flex items-center gap-3 p-3 bg-gray-50 rounded-xl">
                            <div className="w-8 h-8 bg-gray-100 rounded-lg flex items-center justify-center">
                              <Clock className="w-4 h-4 text-gray-500" />
                            </div>
                            <div>
                              <div className="text-xs font-medium text-gray-500 uppercase tracking-wide">Started</div>
                              <div className="text-sm font-semibold text-gray-700">{formatDate(booking.startTime)}</div>
                            </div>
                          </div>
                          
                          <div className="flex items-center gap-3 p-3 bg-gray-50 rounded-xl">
                            <div className="w-8 h-8 bg-gray-100 rounded-lg flex items-center justify-center">
                              <Clock className="w-4 h-4 text-gray-500" />
                            </div>
                            <div>
                              <div className="text-xs font-medium text-gray-500 uppercase tracking-wide">Ended</div>
                              <div className="text-sm font-semibold text-gray-700">{formatDate(booking.endTime)}</div>
                            </div>
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              );
            })}
          </div>
        ) : (
          <div className="bg-white rounded-2xl shadow-sm border border-gray-100 p-12 text-center">
            <div className="w-16 h-16 mx-auto mb-4 bg-gray-100 rounded-full flex items-center justify-center">
              <Clock className="w-8 h-8 text-gray-400" />
            </div>
            <h3 className="text-lg font-medium text-gray-900 mb-2">No past bookings</h3>
            <p className="text-gray-600">Your booking history will appear here.</p>
          </div>
        )}
      </div>
    </div>
  );
};

const MyBookingsPage = () => {
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [debugInfo, setDebugInfo] = useState(null);
  const [useMockData, setUseMockData] = useState(false);
  const [message, setMessage] = useState(null);

  // Show message function
  const showMessage = (type, text) => {
    setMessage({ type, text });
    setTimeout(() => setMessage(null), 5000);
  };

  useEffect(() => {
    const fetchBookings = async () => {
      try {
        setLoading(true);
        setError(null);
        setDebugInfo(null);
        
        // Get the JWT token from localStorage (mock for demo)
        const token = 'demo-token';
        console.log('Token exists:', !!token);
        
        if (!token) {
          throw new Error('Authentication token not found. Please log in again.');
        }
        
        // Option to use mock data for testing UI when API is not working
        if (useMockData) {
          console.log('Using mock data');
          setTimeout(() => {
            setBookings(MOCK_BOOKINGS);
            setLoading(false);
          }, 1000);
          return;
        }
        
        // Simulate API call for demo
        setTimeout(() => {
          setBookings(MOCK_BOOKINGS);
          setLoading(false);
        }, 1000);
        
      } catch (err) {
        console.error('Error fetching bookings:', err);
        setError(`${err.message}`);
        setLoading(false);
      }
    };

    fetchBookings();
  }, [useMockData]);

  // Function to manually retry fetching
  const handleRetry = () => {
    setLoading(true);
    setError(null);
    setUseMockData(useMockData); 
  };

  // Handle booking cancellation at the page level
  const handleCancelBooking = async (bookingId) => {
    try {
      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 500));
      
      // Update the bookings state to reflect cancellation
      setBookings(prevBookings => 
        prevBookings.map(booking => 
          booking._id === bookingId 
            ? { ...booking, status: 'cancelled' } 
            : booking
        )
      );
      
      showMessage('success', 'Booking cancelled successfully');
    } catch (err) {
      console.error('Error cancelling booking:', err);
      showMessage('error', 'Failed to cancel booking');
    }
  };

  const handleBookingUpdate = (updatedBooking) => {
    setBookings(prevBookings => 
      prevBookings.map(booking => 
        booking._id === updatedBooking._id ? updatedBooking : booking
      )
    );
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 via-blue-50 to-indigo-50">
      <UserNavbar />
      
      {/* Message Toast */}
      {message && (
        <div className={`fixed top-4 right-4 z-50 px-6 py-3 rounded-xl shadow-lg transition-all duration-300 ${
          message.type === 'success' 
            ? 'bg-green-500 text-white' 
            : 'bg-red-500 text-white'
        }`}>
          {message.text}
        </div>
      )}
      
      <div className="container mx-auto px-4 py-8">
        <div className="max-w-6xl mx-auto">
          {/* Header Section */}
          <div className="bg-white rounded-3xl shadow-sm border border-gray-100 p-8 mb-8">
            <div className="flex flex-col lg:flex-row lg:items-center justify-between gap-6">
              <div>
                <h1 className="text-3xl font-bold text-gray-900 mb-2">My Bookings</h1>
                <p className="text-gray-600 text-lg">Manage your EV charging appointments with ease</p>
                
                {/* Development toggle for mock data */}
                {true && ( // Always show for demo
                  <div className="mt-4">
                    <label className="inline-flex items-center cursor-pointer">
                      <input
                        type="checkbox"
                        checked={useMockData}
                        onChange={() => setUseMockData(!useMockData)}
                        className="sr-only peer"
                      />
                      <div className="relative w-11 h-6 bg-gray-200 peer-focus:outline-none peer-focus:ring-4 peer-focus:ring-blue-300 rounded-full peer peer-checked:after:translate-x-full rtl:peer-checked:after:-translate-x-full peer-checked:after:border-white after:content-[''] after:absolute after:top-[2px] after:start-[2px] after:bg-white after:border-gray-300 after:border after:rounded-full after:h-5 after:w-5 after:transition-all peer-checked:bg-blue-600"></div>
                      <span className="ms-3 text-sm font-medium text-gray-700">Use test data</span>
                    </label>
                  </div>
                )}
              </div>
              
              <div className="flex flex-col sm:flex-row gap-3">
                <button 
                  onClick={() => window.location.href = '/user/bookings/new'}
                  className="inline-flex items-center gap-2 bg-gradient-to-r from-green-600 to-emerald-600 hover:from-green-700 hover:to-emerald-700 text-white py-3 px-6 rounded-xl font-medium transition-all duration-200 shadow-lg hover:shadow-xl"
                >
                  <Plus className="w-5 h-5" />
                  Book New Slot
                </button>
              </div>
            </div>
          </div>

          {/* Main Content */}
          <div className="bg-white rounded-3xl shadow-sm border border-gray-100 p-8">
            {loading ? (
              <div className="text-center py-16">
                <div className="inline-flex items-center justify-center w-16 h-16 bg-blue-100 rounded-full mb-6">
                  <RefreshCw className="w-8 h-8 text-blue-600 animate-spin" />
                </div>
                <h3 className="text-xl font-semibold text-gray-900 mb-2">Loading your bookings</h3>
                <p className="text-gray-600">Please wait while we fetch your appointments...</p>
              </div>
            ) : error ? (
              <div className="text-center py-16">
                <div className="max-w-md mx-auto">
                  <div className="w-16 h-16 mx-auto mb-6 bg-red-100 rounded-full flex items-center justify-center">
                    <AlertCircle className="w-8 h-8 text-red-600" />
                  </div>
                  <h3 className="text-xl font-semibold text-gray-900 mb-2">Unable to load bookings</h3>
                  <p className="text-gray-600 mb-8">{error}</p>
                  
                  <button 
                    onClick={handleRetry}
                    className="inline-flex items-center gap-2 bg-red-600 hover:bg-red-700 text-white py-3 px-6 rounded-xl font-medium transition-colors duration-200"
                  >
                    <RefreshCw className="w-5 h-5" />
                    Try Again
                  </button>
                  
                  {debugInfo && (
                    <div className="mt-6 p-4 bg-gray-50 rounded-xl text-left text-sm">
                      <p className="font-semibold mb-2 text-gray-800">Debug Information:</p>
                      {debugInfo.status && <p className="text-gray-600"><strong>Status:</strong> {debugInfo.status}</p>}
                      {debugInfo.statusText && <p className="text-gray-600"><strong>Status Text:</strong> {debugInfo.statusText}</p>}
                      {debugInfo.errorDetails && Object.keys(debugInfo.errorDetails).length > 0 && (
                        <div className="mt-2">
                          <p className="text-gray-600"><strong>Error Details:</strong></p>
                          <pre className="bg-gray-800 text-white p-3 rounded-lg overflow-x-auto mt-1 text-xs">{JSON.stringify(debugInfo.errorDetails, null, 2)}</pre>
                        </div>
                      )}
                    </div>
                  )}
                </div>
              </div>
            ) : (
              <UserBookings 
                bookings={bookings} 
                onCancelBooking={handleCancelBooking}
                onBookingUpdate={handleBookingUpdate}
                showMessage={showMessage}
              />
            )}
          </div>

          {/* Booking Policies Section */}
          <div className="mt-8 bg-gradient-to-r from-blue-50 to-indigo-50 rounded-3xl border border-blue-100 p-8">
            <div className="flex items-start gap-4">
              <div className="w-12 h-12 bg-blue-100 rounded-xl flex items-center justify-center flex-shrink-0">
                <Shield className="w-6 h-6 text-blue-600" />
              </div>
              <div>
                <h3 className="text-xl font-bold text-blue-900 mb-4">Booking Policies & Guidelines</h3>
                <div className="grid md:grid-cols-2 gap-6">
                  <div className="flex items-start gap-3">
                    <Clock4 className="w-5 h-5 text-blue-600 mt-0.5 flex-shrink-0" />
                    <div>
                      <h4 className="font-semibold text-blue-800 mb-1">Cancellation Policy</h4>
                      <p className="text-blue-700 text-sm">Cancellations less than 2 hours before your appointment may result in a cancellation fee.</p>
                    </div>
                  </div>
                  <div className="flex items-start gap-3">
                    <Zap className="w-5 h-5 text-blue-600 mt-0.5 flex-shrink-0" />
                    <div>
                      <h4 className="font-semibold text-blue-800 mb-1">Flexible Booking</h4>
                      <p className="text-blue-700 text-sm">For frequent modifications, consider booking flexible time slots that offer more convenience.</p>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

