import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';

const UserBookings = ({ bookings, onCancelBooking }) => {
  const navigate = useNavigate();
  const [message, setMessage] = useState({ text: '', type: '' });
  
  // Helper function to format dates in a readable way
  const formatDate = (dateString) => {
    try {
      const options = { 
        year: 'numeric', 
        month: 'short', 
        day: 'numeric', 
        hour: '2-digit', 
        minute: '2-digit'
      };
      return new Date(dateString).toLocaleDateString('en-US', options);
    } catch (error) {
      console.error('Date formatting error:', error);
      return 'Invalid date';
    }
  };

  // Helper function to get appropriate status badge class
  const getStatusClass = (status) => {
    switch(status) {
      case 'active':
        return 'bg-green-100 text-green-800';
      case 'completed':
        return 'bg-blue-100 text-blue-800';
      case 'cancelled':
        return 'bg-red-100 text-red-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  // Calculate if a booking is upcoming or past
  const isUpcoming = (startTime) => {
    try {
      return new Date(startTime) > new Date();
    } catch (error) {
      return false;
    }
  };

  // Handle booking cancellation with success/failure message
  const handleCancelBooking = async (bookingId) => {
    if (window.confirm('Are you sure you want to cancel this booking?')) {
      try {
        await onCancelBooking(bookingId);
        setMessage({
          text: 'Booking cancelled successfully!',
          type: 'success'
        });
        setTimeout(() => setMessage({ text: '', type: '' }), 5000); // Clear after 5 seconds
      } catch (error) {
        setMessage({
          text: 'Failed to cancel booking. Please try again.',
          type: 'error'
        });
        setTimeout(() => setMessage({ text: '', type: '' }), 5000); // Clear after 5 seconds
      }
    }
  };

  // Function to go back to previous page
  const goBack = () => {
    navigate(-1);
  };

  // Get message style based on type
  const getMessageStyle = (type) => {
    switch(type) {
      case 'success':
        return 'bg-green-100 border-green-500 text-green-700';
      case 'error':
        return 'bg-red-100 border-red-500 text-red-700';
      default:
        return 'bg-blue-100 border-blue-500 text-blue-700';
    }
  };

  // If no bookings
  if (!bookings || bookings.length === 0) {
    return (
      <div>
        {/* Back button */}
        <button 
          onClick={goBack} 
          className="flex items-center mb-4 px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 transition-colors duration-300 shadow-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-opacity-50"
        >
          <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-2" fill="none" viewBox="0 0 24 24" stroke="currentColor">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M10 19l-7-7m0 0l7-7m-7 7h18" />
          </svg>
          Back
        </button>

        {/* Success/Error Message */}
        {message.text && (
          <div className={`p-4 mb-4 border-l-4 rounded ${getMessageStyle(message.type)}`}>
            {message.text}
          </div>
        )}
        
        <div className="text-center py-12 bg-gray-50 rounded-lg">
          <p className="text-gray-600 mb-4">You don't have any bookings yet.</p>
          <Link 
            to="/user/bookings/new" 
            className="bg-blue-600 hover:bg-blue-700 text-white py-2 px-4 rounded-md"
          >
            Book Your First Slot
          </Link>
        </div>
      </div>
    );
  }

  // Safe access to properties using optional chaining
  const getBookingName = (booking) => {
    return booking?.bunkId?.name || 'Unknown Station';
  };

  return (
    <div>
      {/* Back button */}
      <button 
        onClick={goBack} 
        className="flex items-center mb-4 px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 transition-colors duration-300 shadow-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-opacity-50"
      >
        <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-2" fill="none" viewBox="0 0 24 24" stroke="currentColor">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M10 19l-7-7m0 0l7-7m-7 7h18" />
        </svg>
        Back
      </button>

      {/* Success/Error Message */}
      {message.text && (
        <div className={`p-4 mb-4 border-l-4 rounded ${getMessageStyle(message.type)}`}>
          {message.text}
        </div>
      )}

      {/* Upcoming bookings section */}
      <div className="mb-8">
        <h2 className="text-lg font-medium mb-4 text-gray-700">Upcoming Bookings</h2>
        <div className="bg-white rounded-lg overflow-hidden shadow">
          {bookings.filter(b => isUpcoming(b.startTime)).length > 0 ? (
            <div className="divide-y divide-gray-200">
              {bookings
                .filter(booking => isUpcoming(booking.startTime))
                .map(booking => (
                  <div key={booking._id} className="p-4 hover:bg-gray-50 transition-colors">
                    <div className="flex items-center justify-between">
                      <div>
                        <h3 className="font-medium">{getBookingName(booking)}</h3>
                        <div className="text-sm text-gray-500 mt-1">
                          <div>Start: {formatDate(booking.startTime)}</div>
                          <div>End: {formatDate(booking.endTime)}</div>
                        </div>
                      </div>
                      
                      <div className="flex items-center space-x-3">
                        <span className={`px-2 py-1 text-xs rounded-full ${getStatusClass(booking.status)}`}>
                          {booking.status.charAt(0).toUpperCase() + booking.status.slice(1)}
                        </span>
                        
                        {booking.status === 'active' && (
                          <div className="flex space-x-2">
                            <Link
                              to={`/user/bookings/list`}
                              className="px-3 py-1 bg-blue-600 text-white text-xs rounded hover:bg-blue-700"
                            >
                              Reschedule
                            </Link>
                            <button
                              onClick={() => handleCancelBooking(booking._id)}
                              className="px-3 py-1 bg-red-600 text-white text-xs rounded hover:bg-red-700"
                            >
                              Cancel
                            </button>
                          </div>
                        )}
                      </div>
                    </div>
                  </div>
              ))}
            </div>
          ) : (
            <div className="p-4 text-center text-gray-500">
              No upcoming bookings
            </div>
          )}
        </div>
      </div>
      
      {/* Past bookings section */}
      <div>
        <h2 className="text-lg font-medium mb-4 text-gray-700">Past Bookings</h2>
        <div className="bg-white rounded-lg overflow-hidden shadow">
          {bookings.filter(b => !isUpcoming(b.startTime)).length > 0 ? (
            <div className="divide-y divide-gray-200">
              {bookings
                .filter(booking => !isUpcoming(booking.startTime))
                .map(booking => (
                  <div key={booking._id} className="p-4 hover:bg-gray-50 transition-colors">
                    <div className="flex items-center justify-between">
                      <div>
                        <h3 className="font-medium">{getBookingName(booking)}</h3>
                        <div className="text-sm text-gray-500 mt-1">
                          <div>Start: {formatDate(booking.startTime)}</div>
                          <div>End: {formatDate(booking.endTime)}</div>
                        </div>
                      </div>
                      
                      <span className={`px-2 py-1 text-xs rounded-full ${getStatusClass(booking.status)}`}>
                        {booking.status.charAt(0).toUpperCase() + booking.status.slice(1)}
                      </span>
                    </div>
                  </div>
              ))}
            </div>
          ) : (
            <div className="p-4 text-center text-gray-500">
              No past bookings
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default UserBookings;
