// src/components/user/RescheduleBookingForm.jsx
import React, { useState } from 'react';

const RescheduleBookingForm = ({ booking, onReschedule, onCancel }) => {
  const [newDate, setNewDate] = useState('');
  const [newTime, setNewTime] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!newDate || !newTime) {
      setError('Please select both date and time.');
      return;
    }

    const selectedDateTime = new Date(`${newDate}T${newTime}`);
    if (selectedDateTime < new Date()) {
      setError('Please select a future date and time.');
      return;
    }

    try {
      // Simulate an API call
      await new Promise((resolve) => setTimeout(resolve, 1000));
      onReschedule({ ...booking, date: newDate, time: newTime });
    } catch (err) {
      console.error('Rescheduling error:', err);
      setError('Failed to reschedule. Please try again.');
    }
  };

  return (
    <div className="p-4 border rounded-lg shadow-lg bg-white dark:bg-gray-800">
      <h3 className="text-lg font-semibold mb-2 text-gray-800 dark:text-white">Reschedule Booking</h3>
      <p className="text-sm text-gray-600 dark:text-gray-300 mb-4">
        Current Date: {booking.date} | Current Time: {booking.time}
      </p>
      {error && <p className="text-red-500 text-sm mb-2">{error}</p>}
      <form onSubmit={handleSubmit} className="flex flex-col space-y-2">
        <label className="text-gray-700 dark:text-gray-200">
          New Date:
          <input
            type="date"
            value={newDate}
            onChange={(e) => setNewDate(e.target.value)}
            className="border p-2 rounded w-full mt-1"
          />
        </label>
        <label className="text-gray-700 dark:text-gray-200">
          New Time:
          <input
            type="time"
            value={newTime}
            onChange={(e) => setNewTime(e.target.value)}
            className="border p-2 rounded w-full mt-1"
          />
        </label>
        <div className="flex justify-end space-x-2">
          <button
            type="button"
            onClick={onCancel}
            className="bg-gray-300 hover:bg-gray-400 text-gray-800 py-1 px-3 rounded"
          >
            Cancel
          </button>
          <button
            type="submit"
            className="bg-blue-600 hover:bg-blue-700 text-white py-1 px-3 rounded"
          >
            Confirm
          </button>
        </div>
      </form>
    </div>
  );
};

export default RescheduleBookingForm;
