import React, { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom'; // Assuming you have react-router-dom for Link
import Navbar from '../components/common/navbars/Navbar'; // Adjust path if needed


const steps = [
    {
        title: "Locate Stations",
        description: "Effortlessly find the nearest EV charging stations on an interactive map, complete with real-time availability and directions.",
        icon: "📍" // Unicode characters for icons
    },
    {
        title: "Book Your Slot",
        description: "Secure your charging slot in advance with our easy-to-use booking system, ensuring a hassle-free experience upon arrival.",
        icon: "🗓️"
    },
    {
        title: "Recharge & Go",
        description: "Connect your EV, initiate charging, and monitor your session directly from the app. Pay securely and get back on the road.",
        icon: "⚡"
    },
    {
        title: "Manage & Monitor",
        description: "Keep track of your charging history, manage payments, and receive smart notifications for an optimized EV ownership experience.",
        icon: "📊"
    }
];

const faqData = [
    {
        question: "How do I find an EV charging station?",
        answer: "You can find charging stations using the interactive map on our platform. It shows real-time availability, connector types, and navigation details."
    },
    {
        question: "Can I book a charging slot in advance?",
        answer: "Yes, our platform allows you to check real-time slot availability and book your preferred charging time at any station."
    },
    {
        question: "What payment methods are accepted?",
        answer: "We support various secure payment options, including credit/debit cards, UPI, and popular digital wallets, ensuring convenient transactions."
    },
    {
        question: "How accurate is the real-time availability?",
        answer: "Our system updates station availability in real-time based on live data from the charging stations, providing you with the most accurate information."
    },
    {
        question: "Is EV Charge Hub available outside Karnataka?",
        answer: "Currently, our primary focus is on expanding our network across Karnataka. We aim to expand to other regions in India soon."
    }
];

// Dummy functions for contact form (replace with your actual handlers)
const handleContactSubmit = (e) => {
    e.preventDefault();
    alert('Contact form submitted! (This is a dummy function)');
    // In a real app, you'd send this data to a backend
};
const handleContactChange = () => {
    // Dummy handler for input changes
};
const contactFormData = { name: '', email: '', message: '' }; // Dummy state
const contactMessage = '';
const contactError = '';

const Homepage = () => {
    const [activeIndex, setActiveIndex] = useState(null); // For FAQ section

    const toggleFAQ = (index) => {
        setActiveIndex(activeIndex === index ? null : index);
    };

    // --- Scroll Animation Logic ---
    // A map to store the visibility state of each observed element
    const [elementsInView, setElementsInView] = useState(new Map());
    // A ref to hold all the DOM nodes we want to observe
    const observedElements = useRef([]);
    observedElements.current = []; // Clear current refs on re-render

    // Add ref to the array for observation
    const addElementToObserve = (el) => {
        if (el && !observedElements.current.includes(el)) {
            observedElements.current.push(el);
        }
    };

    useEffect(() => {
        const observerOptions = {
            root: null, // relative to the viewport
            rootMargin: '0px',
            threshold: 0.2, // Element is 20% visible
        };

        const observer = new IntersectionObserver((entries) => {
            setElementsInView((prev) => {
                const newMap = new Map(prev);
                entries.forEach((entry) => {
                    // Use entry.target.id or a unique data-attribute for key if elements are not simple array mapped
                    // For this example, we'll assume a consistent way to identify elements, e.g., via their direct reference
                    // For multiple instances of the same component, you might need a unique ID prop.
                    // For now, we'll just use the element itself as the key in the map
                    newMap.set(entry.target, entry.isIntersecting);
                });
                return newMap;
            });
        }, observerOptions);

        // Observe all collected elements
        observedElements.current.forEach((el) => {
            if (el) observer.observe(el);
        });

        // Cleanup function
        return () => {
            observedElements.current.forEach((el) => {
                if (el) observer.unobserve(el);
            });
        };
    }, []); // Run once on mount

    // Helper function to get animation classes based on element's visibility
    const getAnimationClasses = (elementRef) => {
        const isInView = elementsInView.get(elementRef) || false;
        return isInView
            ? 'opacity-100 translate-y-0 translate-x-0'
            : 'opacity-0 translate-y-10'; // Default slide up for most
    };

    // More specific animation for left/right elements
    const getLeftAnimationClasses = (elementRef) => {
        const isInView = elementsInView.get(elementRef) || false;
        return isInView
            ? 'opacity-100 translate-x-0'
            : 'opacity-0 -translate-x-10';
    };

    const getRightAnimationClasses = (elementRef) => {
        const isInView = elementsInView.get(elementRef) || false;
        return isInView
            ? 'opacity-100 translate-x-0'
            : 'opacity-0 translate-x-10';
    };

    // --- END Scroll Animation Logic ---


    // Define refs for main sections
    const heroRef = useRef(null);
    const featuresSectionRef = useRef(null);
    const aboutSectionRef = useRef(null);
    const howItWorksSectionRef = useRef(null);
    const benefitsSectionRef = useRef(null);
    const testimonialsSectionRef = useRef(null);
    const faqSectionRef = useRef(null);
    const ctaSectionRef = useRef(null);
    const contactSectionRef = useRef(null);

    // Ref for each feature card to animate individually
    const featureCardRefs = useRef([]);
    featureCardRefs.current = [];
    const addFeatureCardRef = (el) => {
        if (el) featureCardRefs.current.push(el);
    };

    // Ref for each benefit card to animate individually
    const benefitCardRefs = useRef([]);
    benefitCardRefs.current = [];
    const addBenefitCardRef = (el) => {
        if (el) benefitCardRefs.current.push(el);
    };

    // Ref for each testimonial card to animate individually
    const testimonialCardRefs = useRef([]);
    testimonialCardRefs.current = [];
    const addTestimonialCardRef = (el) => {
        if (el) testimonialCardRefs.current.push(el);
    };

    // Ref for each FAQ item to animate individually
    const faqItemRefs = useRef([]);
    faqItemRefs.current = [];
    const addFaqItemRef = (el) => {
        if (el) faqItemRefs.current.push(el);
    };


    useEffect(() => {
        // Collect all refs to observe
        const allElementsToObserve = [
            heroRef.current,
            featuresSectionRef.current,
            aboutSectionRef.current,
            howItWorksSectionRef.current,
            benefitsSectionRef.current,
            testimonialsSectionRef.current,
            faqSectionRef.current,
            ctaSectionRef.current,
            contactSectionRef.current,
            ...featureCardRefs.current,
            ...benefitCardRefs.current,
            ...testimonialCardRefs.current,
            ...faqItemRefs.current,
        ].filter(Boolean); // Filter out any nulls

        const observerOptions = {
            root: null,
            rootMargin: '0px',
            threshold: 0.1, // Trigger when 10% of the element is visible
        };

        const observer = new IntersectionObserver((entries) => {
            setElementsInView((prev) => {
                const newMap = new Map(prev);
                entries.forEach((entry) => {
                    newMap.set(entry.target, entry.isIntersecting);
                });
                return newMap;
            });
        }, observerOptions);

        allElementsToObserve.forEach((el) => {
            observer.observe(el);
        });

        return () => {
            allElementsToObserve.forEach((el) => {
                observer.unobserve(el);
            });
        };
    }, []); // Empty dependency array means this runs once on mount


    return (
        <div>
            <Navbar />
            <main className="dark:bg-gray-900 dark:text-white text-gray-800 font-inter overflow-hidden">
                {/* Hero Section */}
                <section
                    id="hero"
                    ref={heroRef}
                    className={`relative bg-gradient-to-br from-blue-200 to-indigo- dark:from-gray-800 dark:to-gray-900 py-24 md:py-32 px-6
                        transition-all duration-1000 ease-out ${getAnimationClasses(heroRef)}`}
                >
                    <div className="absolute inset-0 z-0 opacity-10">
                        <svg className="w-full h-full" fill="none" viewBox="0 0 100 100" xmlns="http://www.w3.org/2000/svg">
                            <pattern id="pattern-circles" x="0" y="0" width="10" height="10" patternUnits="userSpaceOnUse">
                                <circle cx="5" cy="5" r="1" fill="rgba(255,255,255,0.2)" />
                            </pattern>
                            <rect x="0" y="0" width="100%" height="100%" fill="url(#pattern-circles)" />
                        </svg>
                    </div>
                    <div className="max-w-7xl mx-auto flex flex-col md:flex-row items-center justify-between gap-12 relative z-10">
                        <div className="flex-1 text-center md:text-left">
                            <h1 className="text-4xl lg:text-6xl font-extrabold leading-tight mb-6 text-blue-450 dark:text-blue-600">
                                Power Your Journey with <span className="text-blue-600 dark:text-blue-800 drop-shadow-md">EV Charge Hub</span>
                            </h1>
                            <p className="text-xl lg:text-2xl mb-8 text-blue-600 dark:text-gray-300">
                                Locate, Book, and Recharge at your nearest EV Station. Easy. Fast. Smart.
                            </p>
                            <div className="flex justify-center md:justify-start gap-5">
                                <Link to="/user/login" className="bg-white text-blue-700 px-8 py-4 rounded-full font-bold text-lg shadow-lg hover:bg-gray-100 transition-all duration-300 transform hover:-translate-y-1 hover:shadow-xl"> Get Started </Link>
                                <Link to="/how-it-works" className="border-2 bg-white text-blue-700 px-8 py-4 rounded-full font-bold text-lg hover:bg-white hover:text-blue-700 transition-all duration-300 transform hover:-translate-y-1 hover:shadow-lg"> How It Works </Link>
                            </div>
                        </div>
                        <div className="flex-1 flex justify-center md:justify-end mt-12 md:mt-0">
                            <img
                                src="https://img.freepik.com/free-vector/electric-car-charging-station-concept-illustration_114360-8227.jpg?w=740&t=st=1701345600~exp=1701346200~hmac=2e5a6f2b4c1d0e8f0a0c9b0e2d1f0e8f0a0c9b0e2d1f0e8f0a0c9b0e2d1f0e8f"
                                alt="EV Charging Station"
                                className="rounded-3xl shadow-2xl w-full max-w-md border-4 border-blue-300 dark:border-gray-700 transform hover:scale-105 transition-transform duration-500"
                                onError={(e) => { e.target.onerror = null; e.target.src = "https://placehold.co/600x400/E0E7FF/3F51B5?text=EV+Charging" }}
                            />
                        </div>
                    </div>
                </section>

                {/* Features Section */}
                <section
                    id="features"
                    ref={featuresSectionRef}
                    className={`py-24 px-6 md:px-12 lg:px-24 bg-white dark:bg-gray-900
                        transition-all duration-1000 ease-out ${getAnimationClasses(featuresSectionRef)}`}
                >
                    <div className="max-w-7xl mx-auto text-center mb-16">
                        <h2 className="text-4xl lg:text-5xl font-extrabold text-gray-900 dark:text-white mb-8">Unlock the Power of Seamless EV Charging</h2>
                        <p className="text-lg md:text-xl text-gray-600 dark:text-gray-400 max-w-3xl mx-auto">Experience the future of EV charging with these key features, designed to make your journey effortless and efficient.</p>
                    </div>
                    <div className="grid sm:grid-cols-2 lg:grid-cols-3 gap-10 lg:gap-12">
                        {[
                            { icon: (
                                <svg className="w-8 h-8" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M17.657 16.657L13.414 20.9a1.998 1.998 0 01-2.827 0l-4.244-4.243a8 8 0 1111.314 0z"></path><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M15 11a3 3 0 11-6 0 3 3 0 016 0z"></path></svg>
                            ), title: "Extensive Network in Karnataka", description: "Discover a growing network of EV charging stations across Mudbidri and all major locations in Karnataka, ensuring you're always connected." },
                            { icon: (
                                <svg className="w-8 h-8" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M8 7V3m8 4V3m-9 8h10M5 21h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v12a2 2 0 002 2z"></path></svg>
                            ), title: "Real-Time Availability & Booking", description: "Check live availability of charging slots and book your preferred time in advance to avoid waiting, ensuring a smooth charging experience." },
                            { icon: (
                                <svg className="w-8 h-8" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 15v2m-6 4h12a2 2 0 002-2v-6a2 2 0 00-2-2H6a2 2 0 00-2 2v6a2 2 0 002 2zm10-10V7a4 4 0 00-8 0v4h8z"></path></svg>
                            ), title: "Secure & Transparent Payments", description: "Enjoy hassle-free and secure payments through multiple options, with transparent pricing, ensuring a smooth transaction every time." },
                            { icon: (
                                <svg className="w-8 h-8" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z"></path></svg>
                            ), title: "Intuitive User & Admin Panels", description: "Dedicated, user-friendly dashboards for EV owners to manage bookings and for station administrators to oversee the network efficiently." },
                            { icon: (
                                <svg className="w-8 h-8" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M13 10V3L4 14h7v7l9-11h-7z"></path></svg>
                            ), title: "Smart Charge Management", description: "Optimize your charging sessions with smart features, monitor energy usage in real-time, and track your complete charging history for insights." },
                            { icon: (
                                <svg className="w-8 h-8" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M15 15l-2 5L9 9l11 4-5 2zm0 0l-2 5m2-5l-2-5m-2 5l4-9"></path></svg>
                            ), title: "Eco-Friendly Initiative", description: "Join our community committed to sustainable transportation, reduce your carbon footprint, and contribute to a cleaner environment in Karnataka." },
                        ].map((feature, index) => (
                            <div
                                key={index}
                                ref={addFeatureCardRef} // Add ref to this card
                                className={`p-10 bg-gray-50 dark:bg-gray-800 rounded-3xl shadow-xl hover:shadow-2xl transition-all duration-500 transform hover:-translate-y-2 border border-gray-100 dark:border-gray-700 group
                                    ${getAnimationClasses(featureCardRefs.current[index])} delay-${index * 100}`}
                            >
                                <div className="w-16 h-16 bg-gradient-to-br from-blue-600 to-indigo-700 text-white rounded-2xl flex items-center justify-center mb-7 shadow-lg group-hover:scale-110 transition-transform duration-300">
                                    {feature.icon}
                                </div>
                                <h3 className="text-2xl font-bold text-gray-900 dark:text-white mb-4">{feature.title}</h3>
                                <p className="text-gray-700 dark:text-gray-300 leading-relaxed">{feature.description}</p>
                            </div>
                        ))}
                    </div>
                </section>

                {/* About Section */}
                <section
                    id="about"
                    ref={aboutSectionRef}
                    className={`min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 dark:from-gray-950 dark:to-gray-800 text-gray-800 dark:text-gray-100 py-24 px-6 md:px-12 lg:px-24
                        transition-all duration-1000 ease-out ${getAnimationClasses(aboutSectionRef)}`}
                >
                    <div className="max-w-7xl mx-auto">
                        <h2 className="text-4xl lg:text-5xl font-extrabold mb-16 text-center text-blue-700 dark:text-blue-400">About EV Charge Hub</h2>
                        <div className="grid md:grid-cols-2 gap-12 items-center">
                            <div
                                ref={addElementToObserve} // Observing this div directly
                                className={`p-10 bg-white dark:bg-gray-800 rounded-3xl shadow-2xl border border-gray-100 dark:border-gray-700
                                    transition-all duration-1000 ease-out ${getLeftAnimationClasses(observedElements.current[observedElements.current.length - 2])}`}
                            >
                                <h3 className="text-3xl font-bold text-gray-900 dark:text-white mb-5 border-b-2 border-blue-500 pb-2">Our Mission</h3>
                                <p className="mb-7 text-lg leading-relaxed text-gray-700 dark:text-gray-300">
                                    <strong>EV Charge Hub</strong> is an innovative platform designed to streamline the electric vehicle (EV) charging experience for users and administrators alike. Our mission is to empower the future of mobility by connecting EV drivers with accessible, reliable, and smart charging stations, fostering a greener transportation ecosystem in India.
                                </p>
                                <h3 className="text-3xl font-bold text-gray-900 dark:text-white mb-5 border-b-2 border-blue-500 pb-2">Why EV Charge Hub?</h3>
                                <ul className="list-disc list-inside ml-4 space-y-3 text-base text-gray-700 dark:text-gray-300">
                                    <li><span className="font-semibold text-blue-600 dark:text-blue-300">Locate with Ease:</span> Find nearby EV charging stations with ease and real-time data.</li>
                                    <li><span className="font-semibold text-blue-600 dark:text-blue-300">Seamless Booking:</span> Book and manage charging slots seamlessly from anywhere.</li>
                                    <li><span className="font-semibold text-blue-600 dark:text-blue-300">Detailed Bunk Info:</span> Get detailed bunk info including address, map, available slots, and connector types.</li>
                                    <li><span className="font-semibold text-blue-600 dark:text-blue-300">Dedicated Admin Tools:</span> Dedicated admin tools to add, manage, and monitor EV recharge bunks efficiently.</li>
                                    <li><span className="font-semibold text-blue-600 dark:text-blue-300">Modern Interface:</span> Modern, intuitive interface with full responsiveness and dark mode support for optimal user experience.</li>
                                </ul>
                            </div>
                            <div
                                ref={addElementToObserve} // Observing this div directly
                                className={`flex justify-center items-center p-8 bg-gray-100 dark:bg-gray-900 rounded-3xl shadow-2xl
                                    transition-all duration-1000 ease-out ${getRightAnimationClasses(observedElements.current[observedElements.current.length - 1])}`}
                            >
                                <img
                                    src="https://img.freepik.com/free-vector/gradient-ev-charging-station-illustration_23-2149372950.jpg?w=740&t=st=1701345800~exp=1701346400~hmac=2e5a6f2b4c1d0e8f0a0c9b0e2d1f0e8f0a0c9b0e2d1f0e8f0a0c9b0e2d1f0e8f"
                                    alt="Our Vision for EV Charging"
                                    className="rounded-2xl shadow-xl w-full max-w-md object-cover border-4 border-emerald-300 dark:border-emerald-700"
                                />
                            </div>
                        </div>
                        <div
                            ref={addElementToObserve} // Observing this div directly
                            className={`mt-20 text-center
                                transition-all duration-1000 ease-out ${getAnimationClasses(observedElements.current[observedElements.current.length - 1])}`}
                        >
                            <h2 className="text-4xl font-extrabold text-gray-900 dark:text-white mb-8">Our Vision</h2>
                            <p className="text-lg leading-relaxed text-gray-700 dark:text-gray-300 max-w-4xl mx-auto"> At EV Charge Hub, we believe the future of transportation is electric and sustainable. We're committed to supporting India's transition to green energy by building a platform that is simple, efficient, and accessible for everyone—from enthusiastic EV owners to dedicated recharge station operators. We envision a future where range anxiety is a thing of the past, and charging an EV is as simple as fueling a traditional vehicle. </p>
                        </div>
                    </div>
                </section>

                {/* How It Works Section - Keep its own dedicated animation logic */}
                <section
                    id="how-it-works"
                    ref={howItWorksSectionRef}
                    className={`py-24 px-6 md:px-12 lg:px-24 bg-gray-50 dark:bg-gray-900 text-gray-800 dark:text-gray-100
                        transition-all duration-1000 ease-out ${getAnimationClasses(howItWorksSectionRef)}`}
                >
                    <div className="max-w-7xl mx-auto">
                        <h2 className="text-4xl lg:text-5xl font-extrabold text-center text-blue-700 dark:text-blue-400 mb-16"> How EV Charge Hub Works </h2>
                        {/* Reusing the steps data from previous 'HowItWorks' component */}
                        <div className="relative">
                            {/* Vertical Line for Timeline Effect (Desktop Only) */}
                            <div className="hidden md:block absolute left-1/2 transform -translate-x-1/2 w-1.5 bg-blue-300 dark:bg-blue-700 rounded-full h-full"></div>

                            {steps.map((step, index) => {
                                // Unique ref for each individual timeline step card
                                const stepCardRef = useRef(null);
                                useEffect(() => {
                                    if (stepCardRef.current) addElementToObserve(stepCardRef.current);
                                }, []);

                                const isInView = elementsInView.get(stepCardRef.current) || false;

                                return (
                                    <div
                                        key={index}
                                        className="relative mb-12 md:mb-16 flex items-center justify-center"
                                    >
                                        {/* Step Card - Dynamic styling based on isInView */}
                                        <div
                                            ref={stepCardRef} // Attach ref to this card for individual observation
                                            className={`w-full md:w-5/12 p-8 rounded-xl shadow-lg dark:shadow-2xl transition-all duration-1000 ease-out border
                                                ${index % 2 === 0 ? "md:mr-auto" : "md:ml-auto"}
                                                ${isInView // Dynamic styling based on isInView
                                                    ? "opacity-100 transform translate-y-0 bg-white dark:bg-gray-850 shadow-xl dark:shadow-2xl border-blue-200 dark:border-blue-600"
                                                    : "opacity-0 transform translate-y-10 bg-gray-100 dark:bg-gray-800 border-gray-200 dark:border-gray-700" // Hidden/off-screen state
                                                }`}
                                            style={{ transitionDelay: `${index * 150}ms` }} // Staggered delay
                                        >
                                            <div className="flex items-center mb-4">
                                                {/* Emoji Icon */}
                                                <div className={`text-4xl mr-4 p-3 rounded-full shadow-inner transition-colors duration-1000
                                                    ${isInView ? "bg-blue-200 dark:bg-blue-900" : "bg-blue-100 dark:bg-blue-800"}`}>
                                                    {step.icon}
                                                </div>
                                                <h3 className={`text-2xl font-bold transition-colors duration-1000
                                                    ${isInView ? "text-blue-700 dark:text-blue-300" : "text-gray-700 dark:text-gray-200"}`}>
                                                    {step.title}
                                                </h3>
                                            </div>
                                            <p className={`mt-4 text-base md:text-lg leading-relaxed transition-colors duration-1000
                                                ${isInView ? "text-gray-700 dark:text-gray-300" : "text-gray-600 dark:text-gray-400"}`}>
                                                {step.description}
                                            </p>
                                        </div>

                                        {/* Connector Dot (Desktop Only) - Dynamic styling based on isInView */}
                                        <div className={`hidden md:block absolute left-1/2 transform -translate-x-1/2 z-10 w-8 h-8 rounded-full border-4 shadow-md transition-colors duration-1000
                                            ${isInView
                                                ? "bg-blue-600 dark:bg-blue-400 border-white dark:border-gray-950"
                                                : "bg-blue-300 dark:bg-blue-700 border-white dark:border-gray-950" // Inactive dot color
                                            }`}
                                        style={{ transitionDelay: `${index * 150}ms` }} // Staggered delay
                                        >
                                        </div>
                                    </div>
                                );
                            })}
                        </div>
                        <div
                            ref={addElementToObserve} // Observing this div directly
                            className={`mt-20 text-center
                                transition-all duration-1000 ease-out ${getAnimationClasses(observedElements.current[observedElements.current.length - 1])}`}
                            style={{ transitionDelay: `${steps.length * 150}ms` }} // Delay after last step
                        >
                            <p className="text-xl font-medium text-gray-700 dark:text-gray-300"> Ready to simplify your EV charging?{" "}
                                <Link to="/login/user" className="text-blue-600 dark:text-blue-400 underline hover:text-blue-800 dark:hover:text-blue-200 font-semibold transition-colors duration-200" > Login now </Link>{" "} and book your slot! </p>
                        </div>
                    </div>
                </section>

                {/* Benefits Section */}
                <section
                    id="benefits"
                    ref={benefitsSectionRef}
                    className={`py-24 px-6 md:px-12 lg:px-24 bg-gradient-to-br from-green-50 to-emerald-100 dark:from-green-900 dark:to-emerald-950 text-gray-800 dark:text-white
                        transition-all duration-1000 ease-out ${getAnimationClasses(benefitsSectionRef)}`}
                >
                    <div className="max-w-7xl mx-auto text-center mb-16">
                        <h2 className="text-4xl lg:text-5xl font-extrabold mb-8 text-gray-900 dark:text-white">Driving Towards a Greener & Smarter Karnataka</h2>
                        <p className="text-lg md:text-xl text-gray-700 dark:text-gray-300 max-w-3xl mx-auto">Embrace the numerous advantages of electric vehicles and smart charging solutions in our vibrant region:</p>
                    </div>
                    <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-10 lg:gap-12">
                        {[
                            { icon: (
                                <svg className="w-8 h-8" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M13 10V3L4 14h7v7l9-11h-7z"></path></svg>
                            ), title: "Reduced Emissions & Cleaner Air", description: "Contribute directly to cleaner air quality in Mudbidri and across Karnataka by choosing electric vehicles with zero tailpipe emissions." },
                            { icon: (
                                <svg className="w-8 h-8" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 8h6m-5 0v4m0 0h4m-4 0h-1m1 0v4m0 0h4m-4 0h-1m1 0v4m0 0h4m-4 0h-1m1 0v4m0 0h4m-4 0h-1"></path></svg>
                            ), title: "Significant Cost Savings", description: "Benefit from lower running costs due to cheaper electricity compared to petrol/diesel, and reduced maintenance needs for EVs." },
                            { icon: (
                                <svg className="w-8 h-8" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M21 12a9 9 0 01-9 9m9-9a9 9 0 00-9-9m9 9H3m9 9a9 9 0 01-9-9m9 9c1.657 0 3-4.03 3-9s-1.343-9-3-9m-9 9a9 9 0 019-9m-9 9c-1.657 0-3-4.03-3-9s1.343-9 3-9m11.95 9.95l-1.414 1.414M10.05 10.05l-1.414-1.414M11.95 10.05l1.414 1.414M10.05 11.95l1.414 1.414"></path></svg>
                            ), title: "Government Incentives & Support", description: "Take advantage of various state and central government incentives, subsidies, and tax benefits for EV adoption in India." },
                            { icon: (
                                <svg className="w-8 h-8" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 12l2 2 4-4m5.618-4.016A11.955 11.955 0 0112 2.944a11.955 11.955 0 01-8.618 3.04A12.001 12.001 0 002.92 12c0 3.072 1.22 5.852 3.14 7.954L12 22.95l5.94-5.046A12.004 12.004 0 0021.08 12c0-3.072-1.22-5.852-3.14-7.954z"></path></svg>
                            ), title: "Quiet & Smooth Ride", description: "Enjoy a peaceful and comfortable driving experience with electric vehicles, known for their silent operation and smooth acceleration." },
                        ].map((benefit, index) => (
                            <div
                                key={index}
                                ref={addBenefitCardRef} // Add ref to this card
                                className={`p-10 bg-white dark:bg-gray-800 rounded-3xl shadow-xl hover:shadow-2xl transition-all duration-500 transform hover:scale-102 border border-gray-100 dark:border-gray-700 group
                                    ${getAnimationClasses(benefitCardRefs.current[index])} delay-${index * 100}`}
                            >
                                <div className="w-16 h-16 bg-gradient-to-br from-green-500 to-lime-600 text-white rounded-2xl flex items-center justify-center mb-7 shadow-lg group-hover:scale-110 transition-transform duration-300">
                                    {benefit.icon}
                                </div>
                                <h3 className="text-2xl font-bold text-gray-900 dark:text-white mb-4">{benefit.title}</h3>
                                <p className="text-gray-700 dark:text-gray-300 leading-relaxed">{benefit.description}</p>
                            </div>
                        ))}
                    </div>
                </section>

                {/* Testimonials Section */}
                <section
                    id="testimonials"
                    ref={testimonialsSectionRef}
                    className={`py-24 px-6 md:px-12 lg:px-24 bg-gray-50 dark:bg-gray-900
                        transition-all duration-1000 ease-out ${getAnimationClasses(testimonialsSectionRef)}`}
                >
                    <div className="max-w-7xl mx-auto text-center mb-16">
                        <h2 className="text-4xl lg:text-5xl font-extrabold text-gray-900 dark:text-white mb-8">What Our Users Say</h2>
                        <p className="text-lg md:text-xl text-gray-600 dark:text-gray-400 max-w-3xl mx-auto">Hear from satisfied EV owners who power their journeys with EV Charge Hub:</p>
                    </div>
                    <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-10 lg:gap-12">
                        {[
                            {
                                img: "https://images.unsplash.com/photo-1535713875002-d1d0cfd46dbf?crop=entropy&cs=tinysrgb&fit=max&fm=jpg&ixid=M3w0NTIyMDF8MHwxfHNlYXJjaHw1fHxtYWxlJTIwcG9ydHJhaXR8ZW58MHx8fHwxNzA1MjA1ODQ4fDA&ixlib=rb-4.0.3&q=80&w=400",
                                name: "John D.",
                                location: "Bengaluru, Karnataka",
                                text: "EV Charge Hub has transformed my daily commute in Mudbidri. Finding a station and booking a slot is incredibly easy. Highly recommended!"
                            },
                            {
                                img: "https://images.unsplash.com/photo-1544005313-94ddf0286df2?crop=entropy&cs=tinysrgb&fit=max&fm=jpg&ixid=M3w0NTIyMDF8MHwxfHNlYXJjaHw0fHxmZW1hbGUlMjBwb3J0cmFpdHxlbnwwfHx8fDE3MDUyMDU4NDh8MA&ixlib=rb-4.0.3&q=80&w=400",
                                name: "Anjali S.",
                                location: "Mysuru, Karnataka",
                                text: "The real-time availability feature is a game-changer. I never have to worry about long queues anymore. A must-have app for any EV owner in Karnataka!"
                            },
                            {
                                img: "https://images.unsplash.com/photo-1500648767791-00dcc994a43e?crop=entropy&cs=tinysrgb&fit=max&fm=jpg&ixid=M3w0NTIyMDF8MHwxfHNlYXJjaHwxNXx8bWFsZSUyMHBvcnRyYWl0fGVufDB8fHx8MTcwNTIwNTg0OHww&ixlib=rb-4.0.3&q=80&w=400",
                                name: "Rahul K.",
                                location: "Mangaluru, Karnataka",
                                text: "I appreciate the secure payment options and the detailed charging history. It helps me manage my EV expenses efficiently. Great service!"
                            },
                        ].map((testimonial, index) => (
                            <div
                                key={index}
                                ref={addTestimonialCardRef} // Add ref to this card
                                className={`bg-white dark:bg-gray-800 p-10 rounded-3xl shadow-xl border border-gray-100 dark:border-gray-700 flex flex-col items-center text-center
                                    transition-all duration-500 ease-out ${getAnimationClasses(testimonialCardRefs.current[index])} delay-${index * 100}`}
                            >
                                <img className="w-24 h-24 rounded-full mb-7 border-4 border-blue-400 dark:border-blue-700 object-cover" src={testimonial.img} alt={testimonial.name} />
                                <p className="text-xl italic text-gray-700 dark:text-gray-300 mb-5 leading-relaxed">"{testimonial.text}"</p>
                                <p className="font-bold text-blue-600 dark:text-blue-400 text-lg">- {testimonial.name}</p>
                                <p className="text-base text-gray-500 dark:text-gray-400 mt-1">{testimonial.location}</p>
                            </div>
                        ))}
                    </div>
                </section>

                {/* FAQ Section */}
                <section
                    id="faq"
                    ref={faqSectionRef}
                    className={`py-24 px-6 md:px-12 lg:px-24 bg-white dark:bg-gray-800
                        transition-all duration-1000 ease-out ${getAnimationClasses(faqSectionRef)}`}
                >
                    <div className="max-w-7xl mx-auto text-center mb-16">
                        <h2 className="text-4xl lg:text-5xl font-extrabold text-gray-900 dark:text-white mb-8">Frequently Asked Questions</h2>
                        <p className="text-lg md:text-xl text-gray-600 dark:text-gray-400 max-w-3xl mx-auto">Find quick answers to common questions about EV Charge Hub and EV charging.</p>
                    </div>
                    <div className="max-w-4xl mx-auto space-y-7">
                        {faqData.map((faq, index) => (
                            <div
                                key={index}
                                ref={addFaqItemRef} // Add ref to each FAQ item
                                className={`border border-gray-200 dark:border-gray-700 rounded-2xl shadow-lg overflow-hidden transition-all duration-500 hover:shadow-xl
                                    ${getAnimationClasses(faqItemRefs.current[index])} delay-${index * 100}`}
                            >
                                <button onClick={() => toggleFAQ(index)} className="w-full flex justify-between items-center p-6 bg-gray-100 dark:bg-gray-700 hover:bg-gray-200 dark:hover:bg-gray-600 focus:outline-none transition-colors duration-200" >
                                    <span className="text-xl md:text-2xl font-semibold text-left text-gray-900 dark:text-white">{faq.question}</span>
                                    <span className="text-3xl text-blue-600 dark:text-blue-400 font-bold transform transition-transform duration-300"> {activeIndex === index ? "−" : "+"} </span>
                                </button>
                                {activeIndex === index && (
                                    <div className="p-6 bg-white dark:bg-gray-900 border-t border-gray-200 dark:border-gray-700 animate-fade-in-down">
                                        <p className="text-lg text-gray-700 dark:text-gray-300 leading-relaxed">{faq.answer}</p>
                                    </div>
                                )}
                            </div>
                        ))}
                    </div>
                </section>

                {/* Call To Action Section */}
                <section
                    id="call-to-action"
                    ref={ctaSectionRef}
                    className={`py-24 px-6 md:px-12 lg:px-24 bg-gradient-to-r from-blue-700 to-indigo-800 dark:from-blue-900 dark:to-indigo-950 text-white text-center
                        transition-all duration-1000 ease-out ${getAnimationClasses(ctaSectionRef)}`}
                >
                    <div className="max-w-4xl mx-auto">
                        <h2 className="text-4xl md:text-5xl lg:text-6xl font-extrabold mb-8 leading-tight drop-shadow-lg">Ready to Recharge Smarter?</h2>
                        <p className="mb-12 text-lg md:text-xl max-w-2xl mx-auto text-blue-100 dark:text-gray-300">Join EV Charge Hub today and make your electric vehicle charging experience effortless, efficient, and enjoyable across Karnataka.</p>
                        <Link to="/login/user" className="bg-white text-blue-800 px-12 py-5 rounded-full font-bold text-xl shadow-xl hover:bg-gray-100 transition-all duration-300 transform hover:-translate-y-2 hover:shadow-2xl"> Sign Up Now </Link>
                    </div>
                </section>

                {/* Contact Section */}
                <section
                    id="contact"
                    ref={contactSectionRef}
                    className={`min-h-screen bg-white dark:bg-gray-900 text-gray-800 dark:text-gray-100 py-24 px-6 md:px-12 lg:px-24
                        transition-all duration-1000 ease-out ${getAnimationClasses(contactSectionRef)}`}
                >
                    <div className="max-w-4xl mx-auto">
                        <h2 className="text-4xl lg:text-5xl font-extrabold text-blue-700 dark:text-blue-400 mb-16 text-center">Get in Touch</h2>
                        <p className="mb-12 text-lg md:text-xl text-gray-700 dark:text-gray-300 text-center"> Have questions, suggestions, or need support? We'd love to hear from you. Fill out the form below and our team will get back to you soon. </p>
                        <form onSubmit={handleContactSubmit} className="grid gap-8 bg-gray-50 dark:bg-gray-800 p-10 rounded-3xl shadow-xl border border-gray-100 dark:border-gray-700">
                            {contactMessage && (
                                <div className="bg-green-100 border border-green-400 text-green-700 px-5 py-4 rounded-lg relative text-center text-md font-semibold">
                                    <span className="block sm:inline">{contactMessage}</span>
                                </div>
                            )}
                            {contactError && (
                                <div className="bg-red-100 border border-red-400 text-red-700 px-5 py-4 rounded-lg relative text-center text-md font-semibold">
                                    <span className="block sm:inline">{contactError}</span>
                                </div>
                            )}

                            <div> <label className="block text-lg font-medium text-gray-700 dark:text-gray-200 mb-3" htmlFor="name">Your Name</label> <input type="text" id="name" name="name" value={contactFormData.name} onChange={handleContactChange} required className="w-full px-6 py-4 rounded-lg bg-white dark:bg-gray-700 border border-gray-300 dark:border-gray-600 focus:outline-none focus:ring-2 focus:ring-blue-500 dark:focus:ring-400 text-lg shadow-sm" /> </div>
                            <div> <label className="block text-lg font-medium text-gray-700 dark:text-gray-200 mb-3" htmlFor="email">Your Email</label> <input type="email" id="email" name="email" value={contactFormData.email} onChange={handleContactChange} required className="w-full px-6 py-4 rounded-lg bg-white dark:bg-gray-700 border border-gray-300 dark:border-gray-600 focus:outline-none focus:ring-2 focus:ring-blue-500 dark:focus:ring-400 text-lg shadow-sm" /> </div>

                            <div> <label className="block text-lg font-medium text-gray-700 dark:text-gray-200 mb-3" htmlFor="message">Your Message</label> <textarea id="message" name="message" value={contactFormData.message} onChange={handleContactChange} rows="7" required className="w-full px-6 py-4 rounded-lg bg-white dark:bg-gray-700 border border-gray-300 dark:border-gray-600 focus:outline-none focus:ring-2 focus:ring-blue-500 dark:focus:ring-400 text-lg shadow-sm" /> </div>
                            <button type="submit" className="bg-blue-600 hover:bg-blue-700 text-white font-bold text-xl py-4 px-8 rounded-full transition-all duration-300 shadow-lg hover:shadow-xl transform hover:-translate-y-1" > Send Message </button>
                        </form>
                        <div className="mt-16 text-center text-lg md:text-xl text-gray-700 dark:text-gray-300">
                            <p className="mb-3">Email: <a href="mailto:support@evchargehub.com" className="text-blue-600 dark:text-blue-400 hover:underline font-semibold">support@evchargehub.com</a></p>
                            <p className="mb-3">Phone: <a href="tel:+919876543210" className="text-blue-600 dark:text-blue-400 hover:underline font-semibold">+91-98765 43210</a></p>
                            <p>Location: Mudbidri, Karnataka, India</p>
                        </div>
                    </div>
                </section>
                <footer className="bg-gray-900 dark:bg-gray-950 text-white py-14 px-6 md:px-12 lg:px-24"> <div className="max-w-7xl mx-auto text-center md:text-left grid grid-cols-1 md:grid-cols-4 gap-10"> <div> <h3 className="text-2xl font-bold mb-5 text-blue-400">EV Charge Hub</h3> <p className="text-gray-400 text-sm leading-relaxed">Your trusted partner for EV charging solutions in Karnataka, simplifying your electric journey with innovation and reliability.</p> </div> <div> <h3 className="text-2xl font-bold mb-5 text-blue-400">Quick Links</h3> <ul className="space-y-3"> <li><a href="#hero" className="text-gray-300 hover:text-white transition-colors duration-200 text-lg">Home</a></li> <li><a href="#about" className="text-gray-300 hover:text-white transition-colors duration-200 text-lg">About Us</a></li> <li><a href="#how-it-works" className="text-gray-300 hover:text-white transition-colors duration-200 text-lg">How It Works</a></li> <li><a href="#features" className="text-gray-300 hover:text-white transition-colors duration-200 text-lg">Features</a></li> </ul> </div> <div> <h3 className="text-2xl font-bold mb-5 text-blue-400">More Info</h3> <ul className="space-y-3"> <li><a href="#testimonials" className="text-gray-300 hover:text-white transition-colors duration-200 text-lg">Testimonials</a></li> <li><a href="#faq" className="text-gray-300 hover:text-white transition-colors duration-200 text-lg">FAQ</a></li> <li><a href="#contact" className="text-gray-300 hover:text-white transition-colors duration-200 text-lg">Contact</a></li> <li><Link to="/privacy-policy" className="text-gray-300 hover:text-white transition-colors duration-200 text-lg">Privacy Policy</Link></li> </ul> </div> <div> <h3 className="text-2xl font-bold mb-5 text-blue-400">Connect With Us</h3> <div className="flex justify-center md:justify-start space-x-4 mb-6"> {/* Social Media Icons (replace with actual links) */} <a href="#" className="text-gray-300 hover:text-white transition-colors duration-200"> <svg fill="currentColor" strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" className="w-8 h-8" viewBox="0 0 24 24"><path d="M18 2h-3a5 5 0 00-5 5v3H7v4h3v8h4v-8h3l1-4h-4V7a1 1 0 011-1h3z"></path></svg> </a> <a href="#" className="text-gray-300 hover:text-white transition-colors duration-200"> <svg fill="currentColor" strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" className="w-8 h-8" viewBox="0 0 24 24"><path d="M23 3a10.9 10.9 0 01-3.14 1.53 4.48 4.48 0 00-7.86 3v1A10.66 10.66 0 013 4s-4 9 5 13a11.64 11.64 0 01-7 2c9 5 20 0 20-11.5a4.5 4.5 0 00-.08-.83A7.72 7.72 0 0023 3z"></path></svg> </a> <a href="#" className="text-gray-300 hover:text-white transition-colors duration-200"> <svg fill="none" stroke="currentColor" strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" className="w-8 h-8" viewBox="0 0 24 24"><rect width="20" height="20" x="2" y="2" rx="5" ry="5"></rect><path d="M16 11.37A4 4 0 1112.63 8 4 4 0 0116 11.37zm1.5-4.87h.01"></path></svg> </a> </div> <p className="text-gray-500 text-sm">&copy; {new Date().getFullYear()} EV Charge Hub. All rights reserved.</p> </div> </div> </footer> </main>
        </div>
    );
};

export default Homepage;
