import axios from 'axios';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

// Function to fetch all bookings
export const fetchBookings = async () => {
  try {
    const response = await axios.get(`${API_URL}/bookings`);
    return response.data;
  } catch (error) {
    console.error('Error fetching bookings:', error);
    throw error;
  }
};

// Renamed function to match the import in BookingManagement.jsx
export const getAllBookings = async () => {
  return fetchBookings();
};

// Function to get bookings by ID
export const getBookingById = async (id) => {
  try {
    const response = await axios.get(`${API_URL}/bookings/${id}`);
    return response.data;
  } catch (error) {
    console.error(`Error fetching booking with ID ${id}:`, error);
    throw error;
  }
};

// Function to get bookings by bunk (charging station)
export const getBookingsByBunk = async (bunkId) => {
  try {
    const response = await axios.get(`${API_URL}/bookings/bunk/${bunkId}`);
    return response.data;
  } catch (error) {
    console.error(`Error fetching bookings for bunk ${bunkId}:`, error);
    throw error;
  }
};

// Function to create a new booking
export const createBooking = async (bookingData) => {
  try {
    const response = await axios.post(`${API_URL}/bookings`, bookingData);
    return response.data;
  } catch (error) {
    console.error('Error creating booking:', error);
    throw error;
  }
};

// Function to update a booking
export const updateBooking = async (id, bookingData) => {
  try {
    const response = await axios.put(`${API_URL}/bookings/${id}`, bookingData);
    return response.data;
  } catch (error) {
    console.error(`Error updating booking with ID ${id}:`, error);
    throw error;
  }
};

// Function to delete a booking
export const deleteBooking = async (id) => {
  try {
    const response = await axios.delete(`${API_URL}/bookings/${id}`);
    return response.data;
  } catch (error) {
    console.error(`Error deleting booking with ID ${id}:`, error);
    throw error;
  }
};
