import React, { useState, useEffect } from 'react';
import { Table, Button, Modal, Form, Input, DatePicker, Select, message, Popconfirm } from 'antd';
import moment from 'moment';
import { EditOutlined, DeleteOutlined } from '@ant-design/icons';
import { 
  getAllBookings, 
  getBookingsByBunk, 
  updateBooking, 
  deleteBooking 
} from '../../services/BookingService';

const { Option } = Select;

const BookingManagement = () => {
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(false);
  const [modalVisible, setModalVisible] = useState(false);
  const [editBooking, setEditBooking] = useState(null);
  const [form] = Form.useForm();
  const [filterBunkId, setFilterBunkId] = useState(null);

  // Fetch bookings data
  const fetchBookingsData = async () => {
    setLoading(true);
    try {
      let data;
      if (filterBunkId) {
        data = await getBookingsByBunk(filterBunkId);
      } else {
        data = await getAllBookings();
      }
      setBookings(data);
    } catch (error) {
      message.error('Failed to fetch bookings');
      console.error(error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchBookingsData();
  }, [filterBunkId]);

  // Handle editing a booking
  const handleEdit = (record) => {
    setEditBooking(record);
    form.setFieldsValue({
      ...record,
      startTime: record.startTime ? moment(record.startTime) : null,
      endTime: record.endTime ? moment(record.endTime) : null,
    });
    setModalVisible(true);
  };

  // Handle form submission for editing
  const handleSubmit = async (values) => {
    try {
      const updatedBooking = {
        ...values,
        startTime: values.startTime?.format(),
        endTime: values.endTime?.format(),
      };
      
      await updateBooking(editBooking.id, updatedBooking);
      message.success('Booking updated successfully');
      setModalVisible(false);
      fetchBookingsData();
    } catch (error) {
      message.error('Failed to update booking');
      console.error(error);
    }
  };

  // Handle deleting a booking
  const handleDelete = async (id) => {
    try {
      await deleteBooking(id);
      message.success('Booking deleted successfully');
      fetchBookingsData();
    } catch (error) {
      message.error('Failed to delete booking');
      console.error(error);
    }
  };

  // Table columns
  const columns = [
    {
      title: 'ID',
      dataIndex: 'id',
      key: 'id',
    },
    {
      title: 'User',
      dataIndex: 'userId',
      key: 'userId',
    },
    {
      title: 'Bunk (Station)',
      dataIndex: 'bunkId',
      key: 'bunkId',
    },
    {
      title: 'Start Time',
      dataIndex: 'startTime',
      key: 'startTime',
      render: (text) => moment(text).format('YYYY-MM-DD HH:mm:ss'),
    },
    {
      title: 'End Time',
      dataIndex: 'endTime',
      key: 'endTime',
      render: (text) => moment(text).format('YYYY-MM-DD HH:mm:ss'),
    },
    {
      title: 'Status',
      dataIndex: 'status',
      key: 'status',
    },
    {
      title: 'Actions',
      key: 'actions',
      render: (_, record) => (
        <>
          <Button 
            type="primary" 
            icon={<EditOutlined />} 
            onClick={() => handleEdit(record)}
            style={{ marginRight: 8 }}
          />
          <Popconfirm
            title="Are you sure you want to delete this booking?"
            onConfirm={() => handleDelete(record.id)}
            okText="Yes"
            cancelText="No"
          >
            <Button type="danger" icon={<DeleteOutlined />} />
          </Popconfirm>
        </>
      ),
    },
  ];

  // Filter options
  const filterOptions = [
    { value: null, label: 'All Bunks' },
    { value: '1', label: 'Bunk 1' },
    { value: '2', label: 'Bunk 2' },
    { value: '3', label: 'Bunk 3' },
    // Add more bunks as needed
  ];

  return (
    <div className="booking-management">
      <h1>Booking Management</h1>
      
      <div style={{ marginBottom: 16 }}>
        <Select
          placeholder="Filter by Bunk"
          style={{ width: 200, marginRight: 16 }}
          onChange={(value) => setFilterBunkId(value)}
          value={filterBunkId}
        >
          {filterOptions.map(option => (
            <Option key={option.value} value={option.value}>{option.label}</Option>
          ))}
        </Select>
        <Button type="primary" onClick={fetchBookingsData}>Refresh</Button>
      </div>
      
      <Table
        columns={columns}
        dataSource={bookings}
        rowKey="id"
        loading={loading}
        pagination={{ pageSize: 10 }}
      />
      
      <Modal
        title="Edit Booking"
        visible={modalVisible}
        onCancel={() => setModalVisible(false)}
        footer={null}
      >
        <Form form={form} layout="vertical" onFinish={handleSubmit}>
          <Form.Item name="userId" label="User ID" rules={[{ required: true }]}>
            <Input />
          </Form.Item>
          <Form.Item name="bunkId" label="Bunk ID" rules={[{ required: true }]}>
            <Input />
          </Form.Item>
          <Form.Item name="startTime" label="Start Time" rules={[{ required: true }]}>
            <DatePicker showTime format="YYYY-MM-DD HH:mm:ss" />
          </Form.Item>
          <Form.Item name="endTime" label="End Time" rules={[{ required: true }]}>
            <DatePicker showTime format="YYYY-MM-DD HH:mm:ss" />
          </Form.Item>
          <Form.Item name="status" label="Status" rules={[{ required: true }]}>
            <Select>
              <Option value="pending">Pending</Option>
              <Option value="confirmed">Confirmed</Option>
              <Option value="cancelled">Cancelled</Option>
              <Option value="completed">Completed</Option>
            </Select>
          </Form.Item>
          <Form.Item>
            <Button type="primary" htmlType="submit">
              Save Changes
            </Button>
          </Form.Item>
        </Form>
      </Modal>
    </div>
  );
};

export default BookingManagement;
