import { useState } from 'react';
import axios from 'axios';

const BookSlot = () => {
  const [slotTime, setSlotTime] = useState('');
  const [bunkId, setBunkId] = useState('');
  const [message, setMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      const res = await axios.post('/api/bookings/create', 
        { bunkId, slotTime },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setMessage("Booking successful!");
    } catch (err) {
      setMessage(err.response?.data?.message || 'Booking failed');
    }
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-4">
      <input type="text" placeholder="Bunk ID" value={bunkId} onChange={e => setBunkId(e.target.value)} required />
      <input type="datetime-local" value={slotTime} onChange={e => setSlotTime(e.target.value)} required />
      <button type="submit">Book Slot</button>
      {message && <p>{message}</p>}
    </form>
  );
};

export default BookSlot;
