import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { format } from 'date-fns';

// Mock data for development mode when no auth token is available
const MOCK_BOOKINGS = [
  {
    _id: "60d21b4667d0d8992e610c85",
    userId: { name: "John Doe" },
    bunkId: { name: "Central EV Station" },
    startTime: new Date().toISOString(),
    status: "active"
  },
  {
    _id: "60d21b4667d0d8992e610c86",
    userId: { name: "Jane Smith" },
    bunkId: { name: "North Side Charging" },
    startTime: new Date(Date.now() + 86400000).toISOString(), // Tomorrow
    status: "completed"
  },
  {
    _id: "60d21b4667d0d8992e610c87",
    userId: { name: "Robert Johnson" },
    bunkId: { name: "South EV Hub" },
    startTime: new Date(Date.now() - 86400000).toISOString(), // Yesterday
    status: "cancelled"
  }
];

const AdminBookingsList = () => {
  const navigate = useNavigate();
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Pagination state
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [totalBookings, setTotalBookings] = useState(0);
  const [limit] = useState(10);

  // Filter state
  const [filters, setFilters] = useState({
    status: '',
    startDate: '',
    endDate: '',
    search: ''
  });

  // For development mode: Set up mock auth token
  useEffect(() => {
    const setupDevEnvironment = () => {
      if (process.env.NODE_ENV === 'development' && !getAuthToken()) {
        // For development only - create a temporary mock auth mechanism
        const devMode = localStorage.getItem('devMode');
        
        if (devMode === 'true') {
          console.log('Development mode: Using mock authentication');
          // This is just for development, not for production
          localStorage.setItem('mockAuthToken', 'dev-token-for-testing');
        }
      }
    };
    
    setupDevEnvironment();
  }, []);

  // Get authentication token from storage
  const getAuthToken = () => {
    return (
      localStorage.getItem('authToken') || 
      localStorage.getItem('token') || 
      localStorage.getItem('userToken') ||
      sessionStorage.getItem('authToken') ||
      (process.env.NODE_ENV === 'development' ? localStorage.getItem('mockAuthToken') : null)
    );
  };

  // Function to fetch bookings
  const fetchBookings = useCallback(async () => {
    try {
      setLoading(true);
      setError(null);

      // Get the token
      const token = getAuthToken();
      
      if (!token) {
        if (process.env.NODE_ENV === 'development') {
          console.warn("⚠️ Development mode: No auth token found. You might need to log in first.");
          // Using mock data for development
          setBookings(MOCK_BOOKINGS);
          setTotalPages(1);
          setTotalBookings(MOCK_BOOKINGS.length);
          setLoading(false);
          return;
        } else {
          setError('Authentication required. Please log in again.');
          setLoading(false);
          return;
        }
      }

      // Set up headers with authentication
      const headers = {
        'Content-Type': 'application/json',
        Authorization: `Bearer ${token}`
      };
      
      // Make API call with proper parameters
      const response = await axios.get('/api/bookings', { 
        headers,
        params: {
          page: currentPage,
          limit,
          ...(filters.status && { status: filters.status }),
          ...(filters.startDate && { startDate: filters.startDate }),
          ...(filters.endDate && { endDate: filters.endDate }),
          ...(filters.search && { search: filters.search })
        }
      });
      
      // Handle different response structures consistently
      let bookingsData = [], totalPagesCount = 1, totalBookingsCount = 0;
      
      if (response.data.data && response.data.data.bookings) {
        // Format from the controller code you shared
        bookingsData = response.data.data.bookings;
        totalPagesCount = response.data.data.pagination?.pages || 1;
        totalBookingsCount = response.data.data.pagination?.total || 0;
      } else if (response.data.bookings) {
        // Alternative format
        bookingsData = response.data.bookings;
        totalPagesCount = response.data.totalPages || 1;
        totalBookingsCount = response.data.totalCount || 0;
      } else if (Array.isArray(response.data)) {
        // Direct array response
        bookingsData = response.data;
      } else {
        console.warn("Unexpected response format:", response.data);
        bookingsData = [];
      }
      
      setBookings(bookingsData);
      setTotalPages(totalPagesCount);
      setTotalBookings(totalBookingsCount);
      setLoading(false);
    } catch (err) {
      console.error('Error fetching bookings:', err);
      const errorMessage = err.response?.data?.message || 'Failed to fetch bookings';
      setError(errorMessage);
      setLoading(false);
    }
  }, [currentPage, limit, filters]);

  // Fetch bookings when component mounts or dependencies change
  useEffect(() => {
    fetchBookings();
  }, [fetchBookings]);

  // Add a separate development mode notification
  useEffect(() => {
    if (process.env.NODE_ENV === 'development') {
      const token = getAuthToken();
      if (token === 'dev-token-for-testing') {
        console.log('Development mode: Using mock authentication and data');
      }
    }
  }, []);

  // Handle filter changes
  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters(prev => ({
      ...prev,
      [name]: value
    }));
    // Reset to page 1 when filters change
    setCurrentPage(1);
  };

  // Handle search input submit
  const handleSearch = (e) => {
    e.preventDefault();
    // fetchBookings will be triggered by the dependency in useEffect
  };

  // Format date for display
  const formatDate = (dateString) => {
    if (!dateString) return 'N/A';
    try {
      return format(new Date(dateString), 'MMM dd, yyyy HH:mm');
    } catch (err) {
      return 'Invalid date';
    }
  };

  // Handle status badge styling
  const getStatusBadgeClass = (status) => {
    switch (status?.toLowerCase()) {
      case 'active':
        return 'bg-green-100 text-green-800';
      case 'cancelled':
        return 'bg-red-100 text-red-800';
      case 'completed':
        return 'bg-blue-100 text-blue-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  // Update booking status
  const updateStatus = async (bookingId, newStatus) => {
    if (!bookingId) {
      console.error('No booking ID provided for status update');
      return;
    }
    
    try {
      const token = getAuthToken();
      
      if (!token) {
        alert('You are not authenticated. Please log in again.');
        navigate('/login');
        return;
      }
      
      // For development mode with mock token, update local state only
      if (process.env.NODE_ENV === 'development' && token === 'dev-token-for-testing') {
        console.log(`Development mode: Updating booking ${bookingId} status to ${newStatus}`);
        // Update the booking in the state without making an API call
        setBookings(prevBookings =>
          prevBookings.map(booking =>
            booking._id === bookingId
              ? { ...booking, status: newStatus }
              : booking
          )
        );
        return;
      }
      
      const headers = {
        'Content-Type': 'application/json',
        Authorization: `Bearer ${token}`
      };
      
      const response = await axios.patch(
        `/api/bookings/${bookingId}/status`, 
        { status: newStatus },
        { headers }
      );

      if (response.data.success) {
        // Update the booking in the state
        setBookings(prevBookings =>
          prevBookings.map(booking =>
            booking._id === bookingId
              ? { ...booking, status: newStatus }
              : booking
          )
        );
      } else {
        // Handle unsuccessful response
        alert(response.data.message || 'Status update failed');
      }
    } catch (err) {
      console.error('Error updating booking status:', err);
      alert(err.response?.data?.message || 'Failed to update booking status');
    }
  };
    
  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-2xl font-bold mb-6">Booking Management</h1>

      {/* Filter and Search Section */}
      <div className="bg-white rounded-lg shadow-md p-4 mb-6">
        <h2 className="text-lg font-semibold mb-4">Filters</h2>

        <form onSubmit={handleSearch} className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4 mb-4">
          {/* Status filter */}
          <div>
            <label htmlFor="status" className="block text-sm font-medium text-gray-700 mb-1">
              Status
            </label>
            <select
              id="status"
              name="status"
              value={filters.status}
              onChange={handleFilterChange}
              className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500"
            >
              <option value="">All Statuses</option>
              <option value="active">Active</option>
              <option value="cancelled">Cancelled</option>
              <option value="completed">Completed</option>
            </select>
          </div>

          {/* Start date filter */}
          <div>
            <label htmlFor="startDate" className="block text-sm font-medium text-gray-700 mb-1">
              Start Date
            </label>
            <input
              type="date"
              id="startDate"
              name="startDate"
              value={filters.startDate}
              onChange={handleFilterChange}
              className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500"
            />
          </div>

          {/* End date filter */}
          <div>
            <label htmlFor="endDate" className="block text-sm font-medium text-gray-700 mb-1">
              End Date
            </label>
            <input
              type="date"
              id="endDate"
              name="endDate"
              value={filters.endDate}
              onChange={handleFilterChange}
              className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500"
            />
          </div>

          {/* Search input */}
          <div>
            <label htmlFor="search" className="block text-sm font-medium text-gray-700 mb-1">
              Search
            </label>
            <div className="flex">
              <input
                type="text"
                id="search"
                name="search"
                placeholder="Search by user, bunk..."
                value={filters.search}
                onChange={handleFilterChange}
                className="flex-grow px-3 py-2 border border-gray-300 rounded-l-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500"
              />
              <button
                type="submit"
                className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-r-md shadow-sm text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
              >
                Search
              </button>
            </div>
          </div>
        </form>
      </div>

      {/* Loading and Error States */}
      {loading && (
        <div className="text-center py-4">
          <div className="inline-block animate-spin rounded-full h-8 w-8 border-t-2 border-b-2 border-indigo-600"></div>
          <p className="mt-2 text-gray-600">Loading bookings...</p>
        </div>
      )}

      {error && !loading && (
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded-md mb-4">
          <p>{error}</p>
          {!getAuthToken() && (
            <div className="mt-2 flex space-x-2">
              <button 
                onClick={() => navigate('/login')}
                className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
              >
                Go to Login
              </button>
              {process.env.NODE_ENV === 'development' && (
                <button 
                  onClick={() => {
                    localStorage.setItem('devMode', 'true');
                    localStorage.setItem('mockAuthToken', 'dev-token-for-testing');
                    window.location.reload();
                  }}
                  className="bg-green-500 hover:bg-green-700 text-white font-bold py-2 px-4 rounded"
                >
                  Use Mock Auth (Dev Only)
                </button>
              )}
            </div>
          )}
        </div>
      )}

      {/* Bookings Table */}
      {!loading && !error && (
        <>
          <div className="bg-white shadow-md rounded-lg overflow-hidden">
            <div className="overflow-x-auto">
              <table className="min-w-full divide-y divide-gray-200">
                <thead className="bg-gray-50">
                  <tr>
                    <th
                      scope="col"
                      className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider"
                    >
                      Booking ID
                    </th>
                    <th
                      scope="col"
                      className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider"
                    >
                      User
                    </th>
                    <th
                      scope="col"
                      className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider"
                    >
                      EV Bunk
                    </th>
                    <th
                      scope="col"
                      className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider"
                    >
                      Slot Time
                    </th>
                    <th
                      scope="col"
                      className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider"
                    >
                      Status
                    </th>
                    <th scope="col" className="relative px-6 py-3">
                      <span className="sr-only">Actions</span>
                    </th>
                  </tr>
                </thead>
                <tbody className="bg-white divide-y divide-gray-200">
                  {bookings.length === 0 ? (
                    <tr>
                      <td colSpan="6" className="text-center py-4 text-gray-500">
                        No bookings found.
                      </td>
                    </tr>
                  ) : (
                    bookings.map((booking) => (
                      <tr key={booking._id} className="hover:bg-gray-50">
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                          {booking._id ? `${booking._id.substring(0, 8)}...` : 'N/A'}
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                          {booking.userId?.name || booking.user?.name || 'N/A'}
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                          {booking.bunkId?.name || booking.bunk?.name || 'N/A'}
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                          {formatDate(booking.startTime || booking.slot)}
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap">
                          <span
                            className={`inline-flex px-2 py-1 text-xs font-semibold rounded-full ${getStatusBadgeClass(
                              booking.status
                            )}`}
                          >
                            {booking.status || 'Unknown'}
                          </span>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-right text-sm font-medium space-x-2">
                         <button
                            onClick={() => navigate(`/admin/bookings/${booking._id}`)}
                            className="text-indigo-600 hover:text-indigo-900"
                          >
                            View Details
                          </button>
                          <select
                            value={booking.status || ''}
                            onChange={(e) => updateStatus(booking._id, e.target.value)}
                            className="border border-gray-300 rounded-md p-1 text-sm"
                            aria-label="Update booking status"
                          >
                            <option value="active">Active</option>
                            <option value="cancelled">Cancelled</option>
                            <option value="completed">Completed</option>
                          </select>
                        </td>
                      </tr>
                    ))
                  )}
                </tbody>
              </table>
            </div>
          </div>

          {/* Pagination Controls */}
          <div className="flex justify-between items-center mt-4">
            <p className="text-sm text-gray-600">
              Showing {bookings.length} of {totalBookings} bookings
            </p>
            <div className="space-x-2">
              <button
                onClick={() => setCurrentPage((prev) => Math.max(prev - 1, 1))}
                disabled={currentPage === 1}
                className="px-3 py-1 bg-gray-200 rounded hover:bg-gray-300 disabled:opacity-50"
              >
                Prev
              </button>
              <span className="px-2 text-sm">
                {currentPage} / {totalPages}
              </span>
              <button
                onClick={() => setCurrentPage((prev) => Math.min(prev + 1, totalPages))}
                disabled={currentPage === totalPages}
                className="px-3 py-1 bg-gray-200 rounded hover:bg-gray-300 disabled:opacity-50"
              >
                Next
              </button>
            </div>
          </div>
        </>
      )}
    </div>
  );
};

export default AdminBookingsList;
