import React, { useState, useEffect } from "react";
import { Calendar, Clock, MapPin, ArrowRight, CheckCircle, AlertCircle, Loader2 } from "lucide-react";

const RescheduleBookingForm = ({ booking, onRescheduleSuccess, onCancel }) => {
  const [selectedDate, setSelectedDate] = useState("");
  const [availableSlots, setAvailableSlots] = useState([]);
  const [selectedSlot, setSelectedSlot] = useState("");
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState({ text: "", type: "" });

  // Simulate axios for demo - replace with your actual axios calls
  const axios = {
    get: async (url) => {
      await new Promise(resolve => setTimeout(resolve, 1000));
      return {
        data: {
          data: {
            availableSlots: [
              "2025-06-10T09:00:00Z",
              "2025-06-10T10:00:00Z",
              "2025-06-10T11:00:00Z",
              "2025-06-10T14:00:00Z",
              "2025-06-10T15:00:00Z",
              "2025-06-10T16:00:00Z"
            ]
          }
        }
      };
    },
    post: async (url, data) => {
      await new Promise(resolve => setTimeout(resolve, 500));
      return { data: { data: { available: true } } };
    },
    put: async (url, data, config) => {
      await new Promise(resolve => setTimeout(resolve, 1000));
      return { data: { data: { _id: "123", newTime: data.slotTime } } };
    }
  };

  // Mock booking data for demo
  const mockBooking = booking || {
    _id: "demo123",
    startTime: "2025-06-05T14:00:00Z",
    endTime: "2025-06-05T15:00:00Z",
    bunkId: {
      _id: "bunk123",
      name: "Executive Suite",
      location: "Downtown Business Center"
    }
  };

  // Set default date to booking date when component mounts
  useEffect(() => {
    if (mockBooking && mockBooking.startTime) {
      const bookingDate = new Date(mockBooking.startTime);
      setSelectedDate(bookingDate.toISOString().split('T')[0]);
    }
  }, [mockBooking]);

  // Fetch available slots when date changes
  useEffect(() => {
    const fetchAvailableSlots = async () => {
      if (!mockBooking || !mockBooking.bunkId || !mockBooking.bunkId._id || !selectedDate) return;
      
      setLoading(true);
      try {
        const res = await axios.get(`/api/bookings/available-slots/${mockBooking.bunkId._id}/${selectedDate}`);
        setAvailableSlots(res.data.data.availableSlots || []);
      } catch (err) {
        console.error("Error fetching available slots:", err);
        setMessage({ text: "Failed to load available slots", type: "error" });
      } finally {
        setLoading(false);
      }
    };
    
    fetchAvailableSlots();
  }, [selectedDate, mockBooking]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!selectedSlot) {
      setMessage({ text: "Please select a new time slot", type: "error" });
      return;
    }
    
    setLoading(true);
    try {
      // First check if slot is still available
      const checkRes = await axios.post("/api/bookings/check-availability", {
        bunkId: mockBooking.bunkId._id,
        slotTime: selectedSlot
      });
      
      if (!checkRes.data.data.available) {
        setMessage({ text: "Sorry, this slot was just booked. Please select another.", type: "error" });
        return;
      }
      
      // If available, reschedule the booking
      const response = await axios.put(`/api/bookings/reschedule/${mockBooking._id}`, {
        slotTime: selectedSlot
      }, {
        headers: { Authorization: `Bearer ${localStorage?.getItem?.("token") || "demo-token"}` }
      });
      
      setMessage({ text: "Booking rescheduled successfully!", type: "success" });
      
      // Notify parent component of successful reschedule
      if (onRescheduleSuccess && typeof onRescheduleSuccess === 'function') {
        onRescheduleSuccess(response.data.data);
      }
      
    } catch (err) {
      console.error("Reschedule error:", err);
      
      if (err.response?.status === 401) {
        setMessage({ text: "Please log in to reschedule", type: "error" });
      } else {
        setMessage({ 
          text: err.response?.data?.message || "Reschedule failed", 
          type: "error" 
        });
      }
    } finally {
      setLoading(false);
    }
  };

  // Enhanced function to format time slots as ranges
  const formatTimeSlot = (slotData) => {
    if (!slotData) return "";
    
    let startTime, endTime;
    
    // Handle different slot data formats
    if (typeof slotData === 'string') {
      // If it's an ISO string
      if (slotData.includes('T')) {
        startTime = new Date(slotData);
        // Assume 1-hour duration if no end time provided
        endTime = new Date(startTime.getTime() + 60 * 60 * 1000);
      } 
      // If it's just time like "14:30"
      else if (slotData.includes(':')) {
        const [hours, minutes] = slotData.split(':');
        const today = new Date();
        startTime = new Date(today.getFullYear(), today.getMonth(), today.getDate(), parseInt(hours), parseInt(minutes));
        endTime = new Date(startTime.getTime() + 60 * 60 * 1000);
      }
    } 
    // If it's an object with start and end times
    else if (typeof slotData === 'object') {
      if (slotData.startTime && slotData.endTime) {
        startTime = new Date(slotData.startTime);
        endTime = new Date(slotData.endTime);
      } else if (slotData.time) {
        startTime = new Date(slotData.time);
        endTime = new Date(startTime.getTime() + 60 * 60 * 1000);
      }
    }
    
    if (!startTime || !endTime) {
      console.warn('Could not parse slot time:', slotData);
      return String(slotData);
    }
    
    const formatTime = (date) => {
      const hours = date.getHours();
      const minutes = date.getMinutes();
      const ampm = hours >= 12 ? 'PM' : 'AM';
      const hour = hours % 12 || 12;
      
      // Only show minutes if they're not 00
      if (minutes === 0) {
        return `${hour} ${ampm}`;
      } else {
        return `${hour}:${minutes.toString().padStart(2, '0')} ${ampm}`;
      }
    };
    
    const startFormatted = formatTime(startTime);
    const endFormatted = formatTime(endTime);
    
    // If both times have the same AM/PM, show it only once
    const startAMPM = startTime.getHours() >= 12 ? 'PM' : 'AM';
    const endAMPM = endTime.getHours() >= 12 ? 'PM' : 'AM';
    
    if (startAMPM === endAMPM) {
      const startWithoutAMPM = startFormatted.replace(` ${startAMPM}`, '');
      return `${startWithoutAMPM} - ${endFormatted}`;
    } else {
      return `${startFormatted} - ${endFormatted}`;
    }
  };

  // Format the current booking time for display
  const getCurrentBookingTime = () => {
    if (!mockBooking || !mockBooking.startTime) return "";
    
    const startTime = new Date(mockBooking.startTime);
    const endTime = new Date(mockBooking.endTime);
    
    const formatTime = (date) => {
      const hours = date.getHours();
      const minutes = date.getMinutes();
      const ampm = hours >= 12 ? 'PM' : 'AM';
      const hour = hours % 12 || 12;
      
      if (minutes === 0) {
        return `${hour} ${ampm}`;
      } else {
        return `${hour}:${minutes.toString().padStart(2, '0')} ${ampm}`;
      }
    };
    
    const startFormatted = formatTime(startTime);
    const endFormatted = formatTime(endTime);
    
    // If both times have the same AM/PM, show it only once
    const startAMPM = startTime.getHours() >= 12 ? 'PM' : 'AM';
    const endAMPM = endTime.getHours() >= 12 ? 'PM' : 'AM';
    
    if (startAMPM === endAMPM) {
      const startWithoutAMPM = startFormatted.replace(` ${startAMPM}`, '');
      return `${startWithoutAMPM} - ${endFormatted}`;
    } else {
      return `${startFormatted} - ${endFormatted}`;
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 via-blue-50 to-indigo-100 p-4 flex items-center justify-center">
      <div className="w-full max-w-2xl">
        {/* Header */}
        <div className="text-center mb-8">
          <div className="inline-flex items-center justify-center w-16 h-16 bg-blue-600 rounded-2xl mb-4 shadow-lg">
            <Calendar className="w-8 h-8 text-white" />
          </div>
          <h1 className="text-3xl font-bold text-gray-900 mb-2">Reschedule Booking</h1>
          <p className="text-gray-600">Choose a new date and time for your reservation</p>
        </div>

        <div className="bg-white/80 backdrop-blur-sm rounded-3xl shadow-2xl border border-white/20 overflow-hidden">
          {/* Current Booking Card */}
          <div className="bg-gradient-to-r from-blue-600 to-indigo-600 p-6 text-white">
            <div className="flex items-start justify-between">
              <div className="flex-1">
                <h3 className="text-lg font-semibold mb-3 flex items-center">
                  Current Booking
                  <ArrowRight className="w-5 h-5 ml-2 opacity-70" />
                </h3>
                <div className="space-y-2">
                  <div className="flex items-center">
                    <div className="w-8 h-8 bg-white/20 rounded-lg flex items-center justify-center mr-3">
                      <MapPin className="w-4 h-4" />
                    </div>
                    <div>
                      <p className="font-medium">{mockBooking?.bunkId?.name || 'Loading...'}</p>
                      <p className="text-blue-100 text-sm">{mockBooking?.bunkId?.location || 'Unknown location'}</p>
                    </div>
                  </div>
                  <div className="flex items-center">
                    <div className="w-8 h-8 bg-white/20 rounded-lg flex items-center justify-center mr-3">
                      <Clock className="w-4 h-4" />
                    </div>
                    <div>
                      <p className="font-medium">{getCurrentBookingTime()}</p>
                      <p className="text-blue-100 text-sm">Current appointment time</p>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>

          {/* Form Content */}
          <div className="p-8">
            <div className="space-y-8">
              {/* Date Selection */}
              <div className="space-y-3">
                <label htmlFor="date" className="block text-sm font-semibold text-gray-900 flex items-center">
                  <Calendar className="w-4 h-4 mr-2 text-blue-600" />
                  Select New Date
                </label>
                <div className="relative">
                  <input
                    id="date"
                    type="date"
                    value={selectedDate}
                    onChange={(e) => setSelectedDate(e.target.value)}
                    className="w-full p-4 border border-gray-200 rounded-2xl bg-gray-50/50 focus:bg-white focus:border-blue-500 focus:ring-4 focus:ring-blue-500/10 transition-all duration-200 text-gray-900 font-medium"
                    min={new Date().toISOString().split('T')[0]}
                    required
                  />
                </div>
              </div>
              
              {/* Time Slot Selection */}
              {selectedDate && (
                <div className="space-y-4">
                  <label className="block text-sm font-semibold text-gray-900 flex items-center">
                    <Clock className="w-4 h-4 mr-2 text-blue-600" />
                    Select New Time Slot
                  </label>
                  
                  {loading ? (
                    <div className="flex items-center justify-center py-12 text-gray-600">
                      <Loader2 className="w-6 h-6 animate-spin mr-3" />
                      Loading available slots...
                    </div>
                  ) : availableSlots.length > 0 ? (
                    <div className="grid grid-cols-2 sm:grid-cols-3 gap-3">
                      {availableSlots.map((slot, index) => (
                        <button
                          key={slot?.id || slot?.time || slot || index}
                          type="button"
                          onClick={() => setSelectedSlot(slot)}
                          className={`group relative py-4 px-4 text-sm font-medium rounded-xl border-2 transition-all duration-200 ${
                            selectedSlot === slot
                              ? 'bg-blue-600 text-white border-blue-600 shadow-lg scale-105'
                              : 'bg-white/60 text-gray-700 border-gray-200 hover:bg-white hover:border-blue-300 hover:shadow-md hover:scale-102'
                          }`}
                        >
                          <div className="flex items-center justify-center">
                            <Clock className={`w-4 h-4 mr-2 ${selectedSlot === slot ? 'text-white' : 'text-gray-400'}`} />
                            {formatTimeSlot(slot)}
                          </div>
                          {selectedSlot === slot && (
                            <div className="absolute -top-1 -right-1 w-6 h-6 bg-green-500 rounded-full flex items-center justify-center">
                              <CheckCircle className="w-4 h-4 text-white" />
                            </div>
                          )}
                        </button>
                      ))}
                    </div>
                  ) : (
                    <div className="text-center py-12">
                      <div className="w-16 h-16 bg-gray-100 rounded-full flex items-center justify-center mx-auto mb-4">
                        <Clock className="w-8 h-8 text-gray-400" />
                      </div>
                      <p className="text-gray-500 font-medium">No available slots for this date</p>
                      <p className="text-gray-400 text-sm mt-1">Please try selecting a different date</p>
                    </div>
                  )}
                </div>
              )}
              
              {/* Action Buttons */}
              <div className="flex flex-col sm:flex-row gap-4 pt-6">
                <button
                  type="button"
                  onClick={onCancel}
                  className="flex-1 py-4 px-6 border-2 border-gray-200 rounded-2xl text-gray-700 font-semibold hover:bg-gray-50 hover:border-gray-300 transition-all duration-200"
                >
                  Cancel
                </button>
                <button
                  type="button"
                  onClick={handleSubmit}
                  disabled={loading || !selectedSlot}
                  className={`flex-1 py-4 px-6 rounded-2xl font-semibold transition-all duration-200 flex items-center justify-center ${
                    loading || !selectedSlot
                      ? 'bg-gray-200 text-gray-400 cursor-not-allowed'
                      : 'bg-gradient-to-r from-blue-600 to-indigo-600 text-white hover:from-blue-700 hover:to-indigo-700 shadow-lg hover:shadow-xl transform hover:scale-105'
                  }`}
                >
                  {loading ? (
                    <>
                      <Loader2 className="w-5 h-5 animate-spin mr-2" />
                      Processing...
                    </>
                  ) : (
                    <>
                      <CheckCircle className="w-5 h-5 mr-2" />
                      Reschedule Booking
                    </>
                  )}
                </button>
              </div>
              
              {/* Status Message */}
              {message.text && (
                <div className={`p-4 rounded-2xl border-2 flex items-center ${
                  message.type === 'success' 
                    ? 'bg-green-50 border-green-200 text-green-800' 
                    : 'bg-red-50 border-red-200 text-red-800'
                }`}>
                  {message.type === 'success' ? (
                    <CheckCircle className="w-5 h-5 mr-3 flex-shrink-0" />
                  ) : (
                    <AlertCircle className="w-5 h-5 mr-3 flex-shrink-0" />
                  )}
                  <p className="font-medium">{message.text}</p>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default RescheduleBookingForm;
