import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { format } from 'date-fns';
import AdminNavbar from "../common/navbars/AdminNavbar";

const AdminBookingsList = () => {
  const navigate = useNavigate();
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Pagination state
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [totalBookings, setTotalBookings] = useState(0);
  const [limit] = useState(10);

  // Filter state
  const [filters, setFilters] = useState({
    status: '',
    startDate: '',
    endDate: '',
    search: ''
  });

  // Function to get auth token
  const getAuthToken = () => {
    return localStorage.getItem('authToken') || 
           localStorage.getItem('token') || 
           localStorage.getItem('userToken') ||
           sessionStorage.getItem('authToken');
  };

  // Function to fetch bookings
  const fetchBookings = useCallback(async () => {
    try {
      setLoading(true);
      setError(null);

      const token = getAuthToken();
      
      console.log("Authentication token:", token ? "Found" : "Not found");
      
      if (!token) {
        console.warn("No authentication token found in storage");
        setError('You are not authenticated. Please log in again.');
        setLoading(false);
        return;
      }

      // Construct query params
      const params = new URLSearchParams();
      params.append('page', currentPage.toString());
      params.append('limit', limit.toString());
      
      // Add filters if they exist
      if (filters.status) params.append('status', filters.status);
      if (filters.startDate) params.append('startDate', filters.startDate);
      if (filters.endDate) params.append('endDate', filters.endDate);
      if (filters.search) params.append('search', filters.search);

      const headers = {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${token}`
      };
      
      const apiUrl = `/api/bookings?${params.toString()}`;
      console.log("Fetching bookings with URL:", apiUrl);
      
      const response = await axios.get(apiUrl, { headers });
      
      console.log("API response:", response.data);
      
      // Handle response data
      if (response.data.success) {
        const responseData = response.data.data;
        
        setBookings(responseData.bookings || []);
        setTotalPages(responseData.pagination?.pages || 1);
        setTotalBookings(responseData.pagination?.total || 0);
        
        console.log("Successfully loaded bookings:", responseData.bookings?.length || 0);
      } else {
        setError(response.data.message || 'Failed to fetch bookings');
      }
      
      setLoading(false);
    } catch (err) {
      console.error('Error fetching bookings:', err);
      
      if (err.response?.status === 401) {
        setError('Session expired. Please log in again.');
        // Optionally redirect to login
        // navigate('/login');
      } else if (err.response?.status === 403) {
        setError('Access denied. Admin privileges required.');
      } else {
        setError(err.response?.data?.message || 'Failed to fetch bookings');
      }
      
      setLoading(false);
    }
  }, [currentPage, limit, filters]);

  // Fetch bookings when component mounts or dependencies change
  useEffect(() => {
    fetchBookings();
  }, [fetchBookings]);

  // Handle filter changes
  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters(prev => ({
      ...prev,
      [name]: value
    }));
    setCurrentPage(1); // Reset to page 1 when filters change
  };

  // Handle search input submit
  const handleSearch = (e) => {
    e.preventDefault();
    fetchBookings();
  };

  // Format date for display
  const formatDate = (dateString) => {
    try {
      if (!dateString) return 'N/A';
      return format(new Date(dateString), 'MMM dd, yyyy HH:mm');
    } catch (err) {
      console.error('Date formatting error:', err);
      return 'Invalid date';
    }
  };

  // Handle status badge styling
  const getStatusBadgeClass = (status) => {
    switch (status?.toLowerCase()) {
      case 'active':
        return 'bg-green-100 text-green-800';
      case 'cancelled':
        return 'bg-red-100 text-red-800';
      case 'completed':
        return 'bg-blue-100 text-blue-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  // Navigate to booking details
  const viewBookingDetails = (bookingId) => {
    navigate(`/admin/bookings/${bookingId}`);
    console.log(`Navigating to booking details for ID: ${bookingId}`);
  };

  // Update booking status
  const updateStatus = async (bookingId, newStatus) => {
    try {
      const token = getAuthToken();
                  
      if (!token) {
        alert('You are not authenticated. Please log in again.');
        return;
      }
      
      const headers = {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${token}`
      };
      
      console.log(`Updating booking ${bookingId} status to ${newStatus}`);
      
      const response = await axios.patch(`/api/bookings/${bookingId}/status`, 
        { status: newStatus },
        { headers }
      );

      if (response.data.success) {
        // Update the booking in the state
        setBookings(prevBookings =>
          prevBookings.map(booking =>
            booking._id === bookingId
              ? { ...booking, status: newStatus }
              : booking
          )
        );
        
        console.log('Booking status updated successfully');
      } else {
        alert(response.data.message || 'Failed to update booking status');
      }
    } catch (err) {
      console.error('Error updating booking status:', err);
      alert(err.response?.data?.message || 'Failed to update booking status');
    }
  };

  // Helper function to safely get nested properties
  const safeGet = (obj, path, defaultValue = 'N/A') => {
    return path.split('.').reduce((current, key) => 
      current && current[key] !== undefined ? current[key] : defaultValue, obj
    );
  };
    
  return (
  <div className="min-h-screen bg-gray-50">
    <AdminNavbar />
    <main className="max-w-7xl mx-auto px-6 py-10">
      <h1 className="text-3xl font-extrabold text-gray-900 mb-8">Booking Management</h1>

      {/* Filter and Search Section */}
      <section className="bg-white rounded-xl shadow-md p-6 mb-8">
        <h2 className="text-xl font-semibold text-gray-700 mb-6 border-b border-gray-200 pb-2">Filters</h2>

        <form onSubmit={handleSearch} className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
          {/* Status filter */}
          <div>
            <label htmlFor="status" className="block text-sm font-medium text-gray-600 mb-2">
              Status
            </label>
            <select
              id="status"
              name="status"
              value={filters.status}
              onChange={handleFilterChange}
              className="w-full rounded-md border border-gray-300 bg-white py-2 px-3 text-gray-700 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
            >
              <option value="">All Statuses</option>
              <option value="active">Active</option>
              <option value="cancelled">Cancelled</option>
              <option value="completed">Completed</option>
            </select>
          </div>

          {/* Start date filter */}
          <div>
            <label htmlFor="startDate" className="block text-sm font-medium text-gray-600 mb-2">
              Start Date
            </label>
            <input
              type="date"
              id="startDate"
              name="startDate"
              value={filters.startDate}
              onChange={handleFilterChange}
              className="w-full rounded-md border border-gray-300 bg-white py-2 px-3 text-gray-700 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
            />
          </div>

          {/* End date filter */}
          <div>
            <label htmlFor="endDate" className="block text-sm font-medium text-gray-600 mb-2">
              End Date
            </label>
            <input
              type="date"
              id="endDate"
              name="endDate"
              value={filters.endDate}
              onChange={handleFilterChange}
              className="w-full rounded-md border border-gray-300 bg-white py-2 px-3 text-gray-700 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
            />
          </div>

          {/* Search input */}
          <div>
            <label htmlFor="search" className="block text-sm font-medium text-gray-600 mb-2">
              Search
            </label>
            <div className="flex rounded-md shadow-sm">
              <input
                type="text"
                id="search"
                name="search"
                placeholder="Search by user, bunk..."
                value={filters.search}
                onChange={handleFilterChange}
                className="flex-grow rounded-l-md border border-gray-300 py-2 px-4 text-gray-700 focus:border-indigo-500 focus:ring-indigo-500"
              />
              <button
                type="submit"
                className="inline-flex items-center rounded-r-md bg-indigo-600 px-5 py-2 text-white font-semibold hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-1"
              >
                Search
              </button>
            </div>
          </div>
        </form>
      </section>

      {/* Loading and Error States */}
      {loading && (
        <div className="flex flex-col items-center py-12 text-gray-600">
          <div className="animate-spin rounded-full h-10 w-10 border-t-4 border-b-4 border-indigo-600 mb-4"></div>
          <p className="text-lg">Loading bookings...</p>
        </div>
      )}

      {error && !loading && (
        <div className="rounded-lg bg-red-50 border border-red-300 text-red-700 px-6 py-4 mb-8 max-w-3xl mx-auto">
          <p>{error}</p>
          {!getAuthToken() && (
            <div className="mt-4 text-center">
              <button
                onClick={() => navigate('/admin/login')}
                className="inline-block rounded-md bg-blue-600 px-6 py-2 text-white font-semibold hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-1"
              >
                Go to Login
              </button>
            </div>
          )}
        </div>
      )}

      {/* Bookings Table */}
      {!loading && !error && (
        <>
          <section className="bg-white shadow-lg rounded-xl overflow-hidden max-w-full">
            <div className="overflow-x-auto">
              <table className="min-w-full divide-y divide-gray-200">
                <thead className="bg-gray-100">
                  <tr>
                    {['Booking ID', 'User', 'EV Bunk', 'Slot Time', 'Status', 'Actions'].map((header) => (
                      <th
                        key={header}
                        className="px-6 py-3 text-left text-xs font-semibold text-gray-600 uppercase tracking-wider select-none"
                      >
                        {header}
                      </th>
                    ))}
                  </tr>
                </thead>
                <tbody className="divide-y divide-gray-100 bg-white">
                  {bookings.length === 0 ? (
                    <tr>
                      <td colSpan="6" className="py-16 text-center text-gray-500 font-medium">
                        <div className="space-y-1">
                          <p className="text-lg">No bookings found</p>
                          <p className="text-sm">Try adjusting your filters or search terms</p>
                        </div>
                      </td>
                    </tr>
                  ) : (
                    bookings.map((booking) => (
                      <tr key={booking._id} className="hover:bg-gray-50 cursor-pointer">
                        <td className="px-6 py-4 whitespace-nowrap text-sm font-mono text-gray-800">
                          {booking._id?.substring(0, 8) || 'N/A'}...
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm">
                          <div className="font-semibold text-gray-900">
                            {safeGet(booking, 'userId.name') || safeGet(booking, 'user.name')}
                          </div>
                          <div className="text-gray-500 text-xs truncate max-w-xs">
                            {safeGet(booking, 'userId.email') || safeGet(booking, 'user.email')}
                          </div>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm">
                          <div className="font-semibold text-gray-900">
                            {safeGet(booking, 'bunkId.name') || safeGet(booking, 'bunk.name')}
                          </div>
                          <div className="text-gray-500 text-xs truncate max-w-xs">
                            {safeGet(booking, 'bunkId.address') || safeGet(booking, 'bunk.address')}
                          </div>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm">
                          <div>
                            <div className="font-medium text-gray-800">Start: {formatDate(booking.startTime)}</div>
                            <div className="text-xs text-gray-400">End: {formatDate(booking.endTime)}</div>
                          </div>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap">
                          <span
                            className={`inline-block px-3 py-1 rounded-full text-xs font-semibold tracking-wide ${getStatusBadgeClass(
                              booking.status
                            )}`}
                          >
                            {booking.status || 'Unknown'}
                          </span>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-right space-y-2">
                          <button
                            onClick={() => viewBookingDetails(booking._id)}
                            className="text-indigo-600 hover:text-indigo-900 font-semibold text-sm"
                          >
                            View Details
                          </button>
                          <select
                            value={booking.status || 'active'}
                            onChange={(e) => updateStatus(booking._id, e.target.value)}
                            className="w-full rounded-md border border-gray-300 py-1 px-2 text-xs text-gray-700"
                            aria-label="Update booking status"
                          >
                            <option value="active">Active</option>
                            <option value="cancelled">Cancelled</option>
                            <option value="completed">Completed</option>
                          </select>
                        </td>
                      </tr>
                    ))
                  )}
                </tbody>
              </table>
            </div>
          </section>

          {/* Pagination Controls */}
          <section className="flex flex-col md:flex-row justify-between items-center mt-8 space-y-4 md:space-y-0">
            <p className="text-sm text-gray-600">
              Showing <span className="font-semibold">{bookings.length}</span> of{' '}
              <span className="font-semibold">{totalBookings}</span> bookings (Page{' '}
              <span className="font-semibold">{currentPage}</span> of <span className="font-semibold">{totalPages}</span>)
            </p>
            <div className="flex space-x-3">
              <button
                onClick={() => setCurrentPage((prev) => Math.max(prev - 1, 1))}
                disabled={currentPage === 1}
                className="inline-flex items-center justify-center rounded-md border border-gray-300 bg-white px-4 py-2 text-sm font-medium text-gray-700 shadow-sm hover:bg-gray-100 disabled:opacity-40 disabled:cursor-not-allowed"
              >
                Prev
              </button>
              <span className="inline-flex items-center justify-center px-3 py-2 text-sm font-semibold text-gray-700">
                {currentPage} / {totalPages}
              </span>
              <button
                onClick={() => setCurrentPage((prev) => Math.min(prev + 1, totalPages))}
                disabled={currentPage === totalPages}
                className="inline-flex items-center justify-center rounded-md border border-gray-300 bg-white px-4 py-2 text-sm font-medium text-gray-700 shadow-sm hover:bg-gray-100 disabled:opacity-40 disabled:cursor-not-allowed"
              >
                Next
              </button>
            </div>
          </section>
        </>
      )}
    </main>
  </div>
);
};
export default AdminBookingsList;
